function btnMutilSort_OnClick(obj) {
	gfn_GridMultiSort(gdEduJubsu, 0); // 멀티 정렬을 실행한다.
}

/* 회차 포커스 아웃될때 자동 0 입력 */
function edEduOrder_OnKillFocus(obj) {
	gfn_ComponentOnKillFocus(obj);

    if (obj.Text != "") {
        obj.Text = iif(gfn_IsNull(obj.Text), "", lpad(obj.Text, "0", 3));
    }
}

function edEduOrderEnd_EnterSearch(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{
	if (nChar == 13) { // Enter Key
        edEduOrderEnd.Text = iif(gfn_IsNull(edEduOrderEnd.Text), "", lpad(edEduOrderEnd.Text, "0", 3));
        lfn_Search();
    }
}

function btnToExcel_OnClick(obj) {
    if (ds_oEduJubsu.GetRowCount() < 1) {
        gfn_Alert("출력할 데이터가 존재하지 않습니다.");
        return false;
    }
    
	// 개인정보 다운로드 사유 등록
	var parValue = "sCode=1410";	// COMMON.PERSONALDOWNLOADH
		parValue += " sRowCnt=" + ds_oEduJubsu.GetRowCount();
		parValue += " sTitle=" + quote(gdEduJubsu.UserData);
	var ls_IsRemark = Dialog("COM::frmCOM2100PDownloadRemark.xml", parValue, "", "", "", -1, -1);	

	if (ls_IsRemark == "" || ls_IsRemark == null || ls_IsRemark == "FAIL") {
		return false;
	}
	lfn_Excel(obj);
}
function gdEduJubsu_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	var lobj_Dataset = object(obj.BindDataset);
	if (nCell == 0) { // 선택
        if (obj.GetCellProp("head", nCell, "text") == 1) { // 전체가 이미 선택된 경우
            obj.SetCellProp("head", nCell, "text", 0);
            for(i = 0; i < lobj_Dataset.RowCount(); i++) {
                lobj_Dataset.SetColumn(i, "SEL", "0");
            }
        } else { // 전체가 이미 선택되지 않은 경우
            obj.SetCellProp("head", nCell, "text", 1);
            for(i = 0; i < lobj_Dataset.RowCount(); i++) {
                lobj_Dataset.SetColumn(i, "SEL", "1");
            }
        }
	} else { // 헤드 정렬
        lfn_SortGrid_OnHeadClick(obj,nCell,nX,nY,nPivotIndex);
	}
}

// 교재수령지 출력
function btnPrintBookAddr_OnClick(obj)
{
    if (ds_oEduJubsu.RowCount() < 1) {
        gfn_Alert("출력할 데이터가 없습니다.");
        return false;
    }
   
    if(!gfn_CheckChangedGrid(this))  //선택한것이 있는지 체크 메세지 보임
    { alert("출력할 인원을 선택해 주십시오.");      
        return false;
    }
    
    // 교재수령지 출력제외자 추출
    sMsg = "";
    for(i = 0; i < ds_oEduJubsu.RowCount(); i++) {
    
		if (ds_oEduJubsu.GetColumn(i, "SEL") == "1") {
			if (((ds_oEduJubsu.GetColumn(i, "EJBOOKPRINTESMGNO") > 0) &&
			     (ds_oEduJubsu.GetColumn(i, "EJBOOKPRINTESMGNO") <> ds_oEduJubsu.GetColumn(i, "ESMGNO"))) ||
			     (ds_oEduJubsu.GetColumn(i, "OLDEJBOOKPRINTESMGNO") > 0)) {

				sMsg = sMsg + ds_oEduJubsu.GetColumn(i, "JUBSUNO")+"("+ds_oEduJubsu.GetColumn(i, "EJNM")+"), ";
			}

		}
	   
    }
    sMsg = substr(sMsg, 0, length(sMsg)-2);
    // 출력이력 없는자와 해당회차 접수자는 중복출력 가능
    if (!Confirm("교재수령지를 출력하시겠습니까?\n\n" +
                 " * 출력하시면 해당회차의 출력여부가 기록되고,\n   다른 회차에서 출력한 자(일정변경자)는 교재수령지 출력자\n   명단(엑셀)에서 제외됩니다.\n\n"+
                 " * 출력제외자 : " + iif(gfn_isNull(sMsg), "없음", sMsg))) { //  출력전 확인 메시지
        return false;
	}
        ds_ioBook.copy(ds_oEduJubsu);  // 1) 원본 복사           
    ds_ioBook.Filter("SEL = '1' && OLDEJBOOKPRINTESMGNO = '0' && (EJBOOKPRINTESMGNO = ESMGNO || EJBOOKPRINTESMGNO = '0')"); // 2) 복사 후 필터함 (출력이력 없는자와 해당회차 접수자는 중복출력 가능)
    
    ds_ioBookFilter.copyf(ds_ioBook);  // 3) 필터한 값을 실제 사용 위해 다른 곳에 복사     
    ds_ioBook.ClearData(); // 4) 데이타셋 초기화  레포트 출력위해 필터된 2)번의 데이터셋을 초기화함   	
    
    

    
    gdBookSend.BindDataset = "ds_ioBookFilter";

    lfn_GridToExcel(gdBookSend);
    

    // 교재수령지 출력 여부 업데이트
    tit_clearActionInfo();
    
    tit_addMultiActionInfo("cust:updateCust1026BookPrintYN", "", 0, "", "N");
    // 서버 호출 
    tit_callService(
        ""
        ,""
        ,"ds_iBookPrint=ds_ioBookFilter"/// inDs		
        ,""	// outDs
        ,"" // args 
        ,""
        ,false
    ); 
    	
}

function lfn_GridToExcel(objGrid) {
	ext_OleServerBusyTimeOut(true,5000,false,false);	// 엑셀 변환 시 오류 발생하여 추가(2019.02.01)
    var mobj_Component = null;
    var marr_Grid = Array();

    var mobj_Export;
    mobj_Export = CreateExportObject();
    mobj_Export.ExportType = "Excel";
    mobj_Export.ExportFileName = "교재수령지_"+substr(getDate(),0,8)+".xls";
    mobj_Export.ActiveSheetName = objGrid.UserData;
    mobj_Export.MakeEmptyFileWhenNotExist = false;

    mobj_Export.AddExportGrid(objGrid.UserData + "!" + "A1", objGrid, true, false);


    mobj_Export.Export();
    mobj_Export.Close();
    mobj_Export = null;
    //ext_OleServerBusyTimeOut(false);	// 엑셀 변환 시 오류 발생하여 추가(2019.02.01) // 실행시 마이플랫폼 종료 되서 주석처리함(2019.02.01) 
}


// 회차 이수일자 선택
function radSearchGubun_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if (strCode == "0") {
	
		edEduOrderStart.Enable = true;
		
		edEduOrderEnd.Enable = true;
		
		calEduStart.Enable = false;
		
		calEduEnd.Enable = false;
		
		edEduOrderStart.Value = "";
		
		edEduOrderEnd.Value = "";
		
		calEduStart.Value = "";
		
		calEduEnd.Value = "";
		
	} else {
	
		edEduOrderStart.Enable = false;
		
		edEduOrderEnd.Enable = false;
		
		calEduStart.Enable = true;
		
		calEduEnd.Enable = true;
		
		edEduOrderStart.Value = "";
		
		edEduOrderEnd.Value = "";
		
		calEduStart.Value = left(getDate(), 6)+"01";
		
		calEduEnd.Value = left(getDate(), 8);
		
	}
}


/*
 * 기능 : 세부직능 조회
 */
function lfn_SearchCourseDetail() {
    ds_oDetailCourse.ClearData(); // 데이타셋 초기화
	tit_clearActionInfo();
	
	tit_addSearchActionInfo("cust:searchCust1100ComerCourseDetail", false);  // 고객세부직능

	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_oDetailCourse=ds_oCustomerCourseDetail" // outDs
		,"CGROUPCD_A = " + quote(cbxCourse.Value) // args 
		,"lfn_AfterSearchCourseDetail"
		,false
		,false
		,true
	);
}

/*
 * 기능 : 조회 후 처리해야 할 내용
 * parameter : nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_AfterSearchCourseDetail(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }
	
}

function cbxCourse_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	lfn_FilterDetailCourse(strCode);
    if (cbxCourse.Value == "50") {
		//lfn_SearchCourseDetail();  		 //세부직능
		cbxDetailCourse.Enable = true;
    } else {
		//lfn_SearchCourseDetail();  		 //세부직능
		cbxDetailCourse.Enable = true;
	    cbxDetailCourse.Value = "";      //세부직능
    }
}

/*
 * 세부직능 필터링
 */
function lfn_FilterDetailCourse(sFilterGubun) {
	ds_oDetailCourse.UnFilter();
	var ls_FilterExpr = "";
    if (!gfn_IsNull(sFilterGubun)) {
        ls_FilterExpr += "GCD=='" + trim(sFilterGubun) + "' || CD == ''";
    }
    ds_oDetailCourse.Filter(ls_FilterExpr);
	if( ds_oDetailCourse.FindRow("DATA", cbxDetailCourse.Text) == -1) {
		cbxDetailCourse.Text = "";
    }
}

// 이메일 TXT 출력
function btnPrintEmail_OnClick(obj)
{
    if (ds_oEduJubsu.RowCount() < 1) {
        gfn_Alert("출력할 데이터가 없습니다.");
        return false;
    }
   
    if(!gfn_CheckChangedGrid(this))  //선택한것이 있는지 체크 메세지 보임
    { alert("출력할 인원을 선택해 주십시오.");      
        return false;
    }
        ds_ioEmail.copy(ds_oEduJubsu);  // 1) 원본 복사           
    ds_ioEmail.Filter("SEL = '1'"); // 2) 복사 후 필터함 
    
    ds_ioEmailFilter.copyf(ds_ioEmail);  // 3) 필터한 값을 실제 사용 위해 다른 곳에 복사     
    ds_ioEmail.ClearData(); // 4) 데이타셋 초기화  레포트 출력위해 필터된 2)번의 데이터셋을 초기화함   	
    
	var vEmailList = "";

    for(i = 0; i < ds_ioEmailFilter.GetRowCount(); i++) {

		if (!gfn_isNull(ds_ioEmailFilter.GetColumn(i, "EJEMAILID"))) {

			if (i == ds_ioEmailFilter.GetRowCount()-1) {
				vEmailList = vEmailList + ds_ioEmailFilter.GetColumn(i, "EJEMAIL");
			} else {
				vEmailList = vEmailList + ds_ioEmailFilter.GetColumn(i, "EJEMAIL") + "; ";
			}
		}

	}

	if (substr(vEmailList, length(vEmailList)-2, 2) == "; ") {
		vEmailList = substr(vEmailList, 0, length(vEmailList)-2);
	}


	fdOpenFile.Type = "SAVE";
	fdOpenFile.FileName = substr(getDate(),0,8)+"_이메일발송자료.txt";

	if (fdOpenFile.open()) {
		filSaveFile = fdOpenFile.FilePath +"\\"+ fdOpenFile.FileName;
		filSaveFile.Open("wt");  // Option ( r:read, t:text, b:binary, w:write, a:append +:read/write )
			
		filSaveFile.Write(vEmailList);
		Gfn_Alert("파일을 저장하였습니다.\n\n"+
				  "메일전송 후 파일은 삭제하시기 바랍니다.");
	
		filSaveFile.Close();
	} else {
		gfn_Alert("다운로드에 실패했습니다.");
	}
    
/*    
    parValue =  "sTitle='이메일주소 보기'"; 
    parValue += " sMsg=" + vEmailList;
	Dialog("COM::frmCOM0012PInfoView.xml", parValue, "", "", "", -1, -1);
*/
    
}

