function lfn_Search(obj)
{
    if (!gfn_FormValidate(this)) { // 조회 Validation Check
        return false;
    }
    
    // 조회이력을 저장한다.  
    ls_HistoryArg = "지부=" + quote(cbxJibu.Text)
                + ",직능=" + quote(cbxCourse.Text)
                + ",년도=" + quote(edTrainingYear.Text)
                + ",회차=" + quote(edTrainingOrder.Text)
                + ",정렬방법=" + quote(radSort.Text);
    gfn_InsertSearchPrintHistory(
        "S"
        , this.ID
        , global.mnuLeft.cbxOpenForm.Text
        , ls_HistoryArg
    );
    
    // 실무능력평가표
    ds_oEvaluation.ClearData(); // 데이타셋 초기화
    ds_oEvaluationPoto.ClearData(); // 데이타셋 초기화
    tit_clearActionInfo();
//	tit_addCsvSearchActionInfo("training:searchTraining0145Evaluation", false);
	
	tit_addCsvSearchActionInfo("cust:searchCust1320EvaluationStat", false);
	tit_addCsvSearchActionInfo("cust:searchCust1320EvaluationStatPoto", false);
	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_oEvaluation=ds_oEvaluation ds_oEvaluationPoto=ds_oEvaluationPoto"	// outDs
		,"ESGTMGNO=" + quote(cbxJibu.Value)
        + " ESCOUSECD=" + quote(cbxCourse.Value)
        + " ESYEAR=" + quote(edTrainingYear.Text)
        + " ESEDUORDER =" + quote(edTrainingOrder.Text)
        + " SORT=" + quote(radSort.Value)// args 
		,"lfn_AfterSearchEvaluation"
		,false
	);	
}


/*
 * 기능 : 실무능력평가표 출력
 */
function lfn_Print(obj)
{    
    if (ds_oEvaluationPoto.RowCount() < 1) {
        gfn_Alert("출력할 데이터가 없습니다.");
        return false;
    }
    // 출력이력을 저장한다.  
    ls_HistoryArg = "지부=" + quote(cbxJibu.Text)
                + ",직능=" + quote(cbxCourse.Text)
                + ",년도=" + quote(edTrainingYear.Text)
                + ",회차=" + quote(edTrainingOrder.Text)
                + ",정렬방법=" + quote(radSort.Text);
    gfn_InsertSearchPrintHistory(
        "P"
        , this.ID
        , "실무능력평가표출력"
        , ls_HistoryArg
    );
    
    var PrtTime = "";	
	// 12시 이전에는 발급일자만 출력
	// 12시 이후에는 발급일자+ 시간까지 출력
	
	if (substr(GetDate(), 8,4) > '1200') {
	
		PrtTime = left(GetDate(), 4)+"-"+substr(GetDate(), 4,2)+"-"+substr(GetDate(), 6,2)+" "+
					  substr(GetDate(), 8,2)+":"+substr(GetDate(), 10,2);
	
	} else {
	
		PrtTime = left(GetDate(), 4)+"-"+substr(GetDate(), 4,2)+"-"+substr(GetDate(), 6,2);
		
	}
	
	var sReportName = "";
	
	if (cbxCourse.Value == "20") {   //소방안전관리보조자 
	 
		sReportName = "frmcust0145R03ExecutiveAbilityEvaluation.rex";
	
	} else if (cbxCourse.Value == "10") { //소방안전관리자 
		
		sReportName = "frmcust0145R01ExecutiveAbilityEvaluation.rex";
		
	} else if (cbxCourse.Value == "50") {  //소방기술자 
	
		sReportName = "frmcust0145R04ExecutiveAbilityEvaluation.rex";

	} else if (cbxCourse.Value == "30") {  //위험물안전관리자 
	
		sReportName = "frmcust0145R05ExecutiveAbilityEvaluation.rex";
		
	} else if (cbxCourse.Value == "80") {  //위험물운송자 
	
		sReportName = "frmcust0145R06ExecutiveAbilityEvaluation.rex";
		
	// } else if (cbxCourse.Value == "") {  //위험물 통합관리 
	
	// sReportName = "frmcust0145R07ExecutiveAbilityEvaluation.rex";
		
	} else if (cbxCourse.Value == "40") {
	
		sReportName = "frmcust0145R08ExecutiveAbilityEvaluation.rex";
		

	}
	
    gfn_CallReportPopup(sReportName
                      , "ds_oEvaluationPoto||ds_oEvaluation"//||ds_oTrainingAttendencePoto
                      , "rpDirpath=" + GF_FILE_SERVER_URL + "kems"
                        
                        + "^PrtTime=" + PrtTime);
    // 여분출력
    // 하나의 레포트에서 여분을 만들수가 없어서... 별도로 빼서 작업
    if(cbxCourse.Value == "10")
    {
		if(chk_print.Value == 1)
		{
			sReportName = "frmcust0145R01ExecutiveAbilityEvaluation_Page.rex";
			
			gfn_CallReportPopup(sReportName
                      , "ds_oEvaluationPoto||ds_oEvaluation"//||ds_oTrainingAttendencePoto
                      , "rpDirpath=" + GF_FILE_SERVER_URL + "kems"
                        
                        + "^PrtTime=" + PrtTime);  
		}
    }
}

