Window.Script:
/*
 * 고객정보명 : frmcust2016PPosVat.xml
 * 설명 : 부가세 정보
 * 작성일 : 2009.12.16
 * 작성자 : 김동현
 */

#include "LIB::cust.js"; // 공통 관련

/* 공통 필수 시작 */
function Form_OnLoadCompleted(obj) {
    gfn_Form_OnLoadCompleted(obj); // 공통 폼 로딩 이벤트 처리

    lfn_SearchVat();
}

function lfn_SearchVat() {

    ds_oPosVatH.ClearData(); // 데이타셋 초기화

    tit_clearActionInfo();
	tit_addSearchActionInfo("cust:searchCust2016PosVatH", false);
	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_oPosVatH=ds_oPosVatH" // outDs
		,"SPMYEAR=" + quote(sPmYear)
            + " SPMMGNO=" + quote(sPmMgno)
		,"lfn_AfterSearch"
		,false
		,false
		,true
	);
}

/**
 * 조회 후 처리
 */
function lfn_AfterSearch(nErrorCode, strErrorMsg) {

    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        gfn_Alert("부가세 정보를 확인할 수 없습니다.\n정보지원과로 문의하시기 바랍니다.");
        return;
    }

    var strAddress = "http://172.19.1.7/ReportingFile/cabsoftprint.php?moveURL=/GROUPWARE/vat/vat_prt.php&urlRPX=/vat/vat_lowest.rpx&urlPARA="
                    + "vyear=" + ds_oPosVatH.GetColumn(0, "PVVMYEAR")
                    + "!@vjibu=" + ds_oPosVatH.GetColumn(0, "PVVMJIBU")
                    + "!@vbilltype=" + ds_oPosVatH.GetColumn(0, "PVVMBILLTYPE")
                    + "!@vvatno=" + ds_oPosVatH.GetColumn(0, "PVVMVATNO")
                    + "&cabSETZOOM=100&cabEXPORT=0";

	WebBrowserEstimate.PageUrl = strAddress;
	WebBrowserEstimate.run();
}

/*
 * 웹페이지 조회 에러시
 */
function WebBrowserEstimate_OnNavigateError(obj,strUrl,nStatusCode,strFrame) {
	alert("네트워크 지연이 발생되었습니다. 잠시 후 다시 시도 바랍니다.");
}
