#include "LIB::cust.js"; // 공통업무 관련

/* 공통 필수 시작 */
function Form_OnLoadCompleted(obj) {
    gfn_Form_OnLoadCompleted(obj); // 공통 폼 로딩 이벤트 처리

    // 폼 환경설정
    ls_SystemGubun = gs_SystemGubun;
    ls_SystemMenuId = gs_SystemMenuId;
    var ls_FormId = split(sFormUrl, "::");
    ls_FormId = split(ls_FormId[1], ".");
    obj.Title = sFormTitle + "(" + ls_FormId[0] + ")";

    gfn_MakeRequiredInputMark(this); // 필수 입력란을 설정한다.

    ms_FormAuth = gfn_GetFormAuth(this); // 권한을 조회한다.
    if (ms_FormAuth == "W") { // 권한에 따른 버튼 설정
        lfn_SetFormAuth(true); // 저장 권한
    } else if (ms_FormAuth == "R") {
        lfn_SetFormAuth(false); // 읽기 권한
    } else {
        gfn_Alert(MSGI00080);
        close();
    }

    gfn_SearchJibu(cbxJibu, "Y", "선택"); // 지부 코드 조회
//    medProcDate.Value = left(getDate(), 6);
    
    var currYear = toNumber(left(getDate(), 4));
    ds_ioYear.ClearData();
    
    for (var i=-4; i<=1; i++) {
        ds_ioYear.InsertRow(0);
        ds_ioYear.SetColumn(0, "CD", currYear + i);
        ds_ioYear.SetColumn(0, "DATA", currYear + i);
    }
    
    cbxYear.Value = currYear;
    cbxMonth.Value = substr(getDate(), 4, 2);
    cbxJibu.SetFocus();
    
}

/**
 * 폼 사이즈
 */
function Form_OnSize(obj,nCx,nCy,nState) {
}

/**
 * 버튼 실행 권한
 */
function lfn_SetFormAuth(bStatus) {
}


/////////////////////////////////////////////////////////////////////////////////
// 기본 버튼
/////////////////////////////////////////////////////////////////////////////////

/**
 * 조회
 */
function lfn_Search(obj) {

    ds_ioCusFeeChk.ClearData();
    tit_clearActionInfo();
    tit_addSearchActionInfo("cust:searchCust2060CusFeeChk", false);
    tit_callService(
        ""
        ,""
        ,""	// inDs
        ,"ds_ioCusFeeChk=ds_oCusFeeChk"	// outDs
        ,"CFCGTMGNO=" + quote(cbxJibu.Value)
            + " CFCYEAR=" + quote(cbxYear.Value)
            + " CFCMONTH=" + quote(cbxMonth.Value)	// args
        ,""
        ,false
        ,false
        ,true
    );
    
}

/**
 * 일치
 */
function btnMatched_OnClick(obj) {

	if (!gfn_Confirm("일치 처리하시겠습니까?")) {
        return false;
    }


    ds_iCusFeeChk.ClearData();
    ds_iCusFeeChk.InsertRow(0);
    ds_iCusFeeChk.SetColumn(0, "CFCCHKGUBUN", "0");

    var sRtn = lfn_MatchedProc();
    if (sRtn) {
        gfn_Alert("일치로 적용되었습니다.");
    }
    
    lfn_Search();

}

/**
 * 불일치
 */
function btnUnmatched_OnClick(obj) {

	if (!gfn_Confirm("불일치로 처리하시겠습니까?")) {
        return false;
    }

    ds_iCusFeeChk.ClearData();
    ds_iCusFeeChk.InsertRow(0);
    ds_iCusFeeChk.SetColumn(0, "CFCCHKGUBUN", "1");

    var sRtn = lfn_MatchedProc();
    if (sRtn) {
        gfn_Alert("불일치로 적용되었습니다.");
    }
    
    lfn_Search();

}

/**
 * 처리
 */
function lfn_MatchedProc() {

    if (gfn_IsNull(cbxJibu.Value)) {
        gfn_Alert("지부를 선택해 주십시오.");
        return false;
    }

    var sProcYear = cbxYear.Value;
    var sProcMonth = cbxMonth.Value;

    ds_oCusFeeChk.ClearData();
    tit_clearActionInfo();
    tit_addSearchActionInfo("cust:searchCust2060CusFeeChk", false);
    tit_callService(
        ""
        ,""
        ,""	// inDs
        ,"ds_oCusFeeChk=ds_oCusFeeChk"	// outDs
        ,"CFCGTMGNO=" + quote(cbxJibu.Value)
            + " CFCYEAR=" + quote(sProcYear)
            + " CFCMONTH=" + quote(sProcMonth)	// args
        ,""
        ,false
        ,false
        ,true
    );

    if (ds_oCusFeeChk.GetRowCount() > 0 && ds_oCusFeeChk.GetColumn(0, "CFCCLOSEGUBUN") == "마감") { //마감일 경우
        gfn_Alert(sProcYear + "년 " + sProcMonth + "월 " + "월보는 이미 마감되었습니다.");
        return false;
    }
	
    ds_iCusFeeChk.SetColumn(0, "CFCCLOSEGUBUN", "1");
    ds_iCusFeeChk.SetColumn(0, "CFCGTMGNO", cbxJibu.Value);
    ds_iCusFeeChk.SetColumn(0, "CFCYEAR", sProcYear);
    ds_iCusFeeChk.SetColumn(0, "CFCMONTH", sProcMonth);

    tit_clearActionInfo();
    tit_addSingleActionInfo("cust:insertCust2060CusFeeChk", "", 0, "");
    tit_callService(
        ""
        ,""
        ,"ds_iCusFeeChk=ds_iCusFeeChk"	// inDs
        ,""	// outDs
        ,""	// args 
        ,""
        ,false
        ,false
        ,true
    );
    
    return true;
}


/////////////////////////////////////////////////////////////////////////////////
// 이벤트
/////////////////////////////////////////////////////////////////////////////////

function medProcDate_OnCharChanged(obj,strPreText,strPostText) {
	if (substr(medProcDate.Value, 4, 1) <> "" && substr(medProcDate.Value, 5, 1) <> "") {
		if (substr(medProcDate.Value, 4, 2) < "01") {
			medProcDate.Value = substr(medProcDate.Value, 0, 4) + "01";
		} else if (substr(medProcDate.Value, 4, 2) > "12") {
			medProcDate.Value = substr(medProcDate.Value, 0, 4) + "12";
		}
	}

}

function cbxMonth_OnKeyDown(obj,nChar,bShift,bCtrl,bAlt,LLParam,HLParam)
{
    if (nChar == "13") {
        lfn_Search();
    }
    
	gfn_EnterNextFocus(obj,nChar,bShift,bCtrl,bAlt,LLParam,HLParam);
}

function cbxMonth_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
    lfn_Search();
}

function cbxYear_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
    lfn_Search();	
}
