#include "LIB::cust.js"; // 공통업무 관련

function Form_OnLoadCompleted(obj)
{
	gfn_Form_OnLoadCompleted(obj); // 공통 폼 로딩 이벤트 처리
	
    // 폼 환경설정
    ls_SystemGubun = gs_SystemGubun;
    ls_SystemMenuId = gs_SystemMenuId;
    var ls_FormId = split(sFormUrl, "::");
    ls_FormId = split(ls_FormId[1], ".");
    obj.Title = sFormTitle + "(" + ls_FormId[0] + ")";

    gfn_MakeRequiredInputMark(this); // 필수 입력란을 설정한다.

    ms_FormAuth = gfn_GetFormAuth(this); // 권한을 조회한다.
    if (ms_FormAuth == "W") { // 권한에 따른 버튼 설정
        lfn_SetFormAuth(true); // 저장 권한
    } else if (ms_FormAuth == "R") {
        lfn_SetFormAuth(false); // 읽기 권한
    } else {
        gfn_Alert(MSGI00080);
        close();
    }

	gfn_SearchAllJibu(cbxJibu, "Y", "전체"); // 지부 코드 조회
	
	chkYearMinap1.Text = toNumber(substr(GetDate(),0,4)) + "년";
	chkYearMinap2.Text = toNumber(substr(GetDate(),0,4)) - 1 + "년";
	chkYearMinap3.Text = toNumber(substr(GetDate(),0,4)) - 2 + "년";
	chkYearMinap4.Text = toNumber(substr(GetDate(),0,4)) - 3 + "년";
	
	radMinapGubun_OnChanged();
	
	
}

function lfn_SetFormAuth(bStatus) {

}

function fn_Search(){
	tit_clearActionInfo();
		tit_addCsvSearchActionInfo("cust:searchCust2120UnpaidReport", false);
	
	ds_SearchJogeon.DeleteAll();		ds_SearchJogeon.AddRow();
	ds_MinapList.ClearData();
	ds_SearchJogeon.setColumn(0, "JIBU", cbxJibu.Value);	
	ds_SearchJogeon.setColumn(0, "MINAP_GUBUN", radMinapGubun.Value);	
	ds_SearchJogeon.setColumn(0, "LONGMINAP", radLongMinap.Value);	
	ds_SearchJogeon.setColumn(0, "MINAP1", chkYearMinap1.Value);	
	ds_SearchJogeon.setColumn(0, "MINAP2", chkYearMinap2.Value);	
	ds_SearchJogeon.setColumn(0, "MINAP3", chkYearMinap3.Value);	
	ds_SearchJogeon.setColumn(0, "MINAP4", chkYearMinap4.Value);
		
	tit_callService(  
	                 ""
	               , ""  
	               , "ds_iMinapList=ds_SearchJogeon"//inDs 
	               , "ds_MinapList=ds_oMinapList" //outDs
	               , ""
	               , "afterSearch"  //callBack
	               , false
	               ,false
				   ,true
	               ); 
}

