function fn_SearchExcel(){
	tit_clearActionInfo();
		tit_addCsvSearchActionInfo("cust:searchCust2120UnpaidReportExcel", false);
	
	ds_SearchJogeon.DeleteAll();		ds_SearchJogeon.AddRow();
	ds_MinapListExcel.ClearData();
	ds_SearchJogeon.setColumn(0, "JIBU", cbxJibu.Value);	
	ds_SearchJogeon.setColumn(0, "MINAP_GUBUN", radMinapGubun.Value);	
	ds_SearchJogeon.setColumn(0, "LONGMINAP", radLongMinap.Value);	
	ds_SearchJogeon.setColumn(0, "MINAP1", chkYearMinap1.Value);	  
	ds_SearchJogeon.setColumn(0, "MINAP2", chkYearMinap2.Value);	
	ds_SearchJogeon.setColumn(0, "MINAP3", chkYearMinap3.Value);	
	ds_SearchJogeon.setColumn(0, "MINAP4", chkYearMinap4.Value);	
	
	tit_callService(  
	                 ""
	               , ""  
	               , "ds_iMinapList=ds_SearchJogeon"//inDs 
	               , "ds_MinapListExcel=ds_oMinapListExcel" //outDs
	               , ""
	               , "afterSearchExcel"  //callBack
	               , false
	               ,""
				   ,true
	               ); 
}    

// 조회 후 callbackfunction afterSearchExcel(errCode, errMsg){
	//trace(ds_CustomerList.SaveXML());
	if(errCode != 0 ){
		alert("오류가 발생했습니다.");
		return;
	}
	else{
		if(ds_MinapListExcel.rowcount > 0){
			gdList.SetCellProp("head",15,"text", ds_MinapListExcel.GetColumn(1, "YEAR3")+"미납");
			gdList.SetCellProp("head",16,"text", ds_MinapListExcel.GetColumn(1, "YEAR2")+"미납");
			gdList.SetCellProp("head",17,"text", ds_MinapListExcel.GetColumn(1, "YEAR1")+"미납");
			gdList.SetCellProp("head",18,"text", ds_MinapListExcel.GetColumn(1, "YEAR0")+"미납");
		}
		else{
			
		}
	}
	//trace(ds_MinapListExcel.SaveXML());}

