//엑샐인쇄 시작----------------------------------------------------------------------------------------
function btnExcell_OnClick(obj)
{	
	fn_SearchExcel();
	
	if(ds_MinapListExcel.rowcount == 0){
		alert("데이터가 없습니다.");
		return;
	}   
	
	// 개인정보 다운로드 사유 등록
	var parValue = "sCode=1439";	// COMMON.PERSONALDOWNLOADH
		parValue += " sRowCnt=" + ds_MinapListExcel.GetRowCount();
		parValue += " sTitle=" + quote(gdList.UserData);
	var ls_IsRemark = Dialog("COM::frmCOM2100PDownloadRemark.xml", parValue, "", "", "", -1, -1);	

	if (ls_IsRemark == "" || ls_IsRemark == null || ls_IsRemark == "FAIL") {
		return false;
	}
	
	fn_GridToExcel(this);
}
function fn_GridToExcel(objForm) {
    var mobj_Component = null;
    var marr_Grid = Array();

    var i = 0;
    for (var b = 0; b < Length(objForm.Components); b++) {
        mobj_Component = objForm.components(b);
        if (mobj_Component.GetType() == "Grid") {
            if (Length(mobj_Component.UserData) > 0) {
                marr_Grid[i] = mobj_Component;
                i++;
            }
        }
    }
    
    if (Length(marr_Grid) == 0) {
		alert("aa");   
        return false;
    }

    var mobj_Export;
    mobj_Export = CreateExportObject();
    mobj_Export.ExportType = "Excel";
    mobj_Export.ExportFileName = objForm.Title + ".xls";
    mobj_Export.ActiveSheetName = marr_Grid.UserData;
    mobj_Export.MakeEmptyFileWhenNotExist = false;
	
	mobj_Export.AddExportGrid(gdList.UserData + "!" + "A1", gdList, true, false);
	
    
    mobj_Export.Export();
    mobj_Export.Close();
    mobj_Export = null;
}
