		alert("데이터가 없습니다.");
		return;
	}
}

function lfn_Print(obj)
{
	fn_Search();
	
	if(ds_MinapList.rowcount == 0){
			alert("데이터가 없습니다.");
			return;
	}

	gfn_CallReportPopup("frmCust2120R01UnpaidReport.rex"
				  , "ds_MinapList"
				  , "JIBU=" + cbxJibu.Text + "^MINAP_GUBUN=" + radMinapGubun.Value
				  , true);
	
}

function radMinapGubun_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if(radMinapGubun.Value == "1"){
		
		radLongMinap.Visible = false;
		chkYearMinap1.Value = "1";
		chkYearMinap2.Value = "1";
		chkYearMinap3.Value = "1";
		chkYearMinap4.Value = "1";
		chkYearMinap1.Visible = true;
		chkYearMinap2.Visible = true;
		chkYearMinap3.Visible = true;
		chkYearMinap4.Visible = true;
		
	}else{
		radLongMinap.Visible = true;
		radLongMinap.Value = "4";
		chkYearMinap1.Visible = false;
		chkYearMinap2.Visible = false;
		chkYearMinap3.Visible = false;
		chkYearMinap4.Visible = false;
	}
}


