#include "LIB::cust.js"; // 공통 관련

/* 공통 필수 시작 */
function Form_OnLoadCompleted(obj) {
    gfn_Form_OnLoadCompleted(obj); // 공통 폼 로딩 이벤트 처리

	// 공통 코드 조회
    gfn_SearchAllJibu(cbxJibu, "Y", "전체"); // 지부 코드 조회

    /*
        var vArg = "sEGSEQ=" + quote(cbxJibu.Value);
        vArg =  vArg + "sSGRDATE=" + quote(cbxJibu.Value);    
        vArg =  vArg + "sSGTMGNO=" + quote(cbxJibu.Value);
    */
		cbxJibu.Value = sSGTMGNO;    //-- 구분 SEQ
		edEduYear.Text = sSGRDATE;    //-- 년도 
		edSEQ.Text = sEGSEQ; //-- 구분 처리일자// args 

    lfn_Search(); // 조회
}

/*
 * 기능 : Form이 종료되거나 화면 전환시
 */
function Form_OnUnloadCompleted(obj) {
    Close(); // 종료
}

/*
 * 기능 : 조회
 */
function lfn_Search(obj) {
    ds_oEduSchedule.ClearData(); // 데이타셋 초기화
	tit_clearActionInfo();

	tit_addSearchActionInfo("cust:searchCust2171PEGiroSearchList", false);
	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_oPEGiroSearchList=ds_oPEGiroSearchList" // outDs
		,"EGSEQ=" + quote(edSEQ.Value) //-- 구분 SEQ
			+" SGRDATE=" + quote(edEduYear.Text) //-- 년도 
			+" SGTMGNO=" + quote(cbxJibu.Value) //-- 구분 처리일자// args 
		,"lfn_AfterSearchPopUpEduScheduleList"
		,false
		,false
		,true
	);
}

/*
 * 기능 : 조회 후 처리해야 할 내용
 * parameter : nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_AfterSearchPopUpEduScheduleList(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }


    gfn_SearchMessage(gdEduSchedule); // 조회 메세지를 처리하고 그리드의 정렬 헤드 마크를 제거한다.
}

/*
 * 기능 : 종료
 */
function lfn_End(obj) {
    Close(); // 종료
}

/* 공통 이벤트 함수 */
function lfn_SortGrid_OnHeadClick(obj,nCell,nX,nY,nPivotIndex) {
    var lobj_Dataset = object(obj.BindDataset);
    // 데이터가 있는 경우 해당 컬럼을 기준으로 정렬한다.
    if (lobj_Dataset.GetRowCount() > 1) {
        gfn_GridCellSort(obj, nCell); // 해당 셀정렬을 호출한다.
    }
}

/*
 * 기능 : 그리드 멀티정렬
 */
function btnMutilSort_OnClick(obj) {
	gfn_GridMultiSort(gdEduSchedule, 0); // 멀티 정렬을 실행한다.
}

