/*
 * 기능 : Head 영역의 Cell을 Click
 */
function gdEduSchedule_OnHeadClick(obj,nCell,nX,nY,nPivotIndex) {
	var lobj_Dataset = object(obj.BindDataset);
    // 데이터가 있는 경우 해당 컬럼을 기준으로 정렬한다.
    if (lobj_Dataset.GetRowCount() > 1) {
        gfn_GridCellSort(obj, nCell); // 해당 셀정렬을 호출한다.
    }
}

/*
 * 기능 : 키다운
 */
function edYear_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam) {
	if (nChar == 13) {
        lfn_Search(); // 조회
	}
}

/*
 * 기능 :  Body 영역의 Cell을 Double Click 
 */
function gdEduSchedule_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex) {
	btnSelect_OnClick(obj);
}

/*
 * 기능 :  직능 콤보 변경시
 */
function cbxCourse_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex) {
	lfn_Search(); // 조회
}

/*
 * 기능 : 지부 콤보값 변경
 */
function cbxJibu_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex) {
	lfn_Search(); // 조회
}
