function Form_OnSize(obj,nCx,nCy,nState)
{
    if (gs_IsResizingForm == "Y") {
        gdManagerList.Width = nCx - gdManagerList.Left - 8;
        shpGubunBox1.Width = nCx - shpGubunBox1.Left - 4;
    }
}

