/*
 * 기능 : 조회
 */
function lfn_Search(obj) {
var ls_PersonKey = "";

    if (gfn_IsNull(edPersonNM.Text) && gfn_IsNull(medResidentNo.Value)) {
        gfn_Alert("이름이나 주민번호 중 하나는 입력해 주십시오.");
        edPersonNM.SetFocus();
        return;
    }
    
    if (!gfn_FormValidate(this)) { // 입력 Validation Check
        return false;
    }
    
	// personkey 조회
	if (!gfn_isNull(edPersonNM.Text) && (length(medResidentNo.Value) == 13)) {
		ls_PersonKey = gfn_SearchPERSONKEY(edPersonNM.Text, medResidentNo.Value, "");
	}
    
    // 조회이력을 저장한다.  
    ls_HistoryArg = "이름=" + quote(edPersonNM.Text)
                + ",주민번호=" + quote(substr(medResidentNo.Value,0,7));
    gfn_InsertSearchPrintHistory(
        "S"
        , this.ID
        , global.mnuLeft.cbxOpenForm.Text
        , ls_HistoryArg
    );
    
    ds_oManagerList.ClearData(); // 데이타셋 초기화

    tit_clearActionInfo();
	tit_addCsvSearchActionInfo("cust:searchCust3020ManagerList", false);
	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_oManagerList=ds_oManagerList"	// outDs
		,"FMNM=" + quote(edPersonNM.Text)
		 + " FMBIRTHDAY=" + quote(left(medResidentNo.Value),7)
		 + " FMPERSONKEY=" + quote(ls_PersonKey)
		 + " ROWNUM=" + quote(edCount.Text)
		,"lfn_AfterSearchManagerList"
		,false
		,""
		,true
	);
}

/*
 * 기능 : 공통코드 조회 후 처리해야 할 내용
 * parameter : nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_AfterSearchManagerList(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }
    //trace(ds_oManagerList.SaveXML());

    gfn_SearchMessage(gdManagerList); // 조회 메세지를 처리하고 그리드의 정렬 헤드 마크를 제거한다.
}
