function btn_Cancel(obj)
{
    ds_oManagerList.Clear();
	lfn_Cancel(obj);
	edPersonNM.SetFocus();
}

function medResidentNo_OnKeyDown(obj,nChar,bShift,bCtrl,bAlt,nLLParam,nHLParam)
{
	if (nChar = 13) {
		lfn_Search(obj);
	}
}

function btnToExcel_OnClick(obj)
{
    if (ds_oManagerList.GetRowCount() < 1) {
        gfn_Alert("출력할 데이터가 존재하지 않습니다.");
        return false;
    }
    
	// 개인정보 다운로드 사유 등록
	var parValue = "sCode=1443";	// COMMON.PERSONALDOWNLOADH
		parValue += " sRowCnt=" + ds_oManagerList.GetRowCount();
		parValue += " sTitle=" + quote(gdManagerList.UserData);
	var ls_IsRemark = Dialog("COM::frmCOM2100PDownloadRemark.xml", parValue, "", "", "", -1, -1);	

	if (ls_IsRemark == "" || ls_IsRemark == null || ls_IsRemark == "FAIL") {
		return false;
	}
	lfn_Excel(obj);
}

