#include "LIB::cust.js"; // 공통업무 관련
 
/* 공통 필수 시작 */
function Form_OnLoadCompleted(obj) {
    gfn_Form_OnLoadCompleted(obj); // 공통 폼 로딩 이벤트 처리
    
    edSearch.SetFocus();
}

function Form_OnUnloadCompleted(obj) {
    Close();
}

/*
 * 기능 : 종료
 */
function lfn_End(obj) {
    Close();
}

/*
 * 기능 : 조회
 */
function lfn_Search(obj) {
    var sSearchGubun = rdGubun.Value;
    var sKeyWord = edSearch.Text;
    
    if(sSearchGubun == "" || sKeyWord == ""){
		gfn_Alert("검색어를 입력하십시오.");
		edSearch.SetFocus();
		return false;
	}
	
    medTotalCnt.Value = "";
    ds_oAgentList.ClearData(); // 데이타셋 초기화

    tit_clearActionInfo();
	tit_addCsvSearchActionInfo("cust:searchCust3030PAgentList", false);
	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_oAgentList=ds_oAgentList"	// outDs
		,"PSEARCHGUBUN=" + quote(sSearchGubun)
			+ " PSEARCH=" + quote(sKeyWord) // args
		,"lfn_AfterSearch"
		,false
	);	
}

/* 공통 필수 종료 */

/*
 * 기능 : 공통코드 조회 후 처리해야 할 내용
 * parameter : nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_AfterSearch(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }
    
    medTotalCnt.Value = ds_oAgentList.GetCount();
    gfn_SearchMessage(gdAgentList); // 조회 메세지를 처리하고 그리드의 정렬 헤드 마크를 제거한다.
}

/* 이벤트 함수 종료 */


/* 사용자 함수 */
function gdAgentList_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex) {
    btnSelect_OnClick(obj);
}

function btnSelect_OnClick(obj) {
	if(ds_oAgentList.RowCount() <= 0 || ds_oAgentList.currow <= 0 ){
		gfn_Alert("선택 목록이 없습니다.");
		return false;
	}
	
    var arrArg = Array(8);
    arrArg[0] = ds_oAgentList.GetColumn(ds_oAgentList.currow,  "ENTRPS_NM"); 	//상호
    arrArg[1] = ds_oAgentList.GetColumn(ds_oAgentList.currow,  "RPRSNTV"); 	//대표자
    arrArg[2] = ds_oAgentList.GetColumn(ds_oAgentList.currow,  "POST_NO");	//주소-우편번호
    arrArg[3] = ds_oAgentList.GetColumn(ds_oAgentList.currow,  "BASS_ADRES");	//주소-기본주소
    arrArg[4] = ds_oAgentList.GetColumn(ds_oAgentList.currow,  "ETC_ADRES");	//주소-상세주소
    arrArg[5] = ds_oAgentList.GetColumn(ds_oAgentList.currow,  "TLPHON_NO");	//전화번호 
    arrArg[6] = ds_oAgentList.GetColumn(ds_oAgentList.currow,  "BSNM_REGIST_NO");	//사업자 등록 번호
    arrArg[7] = ds_oAgentList.GetColumn(ds_oAgentList.currow,  "REGIST_NO");	//등록번호 -면허번호 
    arrArg[8] = ds_oAgentList.GetColumn(ds_oAgentList.currow,  "ENTRPS_SN");	//업체일련번호
    
    Close(arrArg);
}

function gdAgentList_OnKeyDown(obj,nChar,bShift,bCtrl,bAlt,nLLParam,nHLParam)
{
	if (nChar == 13) { // Enter Key   
        btnSelect_OnClick(obj);
    }
}

function edSearch_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{
	if (nChar == 13) { // Enter Key
        lfn_Search();
    }
}

function rdGubun_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	edSearch.Text = "";
	edSearch.SetFocus();
}
