        //gdZipCode.Width = nCx - gdZipCode.Left - 4;
    }
}

/*
 * 기능 : 저장
 */
function lfn_Save(obj) {
	if (!gfn_FormValidate(this)) { // 입력 Validation Check
		return false;
	}

    if (medZipCode.Value == medZipCodeAfter.Value
        && edSido.Text == edSidoAfter.Text
        && edKu.Text == edKuAfter.Text

        && edRoadNm.Text == edRoadNmAfter.Text
        && edNewbunBun.Text == edNewbunBunAfter.Text
        && edNewbunBu.Text == edNewbunBuAfter.Text
        && edBuldNm.Text == edBuldNmAfter.Text
        && edSangseBuldNm.Text == edSangseBuldNmAfter.Text
        && edLegalCd.Text == edLegalCdAfter.Text
        && edBuldNo.Text == edBuldNoAfter.Text) {
        gfn_Alert("변경된 정보가 없습니다.");
        return false;
    }

    if (!gfn_Confirm(MSGW00040)) { // 정말로 수정 저장 하시겠습니까?
        return false;
    }
    // 입력된 값을 그리드로 복사한다.

	tit_clearActionInfo();
	tit_addSingleActionInfo("cust:updateCust4041NewAddressChange", "", 0, "");
	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,""	// outDs
		,"NZMGNO=" + quote(sZseq)
          + " NZZIPCDE=" + quote(medZipCodeAfter.Text)
          + " NZSIDO=" + quote(edSidoAfter.Text)
          + " NZKU=" + quote(edKuAfter.Text)
          + " NZROADNM=" + quote(edRoadNmAfter.Text)          
          + " NZNEWBONBEN=" + quote(edNewbunBunAfter.Text)
          + " NZNEWBUBEN=" + quote(edNewbunBuAfter.Text)
          + " NZBULDNM=" + quote(edBuldNmAfter.Text)
          + " NZSANGSEBULDNM=" + quote(edSangseBuldNmAfter.Text)
          + " NZLEGALCD=" + quote(edLegalCdAfter.Text)
          + " NZBULDNO=" + quote(edBuldNoAfter.Text)// args 
		,"lfn_AfterSaveZipCode"
		,false
	);
}

// 해당자료변경
