// 변경전 주소검색 팝업
function lfn_SearchAddressPopup(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{
	//if (nChar == 13) {
	if (nChar == 13 || Length(medZipCode.Value) == 5) {
        var sArg = "sZipcode=" + quote(medZipCode.Value);
        var arrZipCode = Dialog("sys::frmsys0053PNewZipCodeList.xml", sArg, 600, 400, "", -1, -1); // 우편번호 조회 팝업
        if (length(arrZipCode) > 0) {
            var arrArg = Array(5);
            medZipCode.Value = arrZipCode[0];
            sZseq = arrZipCode[2];
            arrArg = split(arrZipCode[1], " ");
            edSido.Text = arrArg[0];
            edKu.Text = arrArg[1];
            edRoadNm.Text = arrZipCode[3];
            edBuldNm.Text = arrZipCode[4];            
            edNewbunBun.Text = arrZipCode[5];
            edNewbunBu.Text = arrZipCode[6];
            edSangseBuldNm.Text = arrZipCode[7];
            edLegalCd.Text = arrZipCode[8];
            edBuldNo.Text = arrZipCode[9];

            medZipCodeAfter.Value = arrZipCode[0];
            edSidoAfter.Text = arrArg[0];
            edKuAfter.Text = arrArg[1];
            edRoadNmAfter.Text = arrZipCode[3];
            edBuldNmAfter.Text = arrZipCode[4];
            edNewbunBunAfter.Text = arrZipCode[5];
            edNewbunBuAfter.Text = arrZipCode[6];
            edSangseBuldNmAfter.Text = arrZipCode[7];
            edLegalCdAfter.Text = arrZipCode[8];
            edBuldNoAfter.Text = arrZipCode[9];
            medZipCodeAfter.SetFocus();
        } else {
            medZipCode.SetFocus();
        }
	}
}

// 변경후 주소검색 팝업
function lfn_SearchAddressPopupAfter(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{
	//if (nChar == 13) {
	if (nChar == 13 || Length(medZipCodeAfter.Value) == 5) {
        var sArg = "sZipcode=" + quote(medZipCode.Value);
        var arrZipCode = Dialog("sys::frmsys0053PNewZipCodeList.xml", sArg, 600, 400, "", -1, -1); // 우편번호 조회 팝업
        if (length(arrZipCode) > 0) {
            var arrArg = Array(5);
            arrArg = split(arrZipCode[1], " ");            
            medZipCodeAfter.Value = arrZipCode[0];
            edSidoAfter.Text = arrArg[0];
            edKuAfter.Text = arrArg[1];
            medZipCodeAfter.SetFocus();
        } else {
            medZipCodeAfter.SetFocus();
        }
	}
}
function chkAll_OnClick(obj,strValue)
{
	if (chkAll.Value == "Y") {
        chkManager.Value = "Y";
        chkBuilding.Value = "Y";
        //chkEdu.Value = "Y";
        chkAgency.Value = "Y";
        chkMemberFee.Value = "Y";
        chkEduFee.Value = "Y";
        chkTank1.Value = "Y";
        chkTank2.Value = "Y";
	}
}
