#include "LIB::cust.js"; // 공통업무 관련

var gubun = "";     // 대상물구분값
/* 공통 필수 시작 */
function Form_OnLoadCompleted(obj) {
    gfn_Form_OnLoadCompleted(obj); // 공통 폼 로딩 이벤트 처리

    // 공통 코드 조회
    gfn_SearchJibu(cbxJibu, "Y");

    //sJibu
    cbxJibu.Value = sJibu;

    lfn_searchCust5015FireCodeList();   // 소방서/소방센터 조회
  //lfn_FilterFireStation(cbxJibu.Value);										//소방서 필터링

    lfn_FilterFireStation(sJibu);										//소방서 필터링
    if (IsExistVar("sBuildingNm")) {
        edKeyWord.Text = sBuildingNm;
        lfn_Search();
    }
    //cbxJibu.Enable = false;


    if (sCourse ==10) {
        radBuilding.Value = "1";
    } else if (sCourse ==30) {
        radBuilding.Value = "2";
    } else if (sCourse ==50) {
        radBuilding.Value = "3";
    }

}

/***************************
 * 공통 코드 조회
 ***************************/
// 소방서/소방센터 조회
function lfn_searchCust5015FireCodeList(obj) {

	ds_oFireCd.ClearData();	//소방서 초기화
	ds_oFireCenterCd.ClearData();	//소방센터 초기화

    tit_clearActionInfo();
    tit_addSearchActionInfo("cust:searchCust0010FireInfoCode", false);
    tit_addSearchActionInfo("cust:searchCust0010FireInfoCenterCode", false);
    // 서버 호출
    tit_callService(
        ""
        , ""
        , "" // inDs
        , "ds_oFireCd=ds_oFireCd ds_oFireCenterCd=ds_oFireCenterCd" // outDs
        , ""	//args
        , ""	//callBackFunc
        ,false
        ,false
        ,true
    );
}

/////////////////////////////////////////*
 * 기능 : 조회
 */
function lfn_Search(obj) {
    ds_oList.ClearData(); // 데이타셋 초기화

    tit_clearActionInfo();
	tit_addCsvSearchActionInfo("cust:searchCust5015FireList", false);
	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_oList=ds_oList"	// outDs
		,"GTMGNO=" + quote(cbxJibu.Value)   
		  +" STATION=" + quote(cbxStation.Value)
		  +" BUILDING=" + quote(radBuilding.Value)     
		  +" SEARCHGUBUN=" + quote(radAgent.Value) 
          + " KEYWORD=" + quote(edKeyWord.Text)
          + " ROWNUM=" + quote(edCount.Text)  
		,"lfn_AfterSearch"
		,false
	);
}

/* 공통 필수 종료 */

/*
 * 기능 : 공통코드 조회 후 처리해야 할 내용
 * parameter : nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_AfterSearch(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }
    //trace(ds_oList.SaveXML());
    gubun = radBuilding.Value;

    gfn_SearchMessage(gdBuidingList); // 조회 메세지를 처리하고 그리드의 정렬 헤드 마크를 제거한다.
}

/* 이벤트 함수 종료 */


/* 사용자 함수 */
// 지부 변경
function cbxJibu_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	lfn_FilterFireStation(strCode);
} 

function btnSelect_OnClick(obj) {
    if (ds_oList.GetCount() < 1) {
        gfn_Alert("선택된 대상물이 없습니다.");
        return;
    }
    var arrArg = Array(2);
    arrArg[0] = gubun;		        // 구분
    arrArg[1] = ds_oList.GetColumn(ds_oList.currow,  "MGNO");		        // 대상물관리번호 

    Close(arrArg);
}

function gdBuidingList_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex) {
    btnSelect_OnClick(obj);
}

function edBuildingNm_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam) {
    if (nChar == 13) { // Enter Key
        lfn_Search();
    }
}

/*
 * 기능 : 공통코드 조회 후 처리해야 할 내용
 * parameter : nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_AfterSearchManager(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }
    //trace(ds_oManagerList.SaveXML());

    gfn_SearchMessage(gdManagerList); // 조회 메세지를 처리하고 그리드의 정렬 헤드 마크를 제거한다.
}
function gdManagerList_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	var arrArg = Array(2);
	arrArg[0] = gubun;		        // 구분
    arrArg[1] = ds_oList.GetColumn(ds_oList.currow,  "MGNO");		        // 관리번호

    Close(arrArg);
}

// 소방서 필터링
function lfn_FilterFireStation(sFilterGubun) {	
	ds_oFireCd.UnFilter();

	var ls_FilterExpr = "";
    if (!gfn_IsNull(sFilterGubun)) {
        ls_FilterExpr += "JIBUCD=='" + trim(sFilterGubun) + "'";
    }

    ds_oFireCd.Filter(ls_FilterExpr);

	if( ds_oFireCd.FindRow("DATA", cbxStation.Text) == -1) {
		cbxStation.Text = "";
    }
    cbxStation.Index = 0;
}

function edKeyWord_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam) {
    if (nChar == 13) { // Enter Key
        lfn_Search();
    }
}
