// 구분코드 조회 
function lfn_CompanyCode(obj) {
	ds_ioObjGubun.ClearData();		// 업체구분

    tit_clearActionInfo();
    tit_addSearchActionInfo("cust:searchCust5020CompanyGubun", false);
    // 서버 호출
    tit_callService(
        ""
        , ""
        , "" // inDs
        , "ds_ioObjGubun=ds_oCompanyGubun" // outDs
        , "CTCD=" + quote(substr(tab.page1.cbxMFHead.Value,0,2))	//args
        , ""	//callBackFunc
        ,false
        ,false
        ,true
    );
}

