    tit_callService(
        ""
        , ""
        , "" // inDs
        , "ds_ioObjGubun=ds_oCompanyGubun" // outDs
        , "CTCD=" + quote(substr(tab.page2.cbxMFHead.Value,0,2))	//args
        , ""	//callBackFunc
        ,false
        ,false
        ,true
    );
}

