		parValue += " sMFPIS3_SN="+quote(ds_ioMFFireObjD2.GetColumn(0,"ENTRPS_SN"));
		parValue += " sMFPIS4_SN="+quote(ds_ioMFFireObjD2.GetColumn(0,"HOLD_QUALFUSER_SN"));
		parValue += " sMFPIS5_SN="+quote(ds_ioMFFireObjD2.GetColumn(0,"QUALFUSER_SN"));		
		parValue += " sCTCD=" + quote(substr(tab.page2.cbxMFHead.Value,0,2));		// 지역코드

		var arrNo = Dialog("cust::frmcust5010.xml", parValue, 800, 590, "", -1, -1);
	} else {
		alert("선택된 소방기술자 자료가 없습니다.");
		return;
	}
}

/*================================    연계버튼 처리 	======================================*/
//  연계해지  처리 
function lfn_CancelMatch2(obj)
{  


	/* 체크사항 */
	// 안전원 
	if (ds_ioKFFireObj2.GetCount() < 1) {
		alert("[한국소방안전원] 연계대상을 지정해주세요.");
		return;
	}	
	if (ds_ioKFFireObj2.GetColumn(ds_ioKFFireObj2.currow,"RFLAGEE") < 1 ) {
		alert("[한국소방안전원] 연계된 소방기술자를 지정해주세요.");
		return;
	}
	// 청
	if (ds_ioMFFireObjD2.GetCount() < 1) {
		alert(ORGNM + " 소방기술자를 지정해주세요.");
		return;
	}	
	if (ds_ioMFFireObjD2.GetColumn(0,"MSTATUS") != "1") {
		alert(ORGNM + " 연계된 소방기술자를 지정해주세요.");
		return;
	}

	// 연계 대상 확인 
	if (ds_ioKFFireObj2.GetColumn(ds_ioKFFireObj2.currow,"MMGNO") != ds_ioMFFireObjD2.GetColumn(0,"MMGNO")) {
		alert("잘못된 연계대상입니다.");
		return;
	}

	// 최종 연계해지 여부 확인	
	var meg = "        " + ORGNM + " 소방기술자: "+ds_ioMFFireObjD2.GetColumn(0,"NM") +"\n";
	meg += "     [한국소방안전원] 소방기술자 : "+ds_ioKFFireObj2.GetColumn(ds_ioKFFireObj2.currow,"FMNM") +"\n";
	meg +="   연계해지 하시겠습니까?";

	if(!gfn_Confirm(meg)) {	
		return;
	}



	// 연계해지 사유 
	var cancelReason = Dialog("cust::frmcust5011.xml", "", 520, 188, "", -1, -1);	
	if (cancelReason == "" || cancelReason == null) {
		alert("연계 사유를 입력해 주세요");
		return;
	}


	tit_clearActionInfo();
	tit_addSingleActionInfo("cust:updateCust6200MatchCancel", "", 0, "");     // 연계취소 
	tit_addSingleActionInfo("cust:insertCust0010AdditionInfo", "", 0, "");   // 부가정보

	var parValue = "MFPIS3=" + quote(ds_ioMFFireObjD2.GetColumn(0,"ENTRPS_SN"));
	parValue += " MFPIS4=" + quote(ds_ioMFFireObjD2.GetColumn(0,"HOLD_QUALFUSER_SN"));
	parValue += " MFPIS5=" + quote(ds_ioMFFireObjD2.GetColumn(0,"QUALFUSER_SN"));
	parValue += " MKFSAPK=" + quote(ds_ioKFFireObj2.GetColumn(ds_ioKFFireObj2.currow,"CMGNO"));	
	parValue += " MSABUN=" + quote(gds_oUserInfo.GetColumn(0,"PSABUN"));
	parValue += " MMGNO=" + quote(ds_ioKFFireObj2.GetColumn(ds_ioKFFireObj2.currow,"MMGNO"));	
	parValue += " MDELREMARK=" + quote(cancelReason);
	parValue += " SCMGNO=" + quote(ds_ioKFFireObj2.GetColumn(ds_ioKFFireObj2.currow,"CMGNO"));	
	parValue += " ADCD=8";
	parValue += " ADCONTENT=" + quote("[연계해지사유]" + cancelReason);
	parValue += " CTCD=" + quote(substr(tab.page2.cbxMFHead.Value,0,2));

	// 서버 호출 
	tit_callService( 
		""
		,""
		,""	// inDs
		,"" // outDs	  	  
	    ,parValue
		,"lfn_AfterCancelMatch2"
		,false
		,false
		,true
	);
}

// 8  lfn_CancelMatch2 처리 후 
function lfn_AfterCancelMatch2(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }

    // 매치이후 처리 
    lfn_MatchAfter2("C");

	/* 조회 */
	lfn_KFSearch2();  // 안전원정보조회 
    lfn_MatchData2(); // 청 정보조회 (안전원 더블 클릭 조회 )

    /* 메세지 */
    alert("연계해지가 완료되었습니다.");

}

function lfn_NowMatch2(obj)
{  
	/* 체크 사항 */
	// 안전원 
	if (ds_ioKFFireObj2.GetCount() < 1) {
		alert("[한국소방안전원] 연계할 소방기술자를 지정해주세요.");
		return;
	}	
	if (ds_ioKFFireObj2.GetColumn(ds_ioKFFireObj2.currow,"RFLAGEE") > 0 ) {
		alert("[한국소방안전원] 연계된 소방기술자입니다.");
		return;
	}
	// 청
	if (ds_ioMFFireObjD2.GetCount() < 1) {
		alert(ORGNM + " 연계할 소방기술자를 지정해주세요.");
		return;
	}		
	if (ds_ioMFFireObjD2.GetColumn(0,"MSTATUS") == "1") {
		alert(ORGNM + " 연계된 소방기술자입니다.");
		return;
	}

	// 최종 연계 여부 확인	
	var meg = "        " + ORGNM + " 소방기술자: "+ds_ioMFFireObjD2.GetColumn(0,"NM") +"\n";
	meg += "     [한국소방안전원] 소방기술자 : "+ds_ioKFFireObj2.GetColumn(ds_ioKFFireObj2.currow,"FMNM") +"\n";
	meg +="   연계 하시겠습니까?";

	if(!gfn_Confirm(meg)) {	
		return;
	}


	/* 서버 호출 */
	// SQL 설정 
	tit_clearActionInfo();
	tit_addSingleActionInfo("cust:insertCust6200Match", "", 0, "");     	  // 연계 작업
	tit_addSingleActionInfo("cust:insertCust0010AdditionInfo", "", 0, "");    // 부가정보

	var parValue = "MFPIS3=" + quote(ds_ioMFFireObjD2.GetColumn(0,"ENTRPS_SN"));
	parValue += " MFPIS4=" + quote(ds_ioMFFireObjD2.GetColumn(0,"HOLD_QUALFUSER_SN"));
	parValue += " MFPIS5=" + quote(ds_ioMFFireObjD2.GetColumn(0,"QUALFUSER_SN"));
	parValue += " MKFSAPK=" + quote(ds_ioKFFireObj2.GetColumn(ds_ioKFFireObj2.currow,"CMGNO"));	
	parValue += " SBMGNO=" + quote(ds_ioKFFireObj2.GetColumn(ds_ioKFFireObj2.currow,"BMGNO"));
	parValue += " FIRECD=" + quote(ds_ioMFFireObjD2.GetColumn(0,"CMPN119SC_ID"));
	parValue += " MSABUN=" + quote(gds_oUserInfo.GetColumn(0,"PSABUN"));
	// 부가정보
	parValue += " SCMGNO=" + quote(ds_ioKFFireObj2.GetColumn(ds_ioKFFireObj2.currow,"CMGNO"));
	parValue += " ADCD=8";    
    // 23.12 서울지부 분리    
	parValue += " ADCONTENT=" + quote("데이터연계 일치화처리(" + left(GetDate(), 4)+"-"+mid(GetDate(),4,2)+"-"+mid(GetDate(),6,2)+")");
	parValue += " CTCD=" + quote(substr(tab.page2.cbxMFHead.Value,0,2));

	// 서버 호출 
	tit_callService( 
		""
		,""
		,""	// inDs
		,"" // outDs	  	  
	    ,parValue
		,"lfn_AfterNowMatch2"
		,false
		,false
		,true
	);
}

// 8  lfn_NowMatch 처리 후 
function lfn_AfterNowMatch2(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }

	// 매치이후 처리 
    lfn_MatchAfter2("U");

	/* 조회 */
	lfn_KFSearch2();  // 안전원정보조회 
    lfn_MatchData2(); // 청 정보조회 (안전원 더블 클릭 조회 )

    /* 메세지 */
	alert("연계가 완료되었습니다.");


}

/*
	수정 연계
*/
function btnModMatch_OnClick2(obj) {

	/* 체크 사항 */
	// 안전원 
	if (ds_ioKFFireObj2.GetCount() < 1) {
		alert("[한국소방안전원] 연계할 소방기술자를 지정해주세요.");
		return;
	}	
	if (ds_ioKFFireObj2.GetColumn(ds_ioKFFireObj2.currow,"RFLAGEE") > 0 ) {
		alert("[한국소방안전원] 연계된 소방기술자입니다.");
		return;
	}
	// 청
	if (ds_ioMFFireObjD2.GetCount() < 1) {
		alert(ORGNM + " 연계할 소방기술자를 지정해주세요.");
		return;
	}		
	if (ds_ioMFFireObjD2.GetColumn(0,"MSTATUS") == "1") {
		alert(ORGNM + " 연계된 소방기술자입니다.");
		return;
	}



	var sStatus = "U";
	var sBILD_SN = "";
	var sCMGNO = "";	


	sCMGNO = ds_ioKFFireObj2.GetColumn(ds_ioKFFireObj2.currow,"CMGNO");


	// 파라미터값 설정 
	var parValue = "sStatus=" + quote(sStatus); // 연계모드
	parValue += " sENTRPS_SN=" + quote(ds_ioMFFireObjD2.GetColumn(0,"ENTRPS_SN"));
	parValue += " sHOLD_QUALFUSER_SN=" + quote(ds_ioMFFireObjD2.GetColumn(0,"HOLD_QUALFUSER_SN"));
	parValue += " sQUALFUSER_SN=" + quote(ds_ioMFFireObjD2.GetColumn(0,"QUALFUSER_SN"));
//	parValue += " sBSNM_REGIST_NO=" + quote(ds_ioMFFireObjD2.GetColumn(0,"BSNM_REGIST_NO")); // 사업자등록번호
//	parValue += " sENTRPS_SE=" + quote(ds_ioMFFireObjD2.GetColumn(0,"ENTRPS_SE")); // 업체구분
//	parValue += " sENTRPS_DETAIL_SE=" + quote(ds_ioMFFireObjD2.GetColumn(0,"ENTRPS_DETAIL_SE")); // 업체상세구분
//	parValue += " sHOLD_QUALFUSER_SN=" + quote(ds_ioMFFireObjD2.GetColumn(0,"HOLD_QUALFUSER_SN")); // 보유자격자 일련번호
//	parValue += " sQUALFUSER_SN=" + quote(ds_ioMFFireObjD2.GetColumn(0,"QUALFUSER_SN")); // 자격자 일련번호
	parValue += " sCMGNO=" + quote(sCMGNO); 	// 안전원고객관리번호	
	parValue += " sJIBU=" + quote(tab.page2.cbxKFJibu.Value);
	parValue += " sCTCD=" + quote(substr(tab.page2.cbxMFHead.Value,0,2));
	//parValue += " sAPNTDE=" + quote(ds_ioMFFireObjD.GetColumn(0,"APNTDE"));
	//parValue += " sUSER_SN=" + quote(ds_ioMFFireObjD.GetColumn(0,"QUALFUSERSN"));

	var arrNo = Dialog("cust::frmcust6201PDataConnectionSokiManageSave.xml", parValue , 800, 700, "", -1, -1);

	if(arrNo == "U"){	
		// 매치후 처리
		lfn_MatchAfter2(arrNo);
	}

	/* 조회 */
	lfn_KFSearch2();  // 안전원정보조회 
    lfn_MatchData2(); // 청 정보조회 (안전원 더블 클릭 조회 )
}


// 연계관련 버튼 
function lfn_AllMatchBTN2(nRow) {
	// ROW값 셋팅 
	var MRow = "";
	var KRow = "";
	if (nRow == "" || nRow == null) {
		if (ds_ioMFFireObjD2.GetCount() > 0) { MRow = ds_ioMFFireObjD2.currow;}
	} else {
		MRow = nRow;
	}
	if (ds_ioKFFireObj2.GetCount() > 0) { KRow = ds_ioKFFireObj2.currow; }

	// 버튼 모두 ENAVLE : FALSE
	tab.page2.btnMatchCancel.Enable = false;
	tab.page2.btnMatch.Enable = false;
	tab.page2.btnModMatch.Enable = false;

	// 연계해지 버튼 활성화 
	if (
		ds_ioMFFireObjD2.GetColumn(0,"MSTATUS") > 0 
		&& ds_ioKFFireObj2.GetColumn(ds_ioKFFireObj2.currow,"RFLAGEE") > 0
		&& (ds_ioMFFireObjD2.GetColumn(0,"MMGNO") == ds_ioKFFireObj2.GetColumn(KRow,"MMGNO"))
	   )
	{
		tab.page2.btnMatchCancel.Enable = true;
	} else {
		tab.page2.btnMatchCancel.Enable = false;
	}

	// 수정연계 , 수정없이연계처리  버튼 활성화 
	if (
		ds_ioMFFireObjD2.GetColumn(0,"MSTATUS") == 0 
		&& ds_ioKFFireObj2.GetColumn(KRow,"RFLAGEE") == 0
	   )
	{
		tab.page2.btnMatch.Enable = true;
		tab.page2.btnModMatch.Enable = true;
	} else {
		tab.page2.btnMatch.Enable = false;
		tab.page2.btnModMatch.Enable = false;
	}
}

/*================================    매칭 컬러  ======================================*/

function lfn_MatchColor2(mode,nRow) {	
	if (mode == "ON") {

		// 대상물		
		var BNM = substr(ds_ioKFFireObj2.GetColumn(ds_ioKFFireObj2.currow,"BSEARCHNM"));
		var OBJ_NM = ds_ioMFFireObjD2.GetColumn(0,"CFSERCHNM",0,length(BNM));

		OBJ_NM = trim(OBJ_NM);
		BNM = trim(BNM);

		if (OBJ_NM == BNM) {
			if (OBJ_NM == null || OBJ_NM = "") {
				tab.page2.edMFOBJ_NM.BKColor = "white";
				tab.page2.edKFBNM.BKColor = "white";
			} else {
				tab.page2.edMFOBJ_NM.BKColor = "lightskyblue";
				tab.page2.edKFBNM.BKColor = "lightskyblue";
			}
		} else {
			tab.page2.edMFOBJ_NM.BKColor = "white";
			tab.page2.edKFBNM.BKColor = "white";
		}


