			tab.page2.edKFRoadNM.BKColor = "white";
		}		


		// 건물번호1
		var SEARCHMAINNO = ds_ioMFFireObjD2.GetColumn(0,"MAIN_BDNBR");
		var BMAINNO = ds_ioKFFireObj2.GetColumn(ds_ioKFFireObj2.currow,"RBUILDMAINNO");

