		// 건물번호2
		var SEARCHSUBNO = ds_ioMFFireObjD2.GetColumn(0,"SEC_BDNBR");
		var BSUBNO = ds_ioKFFireObj2.GetColumn(ds_ioKFFireObj2.currow,"RBUILDSUBNO");

