/*================================    연계후 처리	======================================*/

function lfn_MatchAfter2(mode) {
	 if (mode == "I") {    // 신규 연계
	} else if (mode == "U") {   // 수정없이 연계 (수정 연계)
		var KFSTATUS = ds_ioKFFireObj2.GetColumn(ds_ioKFFireObj2.currow,"RFLAGEE");

		KFSTATUS++;

		// 연계값 적용
		ds_ioKFFireObj2.SetColumn(ds_ioKFFireObj2.currow,"RFLAGEE",KFSTATUS);

		// 필터링		
		if(tab.page2.radKFSelectGubun.Value == "1" || tab.page2.radKFSelectGubun.Value == "3") {
			setRow = ds_ioKFFireObj2.currow;
			ds_ioKFFireObj2.Filter("RFLAGEE>='1'");
			tab.page2.edKFCount.Text = ds_ioKFFireObj2.GetCount();
			ds_ioKFFireObj2.row = setRow;
		}

	} else if (mode == "C") {     // 연계 취소 
		// 상현재 연계 값 가져오기 
		var KFSTATUS = ds_ioKFFireObj2.GetColumn(ds_ioKFFireObj2.currow,"RFLAGEE");

		// 연계취소값 으로 변경 
		KFSTATUS--;

		// 적용 
		ds_ioKFFireObj2.SetColumn(ds_ioKFFireObj2.currow,"RFLAGEE",KFSTATUS);		

		// 필터링		
		if(tab.page2.radKFSelectGubun.Value == "2" || tab.page2.radKFSelectGubun.Value == "3") {
			setRow = ds_ioKFFireObj2.currow;
			ds_ioKFFireObj2.Filter("RFLAGEE=='0'");
			tab.page2.edKFCount.Text = ds_ioKFFireObj2.GetCount();
			ds_ioKFFireObj2.row = setRow;
		}

	}
}

/*================================    조건 연계	======================================*/
// 청 조회조건변경(연계구분)
function tab_page2_radKFSelectGubun_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	lfn_SearchBalance2("12");
}
// 청 조회조건변경 (대상물)
function tab_page2_cbxKFCourseD_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	lfn_SearchBalance2("14");
}



function lfn_SearchBalance2(gubun){
	if (gubun == "11") {
		var JIBUCD = tab.page2.cbxKFJibu.Value;
		if (JIBUCD == "1141") {
			 JIBUCD = "1081";
		}
		var row = ds_oFireHead2.FindRow("JIBUCD", JIBUCD );	
		tab.page2.cbxMFHead.Value = ds_oFireHead2.GetColumn(row,"CD");
		cbxMFHead_OnChanged2("tab.page2.cbxMFHead",tab.page2.cbxMFHead.Value);
		lfn_FilterFireStation2(tab.page2.cbxMFHead.Value);
	} else if (gubun == "12") {
		tab.page2.radMFSelectGubun.Value = tab.page2.radKFSelectGubun.Value;
	} else if (gubun == "14") {
		if (tab.page2.cbxKFCourseD.Value == "54") {
			tab.page2.cbxMFObjGubun.Value = "0270009";
		} else if (tab.page2.cbxKFCourseD.Value == "55") {
			tab.page2.cbxMFObjGubun.Value = "100000361";
		} else if (tab.page2.cbxKFCourseD.Value == "53") {
			tab.page2.cbxMFObjGubun.Value = "100000374";
		} else if (tab.page2.cbxKFCourseD.Value == "52") {
			tab.page2.cbxMFObjGubun.Value = "100000375";
		} else if (tab.page2.cbxKFCourseD.Value == "51") {
			tab.page2.cbxMFObjGubun.Value = "100000376";
		} else {
			tab.page2.cbxMFObjGubun.index = 0;
		}		
	}
}


