/*===================================   엑셀 ====================================================*/
function tab_page2_btnKFExcel_OnClick(obj)
{
	if(ds_ioKFFireObj2.rowcount == 0){
		alert("데이터가 없습니다.");
		return;
	}

	// 개인정보 다운로드 사유 등록
	var parValue = "sCode=1452";	// COMMON.PERSONALDOWNLOADH
		parValue += " sRowCnt=" + ds_ioKFFireObj2.GetRowCount();
		parValue += " sTitle=" + quote(tab.page2.gdKFOBJList.UserData);
	var ls_IsRemark = Dialog("COM::frmCOM2100PDownloadRemark.xml", parValue, "", "", "", -1, -1);	

	if (ls_IsRemark == "" || ls_IsRemark == null || ls_IsRemark == "FAIL") {
		return false;
	}
	
	if (ds_ioKFFireObj2.GetCount() > 0) {
		tab.page2.gdKFOBJList.ExportExcelEx("sheet1");
	}
}

function tab_page2_btnMFExcel_OnClick(obj)
{	
	if(ds_ioMFFireObj2.rowcount == 0){
		alert("데이터가 없습니다.");
		return;
	}

	// 개인정보 다운로드 사유 등록
	var parValue = "sCode=1451";	// COMMON.PERSONALDOWNLOADH
		parValue += " sRowCnt=" + ds_ioMFFireObj2.GetRowCount();
		parValue += " sTitle=" + quote(tab.page2.gdMFOBJList.UserData);
	var ls_IsRemark = Dialog("COM::frmCOM2100PDownloadRemark.xml", parValue, "", "", "", -1, -1);	

	if (ls_IsRemark == "" || ls_IsRemark == null || ls_IsRemark == "FAIL") {
		return false;
	}

	if (ds_ioMFFireObj2.GetCount() > 0) {
		tab.page2.gdMFOBJList.ExportExcelEx("sheet1");
	}
}

