Window.Script:
/*
 * 프로그램명 : frmcust6301PDataConnectionTankManageSave.xml
 * 설명 : 데이터연계 일치화관리
 * 작성일 : 2010.05.17
 * 작성자 : 정현호
 */

#include "LIB::cust.js"; // 공통 관련

/* in 변수 
 sMNFCTRETC_SN : 제조소등일련번호
 sCMGNO   : 고객관리번호
 sStatus  : 처리 상태(I:신규, U:수정)
 sMNGR_SN : 안전관리자 선임 일련번호
 sAPNTRLSOFC_SE : 선해임구분 (AP : 선임, RL : 해임)
 sAPNTRLSOFC_DE  : 선임일자
 sJIBU    : 지부코드 
*/

/* OUT 변수 
 Close(I) : 신규저장 완료 후 창 닫을 경우 
 Close(U) : 변경저장 완료 후 창 닫을 경우
 Close()  : 저장없이 창 닫을 경우
*/

	// 기존 기초정보
var	sOldRegion = ""; 				// 지역
var	sOldCourse = ""; 				// 직능
var	sOldDetailCourse = ""; 			// 세부직능

var sFMmgnoAfter  = "";  // 변경선임자관리번호

/* 공통 필수 시작 */
function Form_OnLoadCompleted(obj) {
    gfn_Form_OnLoadCompleted(obj); // 공통 폼 로딩 이벤트 처리
    gfn_MakeRequiredInputMark(this); // 필수 입력란을 설정한다.

    lfn_AdditionStatus();       // 신규추가상태

    lfn_SetCheckStatus(false);  // 변경내용 적용전 체크박스 상태(체크안함)    

    lfn_SetEnableStatus(false); // 변경내용 적용전 상태(비활성화)

    // 공통 코드 조회
    gfn_SearchAllJibu(cbxJibu, "Y"); 											    // 지부 코드 조회
    gfn_SearchCodeRegion(gds_oUserInfo.GetColumn(0, "PDEPTCD"), ds_oRegion, "0");	// 지역 코드 조회
    gfn_SearchCustomerCourseFullName(ds_oCourse, "0");							    // 대표고객직능 조회
    gfn_SearchCustomerDetailCourseFullName("", ds_oDetailCourse, "0");			    // 세부고객직능 조회
    gfn_SearchCodeRegion(cbxJibu.Value, ds_oRegion, "0");						    // 지역 코드 조회

    //조회 초기값 셋팅
	cbxRegion.Value = ds_oRegion.GetColumn(0, "CD");
	cbxCourse.Value = ds_oCourse.GetColumn(5, "CD");
	cbxCourse.Enable = false;
	cbxCourse_OnChanged(object("cbxCourse"), cbxCourse.Value);
	cbxDetailCourse.Value = ds_oDetailCourse.GetColumn(0, "CD");

	// 회원상태
	cbxCustomerStatus.Enable = false;

	//청정보 조회
    var sArg = "MNFCTRETC_SN = " + quote(sMNFCTRETC_SN);	//'28000000062725'"; // 제조소 관리번호
	lfn_FPISSearchFPISMnfctretcInfo(sArg);                 			// 청정보 호출

    if(sStatus == "I") {
        lbInStatus.Text = "신규";

        // 데이타셋 입력가능상태로 만들기
		lfn_AddDataMain();

    } else {
        lbInStatus.Text = "수정";
        btnBuildingSearch.Visible = false;

        // 안전원정보 조회
        var sArg = "CMGNO = " + sCMGNO; // + quote(sCmgno);	// 기존고객번호
        lfn_SearchAllInfo(sArg);                // 기존 고객정보 호출
        lfn_oldInfoCopy(); 				// 적용전 안전원정보 복사
    }


    ds_oFireBonbuCd.Copy("gds_oFireBonbuCode");		 //소방본부 콤보세팅

    // 해당본부 세팅
    var sBonbuCd = "";
	for (var i=0; i < ds_oFireBonbuCd.GetCount(); i++) {
		var jibucd = ds_oFireBonbuCd.GetColumn(i,"JIBUCD");
		if (cbxJibu.value == jibucd){
			if(sBonbuCd == ""){
				sBonbuCd = ds_oFireBonbuCd.GetColumn(i,"CD");		
			}
		}    
    }
    cbxBonbu.Value = sBonbuCd;    

	ds_oFireCd.Copy("gds_oFireCode");  				 //소방서 콤보세팅
	ds_oFireCenterCd.Copy("gds_oFireCenterCode");    //소방센터 콤보세팅

	lfn_FilterFireStation(cbxJibu.Value);     		 //소방서 필터링

}

function Form_OnUnloadCompleted(obj) {
    Close();
}

/*
 * 기능 : 종료
 */
function lfn_End(obj) {
    Close();
}
/* 공통 필수 종료 */

/* 공통 함수 */

// 신규추가 상태 세팅
function lfn_AdditionStatus() {	

	medCNo.Value = "";						//고객번호
	medCNo.Enable = false;					//고객번호
    //cbxCourse.Enable = true;					//대표직능

	/* 대상물 정보 */


	/* 고객정보 */
	medRegYYMM.Value = left(GetDate(), 6);											//등록년월
	medFeeYYMM.Value = left(GetDate(), 6);											//회비부과년월
	cbxDetailCourse_OnChanged(object("cbxDetailCourse"), cbxDetailCourse.Value);	//인력구분 Event 강제 발생 시킴

	/* 선임자 정보 */
	calFMStartDate.Value = left(GetDate(), 8);										//선임일

	cbxRegion.SetFocus();						//지역 콤보 포커스
}

/*
 * 기능 : 초기화 (모든 데이터셋)
 */
function lfn_ClearData() {
	//Dataset Clear
	ds_ioCustomerInfo.ClearData(); 		// 고객 정보(MAIN 1)
	ds_ioBuildingInfo.ClearData(); 		// 대상물 정보(MAIN 2)
	ds_ioManagerInfo.ClearData(); 		// 선임자 정보(MAIN 3)
	ds_ioDangerInfo.ClearData(); 		// 위험물허가정보
	ds_ioAdditionInfo.ClearData();		// 고객 부가 정보(SUB 1)

	lbCancelDate.Value = "";			// 선임자 해임처리시스템일시
}

/*
 * 기능 : Main 데이타셋 입력가능상태로 만들기
 */
function lfn_AddDataMain() {
	ds_ioCustomerInfo.InsertRow(0); 		// 고객 정보(MAIN 1)
	ds_ioBuildingInfo.InsertRow(0); 		// 대상물 정보(MAIN 2)
	ds_ioManagerInfo.InsertRow(0);			// 선임자 정보(MAIN 3)
	ds_ioAdditionInfo.InsertRow(0);			// 고객 부가 정보(SUB 1)
}

/*
 * 기능 : CheckBox Check 여부
 */
function lfn_SetCheckStatus(bStatus) {

    chkBuilding.Value = bStatus;
    chkRegion.Value = bStatus;
    chkCourse.Value = bStatus;
    chkDetailCourse.Value = bStatus;

	/* 대상물 정보 */
    chkBNM.Value = bStatus;
    chkAddr.Value = bStatus;
    chkFireCenter.Value = bStatus;
    chkTel.Value = bStatus;

    /* 고객정보 */
    chkCustomer.Value = bStatus;
    if (sStatus == "I") {
		chkRegYYMM.Value = bStatus;
		chkFeeYYMM.Value = bStatus;
	} else  {
		chkRegYYMM.Value = false;
		chkFeeYYMM.Value = false;	
	}

    /* 선임자 정보 */
    chkFireManager.Value = bStatus;
	chkFMNM.Value = bStatus;
	chkFMResidentno.Value = bStatus;
	chkFMStartDate.Value = bStatus;

	if (chkoldBuildingEndProc.Visible) {
		chkoldBuildingEndProc.Value = bStatus;  //기존 선임대상물 해임처리
	}

    /* 허가 정보 */

    /* 업무대행 정보 */

}

// 소방서 콤보 Changed 이벤트
function cbxStation_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	lfn_FilterFireCenter(strCode);	// 소방센터 필터링
}


// 소방서 필터링
function lfn_FilterFireStation(sFilterGubun) {	
	ds_oFireCd.UnFilter();

	var ls_FilterExpr = "";
    if (!gfn_IsNull(sFilterGubun)) {
        ls_FilterExpr += "JIBUCD=='" + trim(sFilterGubun) + "'";
    }

    ds_oFireCd.Filter(ls_FilterExpr);

	if( ds_oFireCd.FindRow("DATA", cbxStation.Text) == -1) {
		cbxStation.Text = "";
    }
}

// 소방센터 필터링
function lfn_FilterFireCenter(sFilterGubun) {	
	ds_oFireCenterCd.UnFilter();

	var ls_FilterExpr = "";
    if (!gfn_IsNull(sFilterGubun)) {
        ls_FilterExpr += "UPCD=='" + trim(sFilterGubun) + "'";
    } else {
		ls_FilterExpr += "UPCD=='" + trim("~!@#$%") + "'";
    }

    ds_oFireCenterCd.Filter(ls_FilterExpr);

	if( ds_oFireCenterCd.FindRow("DATA", cbxCenter.Text) == -1) {
		cbxCenter.Text = "";
    }
}


// 청정보 조회
function lfn_FPISSearchFPISMnfctretcInfo(sArg) 
{    
    ds_ioDanMnfctretcInfo.ClearData(); // 데이타셋 초기화

    tit_clearActionInfo();
    tit_addCsvSearchActionInfo("cust:searchCust6101FPISDanTankInfo", false);
    // 서버 호출
    tit_callService(
        ""
        , ""
        , "" // inDs
        , "ds_ioFPISDanTankInfo=ds_ioFPISDanTankInfo" // outDs
        , sArg
        , "lfn_AfterFPISSearchDanTankInfo"
        ,false
        ,false
        ,true
    );
}


/*
 * 기능 : 청조회 후 처리해야 할 내용
 * parameter : ErrorCode - 에러코드
 *             ErrorMsg - 에러메시지
 * Return : 없음
 */

function lfn_AfterFPISSearchDanTankInfo(nErrorCode, strErrorMsg) {

    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }

	if(ds_ioFPISDanTankInfo.rowcount = 0) {
		gfn_Alert(MSGI00010);	//검색된 레코드가 없습니다.
		lfn_Cancel();				//초기화
		if(length(ls_curComponent) > 0 ) {
			object(ls_curComponent).SetFocus();
		}
    }
}

// 안전원 대상물 검색
function btnBuildingSearch_OnClick(obj)
{
	//팝업
	var sArg  = "sBuildingNm=" + quote(trim(ds_oJubsuInfo.GetColumn(0, "FCBULDNAME")));
		sArg += " sGtmgno=" + quote(ds_oJubsuInfo.GetColumn(0, "CGTMGNO"));
	/*
     * arrNo[0] : OLD:검색된 경우, NEW:신규추가
     * arrNo[1] : 지부
     * arrNo[2] : 고객번호
     */
    var arrNo = Dialog("cust::frmcust0032PSearchbuilding.xml", sArg, 600, 400, "", -1, -1);
    if (length(arrNo) > 0) {
		if(arrNo[0] == "OLD") {	// 대상물이 검색된 경우
            sStatus = "U";      // 대상물 검색상태
            var sArg = "CGTMGNO=" + quote(arrNo[1]);	// 고객번호
            sArg += " CNO="	+ quote(arrNo[2]); 	// 지부코드

			lfn_SearchAllInfo(sArg);
		} else if(arrNo[0] == "NEW") {	// 신규추가일경우
            sStatus = "I";      // 대상물 신규상태
            cbxCourse.Value = ds_oJubsuInfo.GetColumn(0, "CGCOURSECD");
            cbxDetailCourse.Value = ds_oJubsuInfo.GetColumn(0, "CCOURSECD");

            lfn_SetEnableStatus(false);

            lfn_ClearData();					//초기화 (모든 데이터셋)	
            lfn_AddDataMain();					//Main 데이타셋 입력가능상태로 만들기
			lfn_AdditionStatus();
		}
		lfn_SetEnableStatus(false);
    }
}

// 고객 한방(고객(+고객부가정보)+대상물+선임자 조회)조회
function lfn_SearchAllInfo(sArg) 
{    
    ds_oAllObject.ClearData(); // 데이타셋 초기화

    tit_clearActionInfo();
    tit_addCsvSearchActionInfo("cust:searchCust0010AllSearch", false);
    // 서버 호출
    tit_callService(
        ""
        , ""
        , "" // inDs
        , "ds_oAllObject=ds_oAllObject" // outDs
        , sArg
        , "lfn_AfterSearchAllInfo"
        ,false
        ,false
        ,true
    );
}

/*
 * 기능 : 고객 한방조회 후 처리해야 할 내용
 * parameter : ErrorCode - 에러코드
 *             ErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_AfterSearchAllInfo(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }

	if(ds_oAllObject.rowcount > 0) {
		lfn_SeparateDataset();	
		lfn_searchCust0010DangerInfoList();    // 위험물 허가정보

		// 선임/해임
		if(ds_ioManagerInfo.GetColumn(ds_ioManagerInfo.currow, "FMHENDGUBUN") == 0) {	//해임
			lfn_EndGubunChange(false);
		} else {
			lfn_EndGubunChange(true);
		}
	} else {
		gfn_Alert(MSGI00010);	//검색된 레코드가 없습니다.
		lfn_Cancel();				//초기화
		if(length(ls_curComponent) > 0 ) {
			object(ls_curComponent).SetFocus();
		}
    }
}

// 위험물 허가정보
function lfn_searchCust0010DangerInfoList() {

	ds_ioDangerInfo.ClearData(); // 데이타셋 초기화

    tit_clearActionInfo();
    tit_addCsvSearchActionInfo("cust:searchCust0010DangerInfoList", false);
    // 서버 호출
    tit_callService(
        ""
        , ""
        , "" // inDs
        , "ds_ioDangerInfo=ds_ioDangerInfo" // outDs
        , "DBMGNO=" + quote(ds_ioBuildingInfo.GetColumn(0, "BMGNO")) // 대상물관리번호
        , "lfn_AfterSearchCust0010DangerInfoList"
        ,false
    );
}

/*
 * 기능 : 위험물 허가정보 조회 후 처리해야 할 내용
 * parameter : ErrorCode - 에러코드
 *             ErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_AfterSearchCust0010DangerInfoList(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }

    if (ds_ioDangerInfo.GetCount() < 1) {
        ds_ioDangerInfo.ClearData();
        ds_ioDangerInfo.ApplyChange();
    }
}


// 해임상태 해임/선임 표시
function lfn_EndGubunChange(bStatus) {
	if(bStatus) {	//선임
		lbFMEndStatus.Text = "선임";
		lbFMEndStatus.Color = "blue";
	} else {	//해임
		lbFMEndStatus.Text = "해임";
		lbFMEndStatus.Color = "brown";
	}
	lbFMEndStatus.Visible = true;
}

// 고객(+고객부가정보)+대상물+선임자 조회값 데이타셋 분리(TLogin 고려)
function lfn_SeparateDataset() {

    ds_ioCustomerInfo.ClearData();
    ds_ioBuildingInfo.ClearData();
    ds_ioManagerInfo.ClearData();
    //ds_ioAdditionInfo.ClearData();

    ds_ioCustomerInfo.InsertRow(0);
    ds_ioBuildingInfo.InsertRow(0);
    ds_ioManagerInfo.InsertRow(0);
    //ds_ioAdditionInfo.InsertRow(0);

    if(ds_oAllObject.rowcount == 0) { //데이타 미존재    
        return;
    }

    for(var i=0; i<ds_oAllObject.colcount; i++) {
        for(var j=0; j<ds_ioCustomerInfo.colcount; j++) {
            if (ds_oAllObject.GetColID(i) == ds_ioCustomerInfo.GetColID(j)) {
                ds_ioCustomerInfo.SetColumn(0, ds_ioCustomerInfo.GetColID(j), ds_oAllObject.GetColumn(0, ds_oAllObject.GetColID(i)));
            }
        }
        for(var j=0; j<ds_ioBuildingInfo.colcount; j++) {
            if (ds_oAllObject.GetColID(i) == ds_ioBuildingInfo.GetColID(j)) {
                ds_ioBuildingInfo.SetColumn(0, ds_ioBuildingInfo.GetColID(j), ds_oAllObject.GetColumn(0, ds_oAllObject.GetColID(i)));
            }
        }
        for(var j=0; j<ds_ioManagerInfo.colcount; j++) {
            if (ds_oAllObject.GetColID(i) == ds_ioManagerInfo.GetColID(j)) {
                ds_ioManagerInfo.SetColumn(0, ds_ioManagerInfo.GetColID(j), ds_oAllObject.GetColumn(0, ds_oAllObject.GetColID(i)));
            }
        }
/*        for(var j=0; j<ds_ioAdditionInfo.colcount; j++) {
            if (ds_oAllObject.GetColID(i) == ds_ioAdditionInfo.GetColID(j)) {
                ds_ioAdditionInfo.SetColumn(0, ds_ioAdditionInfo.GetColID(j), ds_oAllObject.GetColumn(0, ds_oAllObject.GetColID(i)));
            }
        }  */      
    }

    // 상단 조회박스
    cbxJibu.Value = ds_ioCustomerInfo.GetColumn(0, "CGTMGNO");
    cbxJibu_OnChanged(object("cbxJibu"), cbxJibu.Value);
    medCNo.Value = ds_ioCustomerInfo.GetColumn(0, "CNO");
    cbxRegion.Value = ds_ioCustomerInfo.GetColumn(0, "CREGCD");

    cbxCourse.Value = ds_ioCustomerInfo.GetColumn(ds_ioCustomerInfo.currow, "CGCOURSECD");				//대표직능
    cbxCourse_OnChanged(object("cbxCourse"), cbxCourse.Value);											//대표직능 변경 이벤트 강제 발생
    cbxDetailCourse.Value = ds_ioCustomerInfo.GetColumn(ds_ioCustomerInfo.currow, "CCOURSECD");			//세부직능
    lfn_FilterDetailCourse(ds_ioCustomerInfo.GetColumn(0, "CGCOURSECD"));								//세부고객직능 필터링

    ds_ioCustomerInfo.ApplyChange();
    ds_ioBuildingInfo.ApplyChange();	//고려
    ds_ioManagerInfo.ApplyChange();
}


// 적용전 안전원정보 dataset에 복사
function lfn_oldInfoCopy() {
	// 기초정보
	sOldRegion = cbxRegion.CodeColumn; 				// 지역
	sOldCourse = cbxCourse.CodeColumn; 				// 직능
	sOldDetailCourse = cbxDetailCourse.CodeColumn; 				// 세부직능

	// 대상물정보
	ds_ioOldBuildingInfo.Copy("ds_ioBuildingInfo");		 // 대상물 DS복사
	// 고객정보
	ds_ioOldCustomerInfo.Copy("ds_ioCustomerInfo");		 // 고객정보 DS복사
	// 선임자정보
	ds_ioOldManagerInfo.Copy("ds_ioManagerInfo");		 // 선임자정보 DS복사
}


// 변경내용 적용
function btnApply_OnClick(obj)
{
	// 상단 기초정보
	cbxJibu.Value = ds_ioDanMnfctretcInfo.GetColumn(0, "MRGTMGNO");   // 해당지부 세팅
	cbxJibu_OnChanged(object("cbxJibu"), cbxJibu.Value);			 // 지역콤보
	cbxRegion.Value = ds_ioDanMnfctretcInfo.GetColumn(0, "MRREGCD");   // 해당지역 세팅

	// 세부직능
	// 제조소
	if (ds_ioDanMnfctretcInfo.GetColumn(0, "MNFCTRETC_SE_CODE") == "P06020100") {
		cbxDetailCourse.Value = "31";  // 제조소
	// 저장소
	} else if (ds_ioDanMnfctretcInfo.GetColumn(0, "MNFCTRETC_SE_CODE") == "P06020200") {   
		if (ds_ioDanMnfctretcInfo.GetColumn(0, "MNFCTRETC_DETAIL_SE_CODE") != "1370163") cbxDetailCourse.Value = "35";  // 이동탱크저장소 제외
	// 취급소
	} else if (ds_ioDanMnfctretcInfo.GetColumn(0, "MNFCTRETC_SE_CODE") == "P06020300") {
		if (ds_ioDanMnfctretcInfo.GetColumn(0, "MNFCTRETC_DETAIL_SE_CODE") == "1370155") cbxDetailCourse.Value = "33";			// 이송취급소
		else if (ds_ioDanMnfctretcInfo.GetColumn(0, "MNFCTRETC_DETAIL_SE_CODE") == "1370156") cbxDetailCourse.Value = "32"; 	// 판매취급소
		else if (ds_ioDanMnfctretcInfo.GetColumn(0, "MNFCTRETC_DETAIL_SE_CODE") == "13715001") cbxDetailCourse.Value = "32";	// 주유취급소
		else cbxDetailCourse.Value = "34";  // 일반취급소
	}

    // 대상물 정보
    if(!gfn_IsNull(edFPISBuildingNm.Text)) ds_ioBuildingInfo.SetColumn(0, "BNM", edFPISBuildingNm.Text);
    if(!gfn_IsNull(edFPISAddr1.Text) && edAddr1.Visible == true) ds_ioBuildingInfo.SetColumn(0, "BADDR1", edFPISAddr1.Text);
    if(!gfn_IsNull(edFPISAddr2.Text) && edAddr2.Visible == true) ds_ioBuildingInfo.SetColumn(0, "BADDR2", edFPISAddr2.Text);
    if(!gfn_IsNull(medFPISZipCode.Value) && medZipCode.Visible == true) ds_ioBuildingInfo.SetColumn(0, "BZIPCD", medFPISZipCode.Value);
    if(!gfn_IsNull(EdFPISFireCenter.Text)) ds_ioBuildingInfo.SetColumn(0, "BFIREBONBUCD", ds_ioDanMnfctretcInfo.GetColumn(0, "BONBU_ID"));
    if(!gfn_IsNull(EdFPISFireCenter.Text)) ds_ioBuildingInfo.SetColumn(0, "BFIREUPCD", ds_ioDanMnfctretcInfo.GetColumn(0, "CMPFRSTT_ID"));
    if(!gfn_IsNull(EdFPISFireCenter.Text)) ds_ioBuildingInfo.SetColumn(0, "BFIRECD", ds_ioDanMnfctretcInfo.GetColumn(0, "CMPN119SC_ID"));
    if(!gfn_IsNull(edFPISTel.Text)) ds_ioBuildingInfo.SetColumn(0, "BTEL", edFPISTel.Text);
//    if(!gfn_IsNull(medAreaAfter.Value)) ds_ioBuildingInfo.SetColumn(0, "BAREA", medAreaAfter.Value);

    // 고객 정보
    if (sStatus == "I") {  // 신규이면
		medRegYYMM.Value = left(GetDate(), 6);    // 등록년월
		medFeeYYMM.Value = left(GetDate(), 6);    // 회비부과년월
		ds_ioCustomerInfo.SetColumn(0, "CSTATUSGUBUN", "1");	// 고객상태		

	} else if (sStatus == "U") {  // 수정이면

	}

    // 선임자 정보
//	if(!gfn_IsNull(ds_ioDanManagerInfo.GetColumn(0, "NM"))) ds_ioManagerInfo.SetColumn(0, "FMNM", ds_ioDanManagerInfo.GetColumn(0, "NM"));
//	if(!gfn_IsNull(ds_ioDanManagerInfo.GetColumn(0, "IHIDNUM"))) ds_ioManagerInfo.SetColumn(0, "FMRESIDENTNO", replace(ds_ioDanManagerInfo.GetColumn(0, "IHIDNUM"), "-", ""));
//	if(!gfn_IsNull(ds_ioDanManagerInfo.GetColumn(0, "AP_APNTRLSOFC_DE"))) ds_ioManagerInfo.SetColumn(0, "FMHSTARTDATE", replace(ds_ioDanManagerInfo.GetColumn(0, "AP_APNTRLSOFC_DE"), "-", ""));

    // 위험물 허가정보
    var j = ds_ioDangerInfo.GetRowCount(); // 안전원 위험물 허가정보 개수
	if(!gfn_IsNull(ds_ioDanMnfctretcInfo.GetColumn(0, "PRMISN_NO"))) {
		ds_ioDangerInfo.InsertRow(j);
		ds_ioDangerInfo.SetColumn(j, "DPERMISSIONNO", ds_ioDanMnfctretcInfo.GetColumn(0, "PRMISN_NO"));
		ds_ioDangerInfo.SetColumn(j, "SEL", "1");		
		ds_ioDangerInfo.SetColumn(j, "DSEQ", j+1);
		if(!gfn_IsNull(ds_ioDanMnfctretcInfo.GetColumn(0, "PRMISN_NO"))) ds_ioDangerInfo.SetColumn(j, "DPERMISSIONNO", ds_ioDanMnfctretcInfo.GetColumn(0, "PRMISN_NO"));
		if(!gfn_IsNull(ds_ioDanMnfctretcInfo.GetColumn(0, "ITL_NO"))) ds_ioDangerInfo.SetColumn(j, "DREMARK", "설치(변경)허가번호 : "+ds_ioDanMnfctretcInfo.GetColumn(0, "ITL_NO"));
	}

    lfn_SetEnableStatus(true); // 입력가능 활성화
    lfn_SetCheckStatus(true);  // 변경내용 적용 후 체크박스 상태(체크)

    btnApply.Enable = False;
}


/*
 * 기능 : 변경내용 적용 전/후 활성화 상태를 지정한다.
 * parameter : bStatus - 활성/비활성여부(true:활성, false:비활성)
 * return : 없음
 */
function lfn_SetEnableStatus(bStatus) {
    /* 조회 정보 */
    cbxJibu.Enable = bStatus;
    cbxRegion.Enable = bStatus;
    //cbxCourse.Enable = bStatus;
    cbxDetailCourse.Enable = bStatus;

	/* 대상물 정보 */
    edBNM.Enable = bStatus;
    edAddr1.Enable = bStatus;
    edAddr2.Enable = bStatus;
    btnSearchZipCode1.Enable = bStatus;    
    cbxBonbu.Enable = bStatus;
    cbxStation.Enable = bStatus;
    cbxCenter.Enable = bStatus;
    edTel.Enable = bStatus;

    /* 고객정보 */
    medRegYYMM.Enable = bStatus;
    medFeeYYMM.Enable = bStatus;
    //cbxCustomerStatus.Enable = bStatus;

    /* 선임자정보 */
    edFMNM.Enable = bStatus;
    medFMResidentno.Enable = bStatus;
    calFMStartDate.Enable = bStatus;

    btnSave.Enable = bStatus;
}

// 대표직능 변경
function cbxCourse_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{	
	lfn_AddressHidden(true);			//대표주소 입력 히든처리
	lfn_FilterDetailCourse(strCode);		// 세부고객직능 필터링
}

// 대표주소 입력 히든처리 (위험물운송자)
function lfn_AddressHidden(bStatus) {
	// 히든처리
	// 대상물 주소
    chkAddr.Visible  		 = bStatus;
    edAddr1.Visible          = bStatus;
    medZipCode.Visible       = bStatus;
    btnSearchZipCode1.Visible   = bStatus;
    edAddr2.Visible          = bStatus;


	// 데이터 초기화
	if( gfn_IsNull(ds_ioBuildingInfo.GetColumn(0, "BMGNO")) ) {
		if(bStatus == false) {
			// 대상물 주소
            edAddr1.Text       = "";
            medZipCode.Value   = "";
            edAddr2.Text       = "";
		}
	}
}

// 대상물주소
function btnSearchZipCode1_OnClick(obj)
{
	var arrZipCode = gfn_ZipCodeListPopup(); // 우편번호 조회 팝업
    if (length(arrZipCode) > 0) {
        medZipCode.Value = arrZipCode[0];
        edAddr1.Text = arrZipCode[1];
        edAddr2.SetFocus();
    }
}
// 대상물주소
function btnSearchZipCode1_OnKeyDown(obj,nChar,bShift,bCtrl,bAlt,nLLParam,nHLParam)
{
	if(nChar == 13) {	// Enter key
		btnSearchZipCode1_OnClick();
	} else {
		gfn_ComponentOnKillFocus(obj);
	}
}
// 대상물 주소1
function edNewAddr1_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{
	if (nChar == 13) {
        btnSearchZipCode1.SetFocus();
        var arrZipCode = gfn_ZipCodeListPopup(edNewAddr1.Text); // 우편번호 조회 팝업
        if (length(arrZipCode) > 0) {
            medZipCode.Value = arrZipCode[0];
            edAddr1.Text = arrZipCode[1];
            edAddr2.SetFocus();
        } else {
            edAddr1.SetFocus();
        }
	} else {
		gfn_EnterNextFocus(obj);
	}
}


// 지부 변경
function cbxJibu_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	gfn_SearchCodeRegion(strCode, ds_oRegion, "0");	//지역 코드 조회
	cbxRegion.Index = 0;
} 

/*
 * 세부직능 필터링
 */
function lfn_FilterDetailCourse(sFilterGubun) {
	ds_oDetailCourse.UnFilter();

	var ls_FilterExpr = "";
    if (!gfn_IsNull(sFilterGubun)) {
        ls_FilterExpr += "GCD=='" + trim(sFilterGubun) + "'";
    }

    ds_oDetailCourse.Filter(ls_FilterExpr);

	if( ds_oDetailCourse.FindRow("DATA", cbxDetailCourse.Text) == -1) {
		cbxDetailCourse.Text = "";
    }
}

