/*
 * 기능 : 대상물의 관리번호를 채번
 * parameter : 없음
 * Return : seq - 대상물 관리번호
 */
function lfn_SearchCust0010BuildingSeq(obj) {

	ds_oBuildingSeq.ClearData(); // 데이타셋 초기화

    tit_clearActionInfo();
    tit_addCsvSearchActionInfo("cust:searchCust0010BuildingSeq", false);
    // 서버 호출
    tit_callService(
        ""
        , ""
        , "" // inDs
        , "ds_oBuildingSeq=ds_oBuildingSeq" // outDs
        , ""
        , ""
        ,false
        ,false
        ,true
    );
}

