// 저장
function lfn_Save(obj)
{	
	if(gfn_IsNull(cbxJibu.Value)) {
		gfn_Alert(lbJibu.Text + MSGW00010);			//지부을(를) 입력해 주십시오.
		cbxJibu.SetFocus();
		return;
	}

	if(gfn_IsNull(cbxRegion.Value)) {
		gfn_Alert(chkRegion.Text + MSGW00010);		//지역을(를) 입력해 주십시오.
		cbxRegion.SetFocus();
		return;
	}

	if(gfn_IsNull(cbxDetailCourse.Value)) {
		gfn_Alert(chkDetailCourse.Text + MSGW00010);	//세부직능을(를) 입력해 주십시오.
		cbxDetailCourse.SetFocus();
		return;
	}

	// 대상물 주소
	if(	gfn_IsNull(edAddr1.Text) || gfn_IsNull(edAddr2.Text) || gfn_IsNull(medZipCode.Value) ) {
		//회비
		gfn_Alert("대상물 대표주소는 필수입력항목 입니다.");
		edAddr1.SetFocus();
		return;
	}

	if(gfn_IsNull(medRegYYMM.Value)) {
		gfn_Alert(chkRegYYMM.Text + MSGW00010);	//등록년월을(를) 입력해 주십시오.
		medRegYYMM.SetFocus();
		return;
	}

	if(gfn_IsNull(medFeeYYMM.Value)) {
		gfn_Alert(chkFeeYYMM.Text + MSGW00010);	//회비부과년월을(를) 입력해 주십시오.
		medFeeYYMM.SetFocus();
		return;
	}


	if(gfn_IsNull(edFMNM.Text)) {
		gfn_Alert("선임자"+chkFMNM.Text + MSGW00010);	//선임자 성명을(를) 입력해 주십시오.
		edFMNM.SetFocus();
		return;
	}

	if(gfn_IsNull(medFMResidentno.Value)) {
		gfn_Alert("선임자"+chkFMResidentno.Text + MSGW00010);	//선임자 주민번호을(를) 입력해 주십시오.
		medFMResidentno.SetFocus();
		return;
	}

    if(sStatus == "I") {      // 신규 저장
		if(!gfn_Confirm("신규 등록 하시겠습니까?\n\n 신규등록시에는 체크박스에 상관없이 저장됩니다.")){
			return;
		}
		lfn_updateInfo();
    } else {                    // 기존정보 수정저장
        if (!gfn_Confirm("변경된 정보를 저장하시겠습니까?")) {
            return;
        }
        lfn_updateInfo();   
    } 
}  


/*
 * 기능 : 기본정보 변경 / 신규등록
 */
function lfn_updateInfo()
{   
    /* MAIN */
	var lsBuildStatus = ds_ioBuildingInfo.GetRowType(ds_ioBuildingInfo.currow);
	var lsCustomerStatus = ds_ioCustomerInfo.GetRowType(ds_ioCustomerInfo.currow);
	var lsManagerStatus = ds_ioManagerInfo.GetRowType(ds_ioManagerInfo.currow);

	/* IN : MAIN */
	var iDsNm = "";
	/* OUT: MAIN */		
	var oDsNm = "";	
/*	    iDsNm = "ds_ioBuildingInfo=ds_ioBuildingInfo:U";
        iDsNm += " ds_ioCustomerInfo=ds_ioCustomerInfo:U";
        iDsNm += " ds_iCustomerInfoTmp=ds_ioCustomerInfo:U";
        iDsNm += " ds_ioManagerInfo=ds_ioManagerInfo:U";
	    iDsNm += " ds_ioDangerInfo=ds_ioDangerInfoSave:U";   // IN : 위험물허가정보 
        iDsNm += " ds_ioAdditionInfo=ds_ioAdditionInfo:U";	// IN : 고객 부가정보 
*/        

	var BSEARCHNM = lfn_ReplaceBnm(ds_ioBuildingInfo.GetColumn(0, "BNM"));  // 검색대상물명

	//input Arguments List
	var iArgsNm = "";	
	/* 공통 */
		iArgsNm += "SJIBU=" + quote(cbxJibu.Value);				// 지부
		iArgsNm += " SREGION=" + quote(cbxRegion.Value);		// 지역
		iArgsNm += " SCOURSE=" + quote(cbxDetailCourse.Value);	// 직능	
		ds_ioCustomerInfo.SetColumn(0, "CGTMGNO", cbxJibu.Value);// 지부
		ds_ioCustomerInfo.SetColumn(0, "CREGCD", cbxRegion.Value);// 지역
		ds_ioCustomerInfo.SetColumn(0, "CCOURSECD", cbxDetailCourse.Value);	// 직능			
		iArgsNm += " COURSE=30";								// 대표직능			
		iArgsNm += " SBNM=" + quote(edBNM.Text);				// 대상물명
		iArgsNm += " BSEARCHNM=" + quote(BSEARCHNM);			// 대상물명
///////////////////////////////////////////////////////////////////////////
		//iArgsNm += " SCSTATUS=1";                        		// 회원구분(0:회원,1:비회원)	♣♣♣♣♣♣♣♣♣♣♣♣
		iArgsNm += " BINGUBUN=1";								// 입력구분(0:KEMS 1:청시스템 연계)
		iArgsNm += " CINGUBUN=1";								// 입력구분(0:KEMS 1:청시스템 연계)		
        //iArgsNm += " SBMGNO="  + quote(ds_ioBuildingInfo.GetColumn(ds_ioBuildingInfo.currow, "BMGNO"));	// 대상물관리번호
        //iArgsNm += " SCMGNO="  + quote(ds_ioCustomerInfo.GetColumn(ds_ioCustomerInfo.currow, "CMGNO"));	// 고객관리번호
        //iArgsNm += " SFMMGNO=" + quote(ds_ioManagerInfo.GetColumn(ds_ioManagerInfo.currow, "FMMGNO"));		// 선임자관리번호

	/* UPDATE 일때 사용 */
	var iArgsNmBuildingUpdate = " SBMGNO="  + quote(ds_ioBuildingInfo.GetColumn(ds_ioBuildingInfo.currow, "BMGNO"));	// 대상물관리번호
	var iArgsNmCustomerUpdate = " SCMGNO="  + quote(ds_ioCustomerInfo.GetColumn(ds_ioCustomerInfo.currow, "CMGNO"));	// 고객관리번호
	var iArgsNmManagerUpdate  = " SFMMGNO=" + quote(ds_ioManagerInfo.GetColumn(ds_ioManagerInfo.currow, "FMMGNO"));		// 선임자관리번호


    /* 신규추가일 경우 관리번호 채번 */
    if (lsBuildStatus == "insert" && lsCustomerStatus == "insert" && lsManagerStatus == "insert") {
        lfn_SearchCust0010BuildingSeq();        // 대상물관리번호
        lfn_SearchCust0010CustomerSeq();        // 고객관리번호
    }

    // 대상물 주소 변경시 포함된 고객 주소 일괄변경(주소 구분이 대상물인 경우만)
	if(lsBuildStatus == "update") {
		if( ds_ioBuildingInfo.GetOrgColumn(0, "BADDR1") <> ds_ioBuildingInfo.GetColumn(0, "BADDR1") 
			|| ds_ioBuildingInfo.GetOrgColumn(0, "BADDR2") <> ds_ioBuildingInfo.GetColumn(0, "BADDR2") 
			|| ds_ioBuildingInfo.GetOrgColumn(0, "BZIPCD") <> ds_ioBuildingInfo.GetColumn(0, "BZIPCD") ) {	//대상물 주소가 변경되었을때
			lfn_FindNotOverBizFireManagerList();		
		}
	}

	/* 선임자 변경시 사용 */
	if (lsManagerStatus == "update") {
		// 선임현황 조회 => 회원상태 조회
		lfn_SearchFireManager();	
		// 신규선임자가 firemanagerh에 있는지 검색
		lfn_FireManagerCheck("INFO");    
    }

	tit_clearActionInfo();

	/* 대상물 신규 또는 수정 */
    if(lsBuildStatus == "insert") {
		iArgsNm += " SBMGNO="  + quote(ds_oBuildingSeq.GetColumn(0, "SEQ"));
		iDsNm += "ds_ioBuildingInfo=ds_ioBuildingInfo:U";
        tit_addSingleActionInfo("cust:insertCust0010BuildingInfo", "", 0, "");		//대상물

    } else if(lsBuildStatus == "update") {
        iArgsNm += iArgsNmBuildingUpdate;

/*         // 체크박스가 체크되지 않으면 기존값을 저장
        if (chkBNM.Value == "0") ds_ioBuildingInfo.SetColumn(0, "BNM", ds_ioBuildingInfo.GetOrgColumn(0, "BNM")); 
        if (chkAddr.Value == "0") {
			ds_ioBuildingInfo.SetColumn(0, "BZIPCD", ds_ioBuildingInfo.GetOrgColumn(0, "BZIPCD")); 
			ds_ioBuildingInfo.SetColumn(0, "BADDR1", ds_ioBuildingInfo.GetOrgColumn(0, "BADDR1")); 
			ds_ioBuildingInfo.SetColumn(0, "BADDR2", ds_ioBuildingInfo.GetOrgColumn(0, "BADDR2")); 
			ds_ioBuildingInfo.SetColumn(0, "BADDRGUBUN", ds_ioBuildingInfo.GetOrgColumn(0, "BADDRGUBUN")); 
		}
        if (chkFireCenter.Value == "0") ds_ioBuildingInfo.SetColumn(0, "BFIRECD", ds_ioBuildingInfo.GetOrgColumn(0, "BFIRECD"));
        if (chkTel.Value == "0") ds_ioBuildingInfo.SetColumn(0, "BTEL", ds_ioBuildingInfo.GetOrgColumn(0, "BTEL"));  
*/		
		iDsNm += "ds_ioBuildingInfo=ds_ioBuildingInfo:U";
        tit_addSingleActionInfo("cust:updateCust6400BuildingInfo", "", 0, "");		//대상물        
    } else {
        iArgsNm += iArgsNmBuildingUpdate;   // SBMGNO 대상물관리번호만 가져옴
    }

    /* 고객 신규 또는 수정 */
    if(lsCustomerStatus == "insert") {
		// 신규일 경우 청구지(교육) 세팅
		ds_ioCustomerInfo.SetColumn(0, "CEDUPOSTADDR1", ds_ioCustomerInfo.GetColumn(0, "CFEEPOSTADDR1")); 
		ds_ioCustomerInfo.SetColumn(0, "CEDUPOSTADDR2", ds_ioCustomerInfo.GetColumn(0, "CFEEPOSTADDR2")); 
		ds_ioCustomerInfo.SetColumn(0, "CEDUPOSTGUBUN", ds_ioCustomerInfo.GetColumn(0, "CFEEPOSTGUBUN")); 
		ds_ioCustomerInfo.SetColumn(0, "CEDUPOSTZIPCD", ds_ioCustomerInfo.GetColumn(0, "CFEEPOSTZIPCD"));     

        iArgsNm += " SCMGNO="  + quote(ds_oCustomerSeq.GetColumn(0, "SEQ"));
        iDsNm += " ds_ioCustomerInfo=ds_ioCustomerInfo:U";
        tit_addSingleActionInfo("cust:insertCust0010CustomerInfo", "", 0, "");					//고객

    } else if(lsCustomerStatus == "update") {
        iArgsNm += iArgsNmCustomerUpdate;

        iDsNm += " ds_ioCustomerInfo=ds_ioCustomerInfo:U";
        tit_addSingleActionInfo("cust:updateCust6400CustomerInfo", "", 0, "");		//고객	

    } else {
		iArgsNm += iArgsNmCustomerUpdate;  // SCMGNO 고객정보관리번호만 가져옴

        iDsNm += " ds_ioCustomerInfo=ds_ioCustomerInfo";
        tit_addSingleActionInfo("cust:updateCust6400CustomerInfo", "", 0, "");		//고객			
	}

    /* 선임자 신규 또는 수정 */
    if(lsManagerStatus == "insert") {
        if(left(cbxCourse.Value,1) < 8 && ds_ioManagerInfo.GetColumn(0, "FMRESIDENTERR") == 0) {	//주민번호 정상 & 운송자,교육희망자 제외
            tit_addSingleActionInfo("cust:executeCust0010NewMemberH", "", 0, "");					//MEMBERH	♣♣♣♣♣♣♣♣♣♣♣♣
        }

        iDsNm += " ds_ioManagerInfo=ds_ioManagerInfo:U";
        tit_addSingleActionInfo("cust:executeCust0010CustAppointFM", "", 0, "");				//신규선임

        iArgsNm += " I_CMGNO=" + quote(ds_oCustomerSeq.GetColumn(0, "SEQ"));					// 고객정보관리번호
        iArgsNm += " I_STARTDATE=" + quote(ds_ioManagerInfo.GetColumn(0, "FMHSTARTDATE"));		// 선임일자
        iArgsNm += " I_NEWNAME=" + quote(ds_ioManagerInfo.GetColumn(0, "FMNM"));				// 겸직시작일자
        iArgsNm += " I_NEWRESIDENT=" + quote(ds_ioManagerInfo.GetColumn(0, "FMRESIDENTNO"));	// 주민번호
		iArgsNm += " I_NEWPRNO=" + quote(ds_ioManagerInfo.GetColumn(0, "FMRESIDENTNO"));		// 주번 
    } else if(lsManagerStatus == "update") {
        iArgsNm += iArgsNmManagerUpdate;

/*         // 체크박스가 체크되지 않으면 값을 저장하지 않음
        if (chkFMNM.Value == "0") ds_ioManagerInfo.SetColumn(0, "FMNM", ""); 
        if (chkFMResidentno.Value == "0") ds_ioManagerInfo.SetColumn(0, "FMRESIDENTNO", ""); 
        if (chkFMStartDate.Value == "0") ds_ioManagerInfo.SetColumn(0, "FMHSTARTDATE", ""); 
*/        
        // 신규선임자가 다른대상물에 선임되어있으면 해임처리
        if (chkoldBuildingEndProc.Value == "1") {
			var sEndcnt = 0;
			for (var i=0;i<ds_oManagerList.GetCount();i++) {
				ds_oStatus.Clear();

				oDsNm += " ds_oStatus=ds_oStatus";
				tit_addSingleActionInfo("cust:executeCust0018FireManagerEnd", "", 0, "");

				iArgsNm += " CMGNO=" + quote(ds_oManagerList.GetColumn(i, "CMGNO"));
				iArgsNm += " FMMGNO=" + quote(ds_oManagerList.GetColumn(i, "FMHFMMGNO"));
				iArgsNm += " FMHSEQ=" + quote(ds_oManagerList.GetColumn(i, "FMHSEQ"));
				iArgsNm += " FMHENDGUBUN='0'";
			}        
        }

		var status = "";
		if (!gfn_IsNull(sFmmgnoAfter)) status = "CHANGE";
		else status = "NEW";

        if (status == "NEW" && ds_oMemberStat.GetCount() < 1) {
			iArgsNm += " MNM=" + quote(ds_ioManagerInfo.GetColumn(ds_ioManagerInfo.currow, "FMNM"));//  신규선임자 이름
			iArgsNm += " MRESIDENTNO=" + quote(ds_ioManagerInfo.GetColumn(ds_ioManagerInfo.currow, "FMRESIDENTNO"));// 신규선임자 주민번호
			iArgsNm += " I_MHMEMGUBUN=" + quote(left(ds_ioCustomerInfo.GetColumn(ds_ioCustomerInfo.currow, "CCOURSECD"), 1)+"0");
			iArgsNm += " I_MHMEMCHK=" + quote(ds_ioCustomerInfo.GetColumn(ds_ioCustomerInfo.currow, "CSTATUSGUBUN"));    // 고객 회원상태로 신규선임자 회원상태 처리
			iArgsNm += " I_MHMEMNO=" + quote(ds_ioCustomerInfo.GetColumn(ds_ioCustomerInfo.currow, "CMGNO"));	// 고객관리번호
			iArgsNm += " I_MHCOMPANYNM=" + quote(ds_ioBuildingInfo.GetColumn(ds_ioBuildingInfo.currow, "BNM"));

			oDsNm += " ds_oMemberStatus=ds_oMemberStatus";			
			tit_addSingleActionInfo("cust:executeCust0018MemberH", 0, "");
		}    

        iArgsNm += " SFMHSEQ=" + quote(ds_ioCustomerInfo.GetColumn(ds_ioCustomerInfo.currow, "CFMHSEQ"));    //  기존선임자관리순번
        iArgsNm += " SFMHSTARTDATE=" + quote(ds_ioManagerInfo.GetColumn(ds_ioManagerInfo.currow, "FMHSTARTDATE"));    //  선임일자
        iArgsNm += " SFMNM=" + quote(ds_ioManagerInfo.GetColumn(ds_ioManagerInfo.currow, "FMNM"));//  신규선임자 이름
        iArgsNm += " SFMRESIDENTNO=" + quote(ds_ioManagerInfo.GetColumn(ds_ioManagerInfo.currow, "FMRESIDENTNO"));// 신규선임자 주민번호
        iArgsNm += " SPRNO=" + quote(ds_ioManagerInfo.GetColumn(ds_ioManagerInfo.currow, "FMRESIDENTNO"));// 주번

		iDsNm += " ds_ioManagerInfo=ds_ioManagerInfo:U";
		tit_addSingleActionInfo("cust:executeCust6001FireManagerChange", "", 0, "");  //선임자

    } else {
        iArgsNm += iArgsNmManagerUpdate;	// SFMMGNO 관리자관리번호만 가져옴
    }


    // 대상물 주소 변경시 포함된 고객 주소 일괄변경(주소 구분이 대상물인 경우만)
	if(lsBuildStatus == "update") {
		if( ds_ioBuildingInfo.GetOrgColumn(0, "BADDR1") <> ds_ioBuildingInfo.GetColumn(0, "BADDR1") 
		|| ds_ioBuildingInfo.GetOrgColumn(0, "BADDR2") <> ds_ioBuildingInfo.GetColumn(0, "BADDR2") 
		|| ds_ioBuildingInfo.GetOrgColumn(0, "BZIPCD") <> ds_ioBuildingInfo.GetColumn(0, "BZIPCD") ) {	//대상물 주소가 변경되었을때

			tit_addSingleActionInfo("cust:updateCust0010BuildingAddress", "", 0, "");			

			// 대상물 주소 변경시 주소1,성명,주민번호 가 동일한 경우 겸직이 안된 경우 겸직처리하기
			if(ds_ioNotOverBizFireManager.rowcount > 0) {
				iDsNm  += " ds_ioNotOverBizFireManager=ds_ioNotOverBizFireManager";
				tit_addMultiActionInfo("cust:executeCust0010OverBizProc", "", 0, "", "N");
			}

			// 대상물 주소 변경시 대상물관리번호가 다르나 주소1, 이름, 주민번호가 동일해서 겸직이 된 경우 겸직해임처리
			if(ds_ioIsOverBizFireManager.rowcount > 0) {
				iDsNm  += " ds_ioIsOverBizFireManager=ds_ioIsOverBizFireManager";
				tit_addMultiActionInfo("cust:executeCust0010OverBizFinshProc", "", 0, "", "N");
			}			
		}
	}

    /* 위험물 허가정보 */
    //ds_ioDangerInfo
//    ds_ioDangerInfoSave.Copy(ds_ioDangerInfo);  // 저장하기 위해 dataset을 복사

    ds_ioDangerInfoSave.ClearData();
    var j = 0;
    for(var i=0; i<ds_ioDangerInfo.GetRowCount(); i++) {
		if (ds_ioDangerInfo.GetColumn(i, "SEL") == "1") {   // 선택안된 row는 삭제
			ds_ioDangerInfoSave.InsertRow(i);
			ds_ioDangerInfoSave.SetColumn(j,"DBMGNO", ds_ioDangerInfo.GetColumn(i,"DBMGNO"));
			ds_ioDangerInfoSave.SetColumn(j,"DPERMISSIONNO", ds_ioDangerInfo.GetColumn(i,"DPERMISSIONNO"));
			ds_ioDangerInfoSave.SetColumn(j,"DREMARK", ds_ioDangerInfo.GetColumn(i,"DREMARK"));
			ds_ioDangerInfoSave.SetColumn(j,"DSEQ", ds_ioDangerInfo.GetColumn(i,"DSEQ"));
			j = j + 1;			
		}
	}

	iArgsNm += " DCMNFCTRETC_SN=" + quote(sMNFCTRETC_SN);	
    iDsNm += " ds_ioDangerInfo=ds_ioDangerInfoSave";        	
    tit_addSingleActionInfo("cust:insertCust6101DangerInfo", "", 0, "");		//위험물 허가정보    

	/* 고객 부가정보 */
 	ds_ioAdditionInfo.InsertRow(0);	
	ds_ioAdditionInfo.SetColumn(0, "ADCD", "8");
	ds_ioAdditionInfo.SetColumn(0, "ADCONTENT", "데이터연계 일치화처리(" + left(GetDate(), 4)+"-"+mid(GetDate(),4,2)+"-"+mid(GetDate(),6,2)+")");		

    iDsNm += " ds_ioAdditionInfo=ds_ioAdditionInfo:U";	
    tit_addSingleActionInfo("cust:insertCust0010AdditionInfo", "", 0, "");		//부가정보

	/* 고객변경이력 */
     if(lsCustomerStatus == "insert") {        
        tit_addSingleActionInfo("cust:executeCust0010InsertCustomerChangeH", "", 0, "");
    } else if(lsCustomerStatus == "update") {
        iDsNm += " ds_iCustomerInfoTmp=ds_ioCustomerInfo:U";
        tit_addSingleActionInfo("cust:executeCust0010UpdateCustomerChangeH", "", 0, "");
    }


	////고객정보변경History     CustomerModifyHistory Insert
	if(lsCustomerStatus == "insert") {
		lfn_CustomerModifyHistoryGenerator(ds_oCustomerSeq.GetColumn(0, "SEQ"));	//고객정보변경History
	} else {
		lfn_CustomerModifyHistoryGenerator(ds_ioCustomerInfo.GetColumn(ds_ioCustomerInfo.currow, "CMGNO"));	//고객정보변경History
	}
	if(lsBuildStatus == "update" || lsCustomerStatus == "update" || lsManagerStatus == "update"
		|| ds_ioDangerInfo.GetUpdate() == true || ds_ioLicenseInfo.GetUpdate() == true) {	
		iDsNm  += " ds_iCustomerModifyHistory=ds_iCustomerModifyHistory:U";
		tit_addMultiActionInfo("cust:insertCust0010CustomerModifyHistory", "", 0, "", "N");
	}	

	/* 매치처리 */
	iArgsNm += " MFPIS1=" + quote(sMNFCTRETC_SN);
	iArgsNm += " MNGR_SN=" + quote(sMNGR_SN);
	iArgsNm += " MBIZGUBUN=1";  // 위험물안전관리자
	iArgsNm += " APNTRLSOFC_DE=" + quote(replace(sAPNTRLSOFC_DE, "-", ""));   // 선임일자
	if (sStatus == "U") iArgsNm += " MPROCGUBUN=0";
	else if (sStatus == "I") iArgsNm += " MPROCGUBUN=1";
	tit_addSingleActionInfo("cust:insertCust6101DangerMatchInfo", "", 0, "");            // 매치처리

	// 서버 호출 
	tit_callService(
		""
		,""
		,iDsNm		// inDs
		,iDsNm      // outDs
		,iArgsNm	// args
		,"lfn_AfterSave"
		,false
	);


}

/*
 * 기능 : 저장 후 처리해야 할 내용
 * parameter : ErrorCode - 에러코드
 *             ErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_AfterSave(nErrorCode, strErrorMsg) {    
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }   

    if(sStatus == "I") { 		
		gfn_Alert("신규 저장을 완료하였습니다.");
		Close("I");
    } else {                
        gfn_Alert("변경 저장을 완료하였습니다.");
        Close("U");
    }   
}


// 선임현황 조회
function lfn_SearchFireManager()
{
	if (gfn_IsNull(edFMNM.Text) && gfn_IsNull(medFMResidentno.Value)) {
        gfn_Alert("변경정보의 이름이나 주민번호 중 하나는 입력해 주십시오.");
        edFMNM.SetFocus();
        return;
    }

	ds_oManagerList.ClearData(); // 데이타셋 초기화
    tit_clearActionInfo();
	tit_addSearchActionInfo("cust:searchCust6001ManagerList", false);
	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_oManagerList=ds_oManagerList"	// outDs
		,"FMNM=" + quote(edFMNM.Text)
		 + " FMRESIDENTNO=" + quote(medFMResidentno.Value)
		 + " CMGNO=" + quote(sCMGNO)
		 + " CHK=0"
		,"lfn_AfterSearchFireManager"
		,false
		,false
		,true
	);
}

// 선임현황 조회후 처리
function lfn_AfterSearchFireManager(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }
    lfn_SearchMemberStat();
    //gfn_SearchMessage(gdManagerList); // 조회 메세지를 처리하고 그리드의 정렬 헤드 마크를 제거한다.
}


// 회원상태 조회
function lfn_SearchMemberStat()
{    
	ds_oMemberStat.ClearData(); // 데이타셋 초기화
    tit_clearActionInfo();
	tit_addSearchActionInfo("cust:searchCust0018MemberStat", false);
	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_oMemberStat=ds_oMemberStat"	// outDs
		,"MHNAME=" + quote(edFMNM.Text)
		 + " MHRESIDENT=" + quote(medFMResidentno.Value)
		,"lfn_AfterSearchMemberStat"
		,false
		,false
		,true
	);
}

// 회원상태 조회후 처리
function lfn_AfterSearchMemberStat(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }
    //trace(ds_oMemberStat.SaveXML());
    if (ds_oMemberStat.GetCount() > 0) {
        ds_MemberGubun.Filter("CD<>''");
        //radMember.Width = 150;
    } else {        
        ds_MemberGubun.UnFilter();
        //radMember.Width = 228;
    }
    //radMember.Value = ds_oMemberStat.GetColumn(0, "MHMEMCHK");
}


/**
 * 선임자 검색
 */
function lfn_FireManagerCheck(gubun){

    var sArg = "";

    if (gubun == "MGNO") {   // 선임자 조회
        sArg = "MMGNO=" + quote(sCMGNO);   // 고객관리번호를 arg로 넘김
    } else {
        sArg = "MNM=" + quote(edFMNM.Text)
            + " MRESIDENTNO=" + quote(medFMResidentno.Value);
    }

    ds_ioManager.ClearData();
    tit_clearActionInfo(); //사람이 이미 있는지 검색한다.
    tit_addCsvSearchActionInfo("cust:searchCust0018ManagerInfo", false);
    // 서버 호출 
    tit_callService(
        ""
        ,""
        ,""	// inDs
        ,"ds_ioManager=ds_oManager"	// outDs
        ,sArg // args
        ,""
		,false
		,false
		,true
    );

    if (ds_ioManager.GetRowCount() == 1) {
        sFmmgnoAfter = ds_ioManager.GetColumn(0, "MMGNO");
    } else {
        sFmmgnoAfter = "";
    }
}

 /*
 * 기능 : 검색을 위한 대상물명 변환
 * parameter : 대상물명
 * return : 대상물명(BSEARCHNM)
 */
function lfn_ReplaceBnm(sBnm) {
    var sBSEARCHNM = sBnm;
    ds_oBnmCode.ClearData();

    tit_clearActionInfo();
    tit_addCsvSearchActionInfo("cust:searchCust0010BnmReplaceCode", false);
    // 서버 호출
    tit_callService(
        ""

        , ""
        , "" // inDs
        , "ds_oBnmCode=ds_oBnmCode" // outDs
        , ""
        , ""
        ,false
        ,false
        ,true
    );

    for (i=0;i<ds_oBnmCode.GetCount();i++) {
        sBSEARCHNM = Replace(sBSEARCHNM, ds_oBnmCode.GetColumn(i, "CCCDNM"), "");
    }

    return sBSEARCHNM;
}


function lfn_FindNotOverBizFireManagerList(obj) {
	//대상물 주소 변경시 주소1,성명,주민번호 가 동일한 경우 겸직이 안된 경우 겸직처리하기
	ds_ioNotOverBizFireManager.ClearData();

	//대상물 주소 변경시 대상물관리번호가 다르나 주소1, 이름, 주민번호가 동일해서 겸직이 된 경우 겸직해임처리
	ds_ioIsOverBizFireManager.ClearData();

	tit_clearActionInfo();
	tit_addSearchActionInfo("cust:searchCust0010NotOverBizFireManager", false);
	tit_addSearchActionInfo("cust:searchCust0010IsOverBizFireManager", false);
	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_ioNotOverBizFireManager=ds_ioNotOverBizFireManager ds_ioIsOverBizFireManager=ds_ioIsOverBizFireManager"	// outDs
		,"NEWBADDR1=" + quote(ds_ioBuildingInfo.GetColumn(0, "BADDR1"))	// 변경된 대상물 주소1
		+ " OLDBADDR1=" + quote(ds_ioBuildingInfo.GetOrgColumn(0, "BADDR1"))	// 변경전 대상물 주소1
		+ " OLDBMGNO=" + quote(ds_ioBuildingInfo.GetColumn(0, "BMGNO"))	// 변경전 대상물 관리번호
		,""
		,true
		,true
		,true
	);
}


/*  	
 * 기능 : 고객 변경 체크
 * parameter : 없음
 * Return : cFlag(상태변경구분)
 *      0 : 변경없음
 *      1 : 비회원		-> 회원
 *      2 : 회원		-> 비회원
 */
 // 안씀 나중에 회원상태 변경 고려
function lfn_CustomerStatusChanged() {
	var cFlag = 0;
	//고객상태 변경
	if(ds_ioCustomerInfo.GetOrgColumn(0, "CSTATUSGUBUN") <> ds_ioCustomerInfo.GetColumn(0, "CSTATUSGUBUN")) {	//회원구분 변경
		if(ds_ioCustomerInfo.GetColumn(0, "CSTATUSGUBUN") == 0) {	//비회원 -> 회원
			cFlag = 1;
		} else if(ds_ioCustomerInfo.GetColumn(0, "CSTATUSGUBUN") == 1) {	//회원 -> 비회원
			cFlag = 2;
		}
	}
	return(cFlag);
} 

// 그리드 헤드전체 체크
function grid_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)

{

	var lobj_Dataset = object(obj.BindDataset);

	if (nCell == 0) { // 선택

        if (obj.GetCellProp("head", nCell, "text") == 1) { // 전체가 이미 선택된 경우

            obj.SetCellProp("head", nCell, "text", 0);

            for(i = 0; i < lobj_Dataset.RowCount(); i++) {

                lobj_Dataset.SetColumn(i, "SEL", "0");

            }

        } else { // 전체가 이미 선택되지 않은 경우

            obj.SetCellProp("head", nCell, "text", 1);

            for(i = 0; i < lobj_Dataset.RowCount(); i++) {

                lobj_Dataset.SetColumn(i, "SEL", "1");

            }

        }

	} else { // 헤드 정렬

        lfn_SortGrid_OnHeadClick(obj,nCell,nX,nY,nPivotIndex);

	}

}

// 그리드 바디 체크박스에서 한Row만 체크되도록
function grid_OnlyOneClick(obj,nCell,nX,nY,nPivotIndex)

{

	var lobj_Dataset = object(obj.BindDataset);

	if (obj.GetCellProp("body", nCell, "text") == 1) { // 전체가 이미 선택된 경우

		for(i = 0; i < lobj_Dataset.RowCount(); i++) {
			if (i <> nCell) {
				lobj_Dataset.SetColumn(i, "SEL", "1");
			}
		}

	} else { // 전체가 이미 선택되지 않은 경우


		for(i = 0; i < lobj_Dataset.RowCount(); i++) {
			if (i <> nCell) {
				lobj_Dataset.SetColumn(i, "SEL", "0");
			}

		}

	}

}


// 대상물정보 체크박스 클릭시
function chkBuilding_OnClick(obj,strValue)
{
	// 대상물
	if (chkBuilding.Value) {
		chkBNM.Value = true;
		chkAddr.Value = true;
		chkFireCenter.Value = true;
		chkTel.Value = true;
	} else {
		chkBNM.Value = false;
		chkAddr.Value = false;
		chkFireCenter.Value = false;
		chkTel.Value = false;
	}
	chkBNM_OnClick();
	chkAddr_OnClick();
	chkFireCenter_OnClick();
	chkTel_OnClick();
}

// 고객정보 체크박스 클릭시
function chkCustomer_OnClick(obj,strValue)
{
	if (chkCustomer.Value) {
		chkRegYYMM.Value = true;
		chkFeeYYMM.Value = true;
	} else {
		chkRegYYMM.Value = false;
		chkFeeYYMM.Value = false;
	}
}

// 선임자정보 체크박스 클릭시
function chkFireManager_OnClick(obj,strValue)
{
	if (chkFireManager.Value) {
		chkFMNM.Value = true;
		chkFMResidentno.Value = true;
		chkFMStartDate.Value = true;
		if (chkoldBuildingEndProc.Visible) {
			chkoldBuildingEndProc.Value = true;  //기존 선임대상물 해임처리
		}		
   } else {
		chkFMNM.Value = false;
		chkFMResidentno.Value = false;
		chkFMStartDate.Value = false;
		if (chkoldBuildingEndProc.Visible) {
			chkoldBuildingEndProc.Value = false;  //기존 선임대상물 해임처리
		}		
   }
   chkFMNM_OnClick();
   chkFMResidentno_OnClick();
   chkFMStartDate_OnClick();
}

/*
 * 기능 : Grid의 checkbox를 한꺼번에 check/uncheck하는 기능
 * parameter : grid - Grid
 *                ds - Dataset
 * return value : 없음
 */
function lfn_GridCheckAll(objGrid,nHeadCell,nBodyCell,bEvent) {
    if (gfn_IsNull(objGrid.BindDataset) == true) {
        return ;
    }

    var strColID = objGrid.GetCellProp("body", nBodyCell, "Colid");

    if (gfn_IsNull(strColID) == true) {
        gfn_Alert("Colid is Null");
        return ;
    }

    var objDs = object(objGrid.BindDataset);

    // Check Box 전체 선택
    var nCount = objDs.GetRowCount();

    var strChk = decode(objGrid.GetCellProp("head", nHeadCell, "Text"), "1", "0", "1");

    objGrid.ReDraw = false;

    if (bEvent == true)
        objDs.FireEvent = false;

    for (var i=0; i<objDs.GetRowCount(); i++) {
        objDs.SetColumn(i, strColID, strChk);
    }

    if (bEvent == true) {
        objDs.FireEvent = true;
    }

    objGrid.SetCellProp("head", nHeadCell, "Text", strChk);

    objGrid.ReDraw = true;
}


