		if (chkAddr.Value == "1") {
			ds_ioBuildingInfo.SetColumn(0, "BADDR1", edFPISAddr1.Text);
			ds_ioBuildingInfo.SetColumn(0, "BADDR2", edFPISAddr2.Text);
			ds_ioBuildingInfo.SetColumn(0, "BZIPCD", medFPISZipCode.Value);
		} else {
			ds_ioBuildingInfo.SetColumn(0, "BADDR1", ds_ioOldBuildingInfo.GetColumn(0, "BADDR1"));
			ds_ioBuildingInfo.SetColumn(0, "BADDR2", ds_ioOldBuildingInfo.GetColumn(0, "BADDR2"));
			ds_ioBuildingInfo.SetColumn(0, "BZIPCD", ds_ioOldBuildingInfo.GetColumn(0, "BZIPCD"));
		}
	}	
}

