// 고객변경이력생성
function lfn_CustomerModifyHistoryGenerator(sCmgno) {

	if(gfn_IsNull(sCmgno)) {
		gfn_Alert("고객정보 변경이력 처리에 실패하였습니다.");
		//return;
	}

	ds_iCustomerModifyHistory.ClearData();

	//대상물정보
	if( ds_ioBuildingInfo.GetRowType(0) == "insert" ) {	//대상물정보 신규

	} else if( ds_ioBuildingInfo.GetRowType(0) == "update" ) {	//대상물정보 수정

		for(var i=0; i<ds_ioBuildingInfo.GetColCount(); i++) {

			if( ds_ioBuildingInfo.GetOrgColumn(0, ds_ioBuildingInfo.GetColID(i))
				<> ds_ioBuildingInfo.GetColumn(0, ds_ioBuildingInfo.GetColID(i)) ) {	//컬럼값 변경

				var nRow = ds_iCustomerModifyHistory.AddRow();

				ds_iCustomerModifyHistory.SetColumn(nRow, "CMHGUBUN", "U");
				ds_iCustomerModifyHistory.SetColumn(nRow, "CMHWORKGUBUN", "대상물정보");
				ds_iCustomerModifyHistory.SetColumn(nRow, "CMHKEY", sCmgno);
				ds_iCustomerModifyHistory.SetColumn(nRow, "CMHCOLUMN", ds_oModifyColumnList.GetColumn( ds_oModifyColumnList.FindRow("COLNM", ds_ioBuildingInfo.GetColID(i)) , "COLKORNM"));

				var lsComboYn = "N";
				var lsInnerDsNm = "";
				if( ds_oModifyColumnList.GetColumn( ds_oModifyColumnList.FindRow("COLNM", ds_ioBuildingInfo.GetColID(i)) , "COMBOGUBUN" ) == "Y" ) {	//Combo Component Check
					lsComboYn = "Y";
					lsInnerDsNm = ds_oModifyColumnList.GetColumn( ds_oModifyColumnList.FindRow("COLNM", ds_ioBuildingInfo.GetColID(i)) , "INNERDSNM" );	//Inner Dataset Name
				}

				if(lsComboYn == "Y") {	//Combo Component 인 경우
					//BEFOREDATA
					if(!gfn_IsNull((ds_ioBuildingInfo.GetOrgColumn(0, ds_ioBuildingInfo.GetColID(i))))) {
						var lsOrgCd = ds_ioBuildingInfo.GetOrgColumn(0, ds_ioBuildingInfo.GetColID(i));
						if(ds_ioBuildingInfo.GetColID(i) == "BFIREUPCD" || ds_ioBuildingInfo.GetColID(i) == "BFIRECD") {	//소방관서 (필터링된 데이터셋까지 처리)
							ds_iCustomerModifyHistory.SetColumn(nRow, "CMHBEFOREDATA", Object(lsInnerDsNm).GetColumnNF(Object(lsInnerDsNm).FindRowNF("CD", lsOrgCd), "DATA"));
						} else {
							ds_iCustomerModifyHistory.SetColumn(nRow, "CMHBEFOREDATA", Object(lsInnerDsNm).GetColumn(Object(lsInnerDsNm).FindRow("CD", lsOrgCd), "DATA"));
						}
					} else {
						ds_iCustomerModifyHistory.SetColumn(nRow, "CMHBEFOREDATA", "$1");
					}
					//AFTERDATA
					if(!gfn_IsNull((ds_ioBuildingInfo.GetColumn(0, ds_ioBuildingInfo.GetColID(i))))) {
						var lsNewCd = ds_ioBuildingInfo.GetColumn(0, ds_ioBuildingInfo.GetColID(i));
						if(ds_ioBuildingInfo.GetColID(i) == "BFIREUPCD" || ds_ioBuildingInfo.GetColID(i) == "BFIRECD") {	//소방관서 (필터링된 데이터셋까지 처리)
							ds_iCustomerModifyHistory.SetColumn(nRow, "CMHAFTERDATA", Object(lsInnerDsNm).GetColumnNF(Object(lsInnerDsNm).FindRowNF("CD", lsNewCd), "DATA"));
						} else {
							ds_iCustomerModifyHistory.SetColumn(nRow, "CMHAFTERDATA", Object(lsInnerDsNm).GetColumn(Object(lsInnerDsNm).FindRow("CD", lsNewCd), "DATA"));
						}
					} else {
						ds_iCustomerModifyHistory.SetColumn(nRow, "CMHAFTERDATA", "$1");
					}
				} else {	//Combo Component 가 아닌경우
					//BEFOREDATA
					if(!gfn_IsNull((ds_ioBuildingInfo.GetOrgColumn(0, ds_ioBuildingInfo.GetColID(i))))) {
						ds_iCustomerModifyHistory.SetColumn(nRow, "CMHBEFOREDATA", ds_ioBuildingInfo.GetOrgColumn(0, ds_ioBuildingInfo.GetColID(i)));
					} else {
						ds_iCustomerModifyHistory.SetColumn(nRow, "CMHBEFOREDATA", "$1");
					}
					//AFTERDATA
					if(!gfn_IsNull((ds_ioBuildingInfo.GetColumn(0, ds_ioBuildingInfo.GetColID(i))))) {
						ds_iCustomerModifyHistory.SetColumn(nRow, "CMHAFTERDATA", ds_ioBuildingInfo.GetColumn(0, ds_ioBuildingInfo.GetColID(i)));
					} else {
						ds_iCustomerModifyHistory.SetColumn(nRow, "CMHAFTERDATA", "$1");
					}
				}
			}
		}
	}

	//고객정보
	if( ds_ioCustomerInfo.GetRowType(0) == "insert" ) {	//고객정보 신규

	} else if( ds_ioCustomerInfo.GetRowType(0) == "update" ) {	//고객정보 수정

		for(var i=0; i<ds_ioCustomerInfo.GetColCount(); i++) {

			if( ds_ioCustomerInfo.GetColID(i) == "CREGCD" ) {	//지역변경
				if( ds_ioCustomerInfo.GetOrgColumn(0, "CREGCD") <> cbxRegion.Value ) {	
					var nRow = ds_iCustomerModifyHistory.AddRow();

					ds_iCustomerModifyHistory.SetColumn(nRow, "CMHGUBUN", "U");
					ds_iCustomerModifyHistory.SetColumn(nRow, "CMHWORKGUBUN", "고객정보");
					ds_iCustomerModifyHistory.SetColumn(nRow, "CMHKEY", sCmgno);
					ds_iCustomerModifyHistory.SetColumn(nRow, "CMHCOLUMN", ds_oModifyColumnList.GetColumn( ds_oModifyColumnList.FindRow("COLNM", "CREGCD") , "COLKORNM"));
					ds_iCustomerModifyHistory.SetColumn(nRow, "CMHBEFOREDATA", ds_oRegion.GetColumn( ds_oRegion.FindRow("CD", ds_ioCustomerInfo.GetOrgColumn(0, "CREGCD")), "DATA" ) );
					ds_iCustomerModifyHistory.SetColumn(nRow, "CMHAFTERDATA", cbxRegion.Text);
				}
			} else if( ds_ioCustomerInfo.GetColID(i) == "CCOURSECD" ) {	//직능변경
				if( ds_ioCustomerInfo.GetOrgColumn(0, "CCOURSECD") <> cbxDetailCourse.Value ) {	
					var nRow = ds_iCustomerModifyHistory.AddRow();

					ds_iCustomerModifyHistory.SetColumn(nRow, "CMHGUBUN", "U");
					ds_iCustomerModifyHistory.SetColumn(nRow, "CMHWORKGUBUN", "고객정보");
					ds_iCustomerModifyHistory.SetColumn(nRow, "CMHKEY", sCmgno);
					ds_iCustomerModifyHistory.SetColumn(nRow, "CMHCOLUMN", ds_oModifyColumnList.GetColumn( ds_oModifyColumnList.FindRow("COLNM", "CCOURSECD") , "COLKORNM"));
					ds_iCustomerModifyHistory.SetColumn(nRow, "CMHBEFOREDATA", ds_oDetailCourse.GetColumn( ds_oDetailCourse.FindRow("CD", ds_ioCustomerInfo.GetOrgColumn(0, "CCOURSECD")), "DATA" ) );
					ds_iCustomerModifyHistory.SetColumn(nRow, "CMHAFTERDATA", cbxDetailCourse.Text);
				}

			} else if ( ds_ioCustomerInfo.GetColID(i) == "CGCOURSECD" 
					|| ds_ioCustomerInfo.GetColID(i) == "AGNM" ) {	//제외 대상

				//대표직능 : CUSTOMER TABLE에 물리적으로 미존재
				//대행업체명 : CUSTOMER TABLE에 물리적으로 미존재

			} else {	//지역, 직능을 제외한 변경
				if( ds_ioCustomerInfo.GetOrgColumn(0, ds_ioCustomerInfo.GetColID(i))
					<> ds_ioCustomerInfo.GetColumn(0, ds_ioCustomerInfo.GetColID(i)) ) {	//컬럼값 변경

					var nRow = ds_iCustomerModifyHistory.AddRow();

					ds_iCustomerModifyHistory.SetColumn(nRow, "CMHGUBUN", "U");
					ds_iCustomerModifyHistory.SetColumn(nRow, "CMHWORKGUBUN", "고객정보");
					ds_iCustomerModifyHistory.SetColumn(nRow, "CMHKEY", sCmgno);
					ds_iCustomerModifyHistory.SetColumn(nRow, "CMHCOLUMN", ds_oModifyColumnList.GetColumn( ds_oModifyColumnList.FindRow("COLNM", ds_ioCustomerInfo.GetColID(i)) , "COLKORNM"));

					var lsComboYn = "N";
					var lsInnerDsNm = "";
					if( ds_oModifyColumnList.GetColumn( ds_oModifyColumnList.FindRow("COLNM", ds_ioCustomerInfo.GetColID(i)) , "COMBOGUBUN" ) == "Y" ) {	//Combo Component Check
						lsComboYn = "Y";
						lsInnerDsNm = ds_oModifyColumnList.GetColumn( ds_oModifyColumnList.FindRow("COLNM", ds_ioCustomerInfo.GetColID(i)) , "INNERDSNM" );	//Inner Dataset Name
					}

					if(lsComboYn == "Y") {	//Combo Component 인 경우
						//BEFOREDATA
						if(!gfn_IsNull((ds_ioCustomerInfo.GetOrgColumn(0, ds_ioCustomerInfo.GetColID(i))))) {
							var lsOrgCd = ds_ioCustomerInfo.GetOrgColumn(0, ds_ioCustomerInfo.GetColID(i));
							ds_iCustomerModifyHistory.SetColumn(nRow, "CMHBEFOREDATA", Object(lsInnerDsNm).GetColumn(Object(lsInnerDsNm).FindRow("CD", lsOrgCd), "DATA"));
						} else {
							ds_iCustomerModifyHistory.SetColumn(nRow, "CMHBEFOREDATA", "$1");
						}
						//AFTERDATA
						if(!gfn_IsNull((ds_ioCustomerInfo.GetColumn(0, ds_ioCustomerInfo.GetColID(i))))) {
							var lsNewCd = ds_ioCustomerInfo.GetColumn(0, ds_ioCustomerInfo.GetColID(i));
							ds_iCustomerModifyHistory.SetColumn(nRow, "CMHAFTERDATA", Object(lsInnerDsNm).GetColumn(Object(lsInnerDsNm).FindRow("CD", lsNewCd), "DATA"));
						} else {
							ds_iCustomerModifyHistory.SetColumn(nRow, "CMHAFTERDATA", "$1");
						}
					} else {	//Combo Component 가 아닌경우
						//BEFOREDATA
						if(!gfn_IsNull((ds_ioCustomerInfo.GetOrgColumn(0, ds_ioCustomerInfo.GetColID(i))))) {
							ds_iCustomerModifyHistory.SetColumn(nRow, "CMHBEFOREDATA", ds_ioCustomerInfo.GetOrgColumn(0, ds_ioCustomerInfo.GetColID(i)));
						} else {
							ds_iCustomerModifyHistory.SetColumn(nRow, "CMHBEFOREDATA", "$1");
						}
						//AFTERDATA
						if(!gfn_IsNull((ds_ioCustomerInfo.GetColumn(0, ds_ioCustomerInfo.GetColID(i))))) {
							ds_iCustomerModifyHistory.SetColumn(nRow, "CMHAFTERDATA", ds_ioCustomerInfo.GetColumn(0, ds_ioCustomerInfo.GetColID(i)));
						} else {
							ds_iCustomerModifyHistory.SetColumn(nRow, "CMHAFTERDATA", "$1");
						}
					}
				}
			}
		}
	}

	//선임자정보

	if( ds_ioManagerInfo.GetRowType(0) == "insert" ) {	//선임자정보 신규

	} else if( ds_ioManagerInfo.GetRowType(0) == "update" ) {	//선임자정보 수정

		for(var i=0; i<ds_ioManagerInfo.GetColCount(); i++) {
			if( ds_ioManagerInfo.GetOrgColumn(0, ds_ioManagerInfo.GetColID(i))
				<> ds_ioManagerInfo.GetColumn(0, ds_ioManagerInfo.GetColID(i)) ) {	//컬럼값 변경

				var nRow = ds_iCustomerModifyHistory.AddRow();

				ds_iCustomerModifyHistory.SetColumn(nRow, "CMHGUBUN", "U");
				ds_iCustomerModifyHistory.SetColumn(nRow, "CMHWORKGUBUN", "선임자정보");
				ds_iCustomerModifyHistory.SetColumn(nRow, "CMHKEY", sCmgno);
				ds_iCustomerModifyHistory.SetColumn(nRow, "CMHCOLUMN", ds_oModifyColumnList.GetColumn( ds_oModifyColumnList.FindRow("COLNM", ds_ioManagerInfo.GetColID(i)) , "COLKORNM"));

				var lsComboYn = "N";
				var lsInnerDsNm = "";
				if( ds_oModifyColumnList.GetColumn( ds_oModifyColumnList.FindRow("COLNM", ds_ioManagerInfo.GetColID(i)) , "COMBOGUBUN" ) == "Y" ) {	//Combo Component Check
					lsComboYn = "Y";
					lsInnerDsNm = ds_oModifyColumnList.GetColumn( ds_oModifyColumnList.FindRow("COLNM", ds_ioManagerInfo.GetColID(i)) , "INNERDSNM" );	//Inner Dataset Name
				}

				if(lsComboYn == "Y") {	//Combo Component 인 경우
					//BEFOREDATA
					if(!gfn_IsNull((ds_ioManagerInfo.GetOrgColumn(0, ds_ioManagerInfo.GetColID(i))))) {
						var lsOrgCd = ds_ioManagerInfo.GetOrgColumn(0, ds_ioManagerInfo.GetColID(i));
						ds_iCustomerModifyHistory.SetColumn(nRow, "CMHBEFOREDATA", Object(lsInnerDsNm).GetColumn(Object(lsInnerDsNm).FindRow("CD", lsOrgCd), "DATA"));
					} else {
						ds_iCustomerModifyHistory.SetColumn(nRow, "CMHBEFOREDATA", "$1");
					}
					//AFTERDATA
					if(!gfn_IsNull((ds_ioManagerInfo.GetColumn(0, ds_ioManagerInfo.GetColID(i))))) {
						var lsNewCd = ds_ioManagerInfo.GetColumn(0, ds_ioManagerInfo.GetColID(i));
						ds_iCustomerModifyHistory.SetColumn(nRow, "CMHAFTERDATA", Object(lsInnerDsNm).GetColumn(Object(lsInnerDsNm).FindRow("CD", lsNewCd), "DATA"));
					} else {
						ds_iCustomerModifyHistory.SetColumn(nRow, "CMHAFTERDATA", "$1");
					}
				} else {	//Combo Component 가 아닌경우
					//BEFOREDATA
					if(!gfn_IsNull((ds_ioManagerInfo.GetOrgColumn(0, ds_ioManagerInfo.GetColID(i))))) {
						ds_iCustomerModifyHistory.SetColumn(nRow, "CMHBEFOREDATA", ds_ioManagerInfo.GetOrgColumn(0, ds_ioManagerInfo.GetColID(i)));
					} else {
						ds_iCustomerModifyHistory.SetColumn(nRow, "CMHBEFOREDATA", "$1");
					}
					//AFTERDATA
					if(!gfn_IsNull((ds_ioManagerInfo.GetColumn(0, ds_ioManagerInfo.GetColID(i))))) {
						ds_iCustomerModifyHistory.SetColumn(nRow, "CMHAFTERDATA", ds_ioManagerInfo.GetColumn(0, ds_ioManagerInfo.GetColID(i)));
					} else {
						ds_iCustomerModifyHistory.SetColumn(nRow, "CMHAFTERDATA", "$1");
					}
				}
			}
		}
	}

	//관계자정보
	for( var j=0; j<ds_ioOwnerInfo.GetRowCount(); j++) {

		if( ds_ioOwnerInfo.GetRowType(j) == "insert" ) {	//관계자정보 신규

			var nRow = ds_iCustomerModifyHistory.AddRow();
			ds_iCustomerModifyHistory.SetColumn(nRow, "CMHGUBUN", "I");
			ds_iCustomerModifyHistory.SetColumn(nRow, "CMHWORKGUBUN", "관계자정보");
			ds_iCustomerModifyHistory.SetColumn(nRow, "CMHKEY", sCmgno);
			ds_iCustomerModifyHistory.SetColumn(nRow, "CMHCOLUMN", "신규등록");
			ds_iCustomerModifyHistory.SetColumn(nRow, "CMHBEFOREDATA", "$1");
			ds_iCustomerModifyHistory.SetColumn(nRow, "CMHAFTERDATA", ds_ioOwnerInfo.GetColumn(j, "ONM"));

		} else if( ds_ioOwnerInfo.GetRowType(j) == "update" ) {	//관계자정보 수정

			for(var i=0; i<ds_ioOwnerInfo.GetColCount(); i++) {
				if( ds_ioOwnerInfo.GetOrgColumn(j, ds_ioOwnerInfo.GetColID(i))
					<> ds_ioOwnerInfo.GetColumn(j, ds_ioOwnerInfo.GetColID(i)) ) {	//컬럼값 변경

					var nRow = ds_iCustomerModifyHistory.AddRow();

					ds_iCustomerModifyHistory.SetColumn(nRow, "CMHGUBUN", "U");
					ds_iCustomerModifyHistory.SetColumn(nRow, "CMHWORKGUBUN", "관계자정보");
					ds_iCustomerModifyHistory.SetColumn(nRow, "CMHKEY", sCmgno);
					ds_iCustomerModifyHistory.SetColumn(nRow, "CMHCOLUMN", ds_oModifyColumnList.GetColumn( ds_oModifyColumnList.FindRow("COLNM", ds_ioOwnerInfo.GetColID(i)) , "COLKORNM"));

					var lsComboYn = "N";
					var lsInnerDsNm = "";
					if( ds_oModifyColumnList.GetColumn( ds_oModifyColumnList.FindRow("COLNM", ds_ioOwnerInfo.GetColID(i)) , "COMBOGUBUN" ) == "Y" ) {	//Combo Component Check
						lsComboYn = "Y";
						lsInnerDsNm = ds_oModifyColumnList.GetColumn( ds_oModifyColumnList.FindRow("COLNM", ds_ioOwnerInfo.GetColID(i)) , "INNERDSNM" );	//Inner Dataset Name
					}

					if(lsComboYn == "Y") {	//Combo Component 인 경우
						//BEFOREDATA
						if(!gfn_IsNull((ds_ioOwnerInfo.GetOrgColumn(j, ds_ioOwnerInfo.GetColID(i))))) {
							var lsOrgCd = ds_ioOwnerInfo.GetOrgColumn(j, ds_ioOwnerInfo.GetColID(i));
							ds_iCustomerModifyHistory.SetColumn(nRow, "CMHBEFOREDATA", Object(lsInnerDsNm).GetColumn(Object(lsInnerDsNm).FindRow("CD", lsOrgCd), "DATA"));
						} else {
							ds_iCustomerModifyHistory.SetColumn(nRow, "CMHBEFOREDATA", "$1");
						}
						//AFTERDATA
						if(!gfn_IsNull((ds_ioOwnerInfo.GetColumn(j, ds_ioOwnerInfo.GetColID(i))))) {
							var lsNewCd = ds_ioOwnerInfo.GetColumn(j, ds_ioOwnerInfo.GetColID(i));
							ds_iCustomerModifyHistory.SetColumn(nRow, "CMHAFTERDATA", Object(lsInnerDsNm).GetColumn(Object(lsInnerDsNm).FindRow("CD", lsNewCd), "DATA"));
						} else {
							ds_iCustomerModifyHistory.SetColumn(nRow, "CMHAFTERDATA", "$1");
						}
					} else {	//Combo Component 가 아닌경우
						//BEFOREDATA
						if(!gfn_IsNull((ds_ioOwnerInfo.GetOrgColumn(j, ds_ioOwnerInfo.GetColID(i))))) {
							ds_iCustomerModifyHistory.SetColumn(nRow, "CMHBEFOREDATA", ds_ioOwnerInfo.GetOrgColumn(j, ds_ioOwnerInfo.GetColID(i)));
						} else {
							ds_iCustomerModifyHistory.SetColumn(nRow, "CMHBEFOREDATA", "$1");
						}
						//AFTERDATA
						if(!gfn_IsNull((ds_ioOwnerInfo.GetColumn(j, ds_ioOwnerInfo.GetColID(i))))) {
							ds_iCustomerModifyHistory.SetColumn(nRow, "CMHAFTERDATA", ds_ioOwnerInfo.GetColumn(j, ds_ioOwnerInfo.GetColID(i)));
						} else {
							ds_iCustomerModifyHistory.SetColumn(nRow, "CMHAFTERDATA", "$1");
						}
					}
				}
			}
		}

	}

	//위험물부가정보
	for( var j=0; j<ds_ioDangerInfo.GetRowCount(); j++) {

		if( ds_ioDangerInfo.GetRowType(j) == "insert" ) {	//위험물부가정보 신규

			var nRow = ds_iCustomerModifyHistory.AddRow();
			ds_iCustomerModifyHistory.SetColumn(nRow, "CMHGUBUN", "I");
			ds_iCustomerModifyHistory.SetColumn(nRow, "CMHWORKGUBUN", "위험물부가정보");
			ds_iCustomerModifyHistory.SetColumn(nRow, "CMHKEY", sCmgno);
			ds_iCustomerModifyHistory.SetColumn(nRow, "CMHCOLUMN", "신규등록");
			ds_iCustomerModifyHistory.SetColumn(nRow, "CMHBEFOREDATA", "$1");
			ds_iCustomerModifyHistory.SetColumn(nRow, "CMHAFTERDATA", ds_ioDangerInfo.GetColumn(j, "DPERMISSIONNO"));

		} else if( ds_ioDangerInfo.GetRowType(j) == "update" ) {	//위험물부가정보 수정

			for(var i=0; i<ds_ioDangerInfo.GetColCount(); i++) {
				if( ds_ioDangerInfo.GetOrgColumn(j, ds_ioDangerInfo.GetColID(i))
					<> ds_ioDangerInfo.GetColumn(j, ds_ioDangerInfo.GetColID(i)) ) {	//컬럼값 변경

					var nRow = ds_iCustomerModifyHistory.AddRow();

					ds_iCustomerModifyHistory.SetColumn(nRow, "CMHGUBUN", "U");
					ds_iCustomerModifyHistory.SetColumn(nRow, "CMHWORKGUBUN", "위험물부가정보");
					ds_iCustomerModifyHistory.SetColumn(nRow, "CMHKEY", sCmgno);
					ds_iCustomerModifyHistory.SetColumn(nRow, "CMHCOLUMN", ds_oModifyColumnList.GetColumn( ds_oModifyColumnList.FindRow("COLNM", ds_ioDangerInfo.GetColID(i)) , "COLKORNM"));

					var lsComboYn = "N";
					var lsInnerDsNm = "";
					if( ds_oModifyColumnList.GetColumn( ds_oModifyColumnList.FindRow("COLNM", ds_ioDangerInfo.GetColID(i)) , "COMBOGUBUN" ) == "Y" ) {	//Combo Component Check
						lsComboYn = "Y";
						lsInnerDsNm = ds_oModifyColumnList.GetColumn( ds_oModifyColumnList.FindRow("COLNM", ds_ioDangerInfo.GetColID(i)) , "INNERDSNM" );	//Inner Dataset Name
					}

					if(lsComboYn == "Y") {	//Combo Component 인 경우
						//BEFOREDATA
						if(!gfn_IsNull((ds_ioDangerInfo.GetOrgColumn(j, ds_ioDangerInfo.GetColID(i))))) {
							var lsOrgCd = ds_ioDangerInfo.GetOrgColumn(j, ds_ioDangerInfo.GetColID(i));
							ds_iCustomerModifyHistory.SetColumn(nRow, "CMHBEFOREDATA", Object(lsInnerDsNm).GetColumn(Object(lsInnerDsNm).FindRow("CD", lsOrgCd), "DATA"));
						} else {
							ds_iCustomerModifyHistory.SetColumn(nRow, "CMHBEFOREDATA", "$1");
						}
						//AFTERDATA
						if(!gfn_IsNull((ds_ioDangerInfo.GetColumn(j, ds_ioDangerInfo.GetColID(i))))) {
							var lsNewCd = ds_ioDangerInfo.GetColumn(j, ds_ioDangerInfo.GetColID(i));
							ds_iCustomerModifyHistory.SetColumn(nRow, "CMHAFTERDATA", Object(lsInnerDsNm).GetColumn(Object(lsInnerDsNm).FindRow("CD", lsNewCd), "DATA"));
						} else {
							ds_iCustomerModifyHistory.SetColumn(nRow, "CMHAFTERDATA", "$1");
						}
					} else {	//Combo Component 가 아닌경우
						//BEFOREDATA
						if(!gfn_IsNull((ds_ioDangerInfo.GetOrgColumn(j, ds_ioDangerInfo.GetColID(i))))) {
							ds_iCustomerModifyHistory.SetColumn(nRow, "CMHBEFOREDATA", ds_ioDangerInfo.GetOrgColumn(j, ds_ioDangerInfo.GetColID(i)));
						} else {
							ds_iCustomerModifyHistory.SetColumn(nRow, "CMHBEFOREDATA", "$1");
						}
						//AFTERDATA
						if(!gfn_IsNull((ds_ioDangerInfo.GetColumn(j, ds_ioDangerInfo.GetColID(i))))) {
							ds_iCustomerModifyHistory.SetColumn(nRow, "CMHAFTERDATA", ds_ioDangerInfo.GetColumn(j, ds_ioDangerInfo.GetColID(i)));
						} else {
							ds_iCustomerModifyHistory.SetColumn(nRow, "CMHAFTERDATA", "$1");
						}
					}
				}
			}
		}

	}

	//타자격증정보
	for( var j=0; j<ds_ioLicenseInfo.GetRowCount(); j++) {

		if( ds_ioLicenseInfo.GetRowType(j) == "insert" ) {	//타자격증정보 신규

			var nRow = ds_iCustomerModifyHistory.AddRow();
			ds_iCustomerModifyHistory.SetColumn(nRow, "CMHGUBUN", "I");
			ds_iCustomerModifyHistory.SetColumn(nRow, "CMHWORKGUBUN", "타자격증정보");
			ds_iCustomerModifyHistory.SetColumn(nRow, "CMHKEY", sCmgno);
			ds_iCustomerModifyHistory.SetColumn(nRow, "CMHCOLUMN", "신규등록");
			ds_iCustomerModifyHistory.SetColumn(nRow, "CMHBEFOREDATA", "$1");
			ds_iCustomerModifyHistory.SetColumn(nRow, "CMHAFTERDATA", ds_ioLicenseInfo.GetColumn(j, "OLNO"));

		} else if( ds_ioLicenseInfo.GetRowType(j) == "update" ) {	//타자격증정보 수정

			for(var i=0; i<ds_ioLicenseInfo.GetColCount(); i++) {
				if( ds_ioLicenseInfo.GetOrgColumn(j, ds_ioLicenseInfo.GetColID(i))
					<> ds_ioLicenseInfo.GetColumn(j, ds_ioLicenseInfo.GetColID(i)) ) {	//컬럼값 변경

					var nRow = ds_iCustomerModifyHistory.AddRow();

					ds_iCustomerModifyHistory.SetColumn(nRow, "CMHGUBUN", "U");
					ds_iCustomerModifyHistory.SetColumn(nRow, "CMHWORKGUBUN", "타자격증정보");
					ds_iCustomerModifyHistory.SetColumn(nRow, "CMHKEY", sCmgno);
					ds_iCustomerModifyHistory.SetColumn(nRow, "CMHCOLUMN", ds_oModifyColumnList.GetColumn( ds_oModifyColumnList.FindRow("COLNM", ds_ioLicenseInfo.GetColID(i)) , "COLKORNM"));

					var lsComboYn = "N";
					var lsInnerDsNm = "";
					if( ds_oModifyColumnList.GetColumn( ds_oModifyColumnList.FindRow("COLNM", ds_ioLicenseInfo.GetColID(i)) , "COMBOGUBUN" ) == "Y" ) {	//Combo Component Check
						lsComboYn = "Y";
						lsInnerDsNm = ds_oModifyColumnList.GetColumn( ds_oModifyColumnList.FindRow("COLNM", ds_ioLicenseInfo.GetColID(i)) , "INNERDSNM" );	//Inner Dataset Name
					}

					if(lsComboYn == "Y") {	//Combo Component 인 경우
						//BEFOREDATA
						if(!gfn_IsNull((ds_ioLicenseInfo.GetOrgColumn(j, ds_ioLicenseInfo.GetColID(i))))) {
							var lsOrgCd = ds_ioLicenseInfo.GetOrgColumn(j, ds_ioLicenseInfo.GetColID(i));
							ds_iCustomerModifyHistory.SetColumn(nRow, "CMHBEFOREDATA", Object(lsInnerDsNm).GetColumn(Object(lsInnerDsNm).FindRow("CD", lsOrgCd), "DATA"));
						} else {
							ds_iCustomerModifyHistory.SetColumn(nRow, "CMHBEFOREDATA", "$1");
						}
						//AFTERDATA
						if(!gfn_IsNull((ds_ioLicenseInfo.GetColumn(j, ds_ioLicenseInfo.GetColID(i))))) {
							var lsNewCd = ds_ioLicenseInfo.GetColumn(j, ds_ioLicenseInfo.GetColID(i));
							ds_iCustomerModifyHistory.SetColumn(nRow, "CMHAFTERDATA", Object(lsInnerDsNm).GetColumn(Object(lsInnerDsNm).FindRow("CD", lsNewCd), "DATA"));
						} else {
							ds_iCustomerModifyHistory.SetColumn(nRow, "CMHAFTERDATA", "$1");
						}
					} else {	//Combo Component 가 아닌경우
						//BEFOREDATA
						if(!gfn_IsNull((ds_ioLicenseInfo.GetOrgColumn(j, ds_ioLicenseInfo.GetColID(i))))) {
							ds_iCustomerModifyHistory.SetColumn(nRow, "CMHBEFOREDATA", ds_ioLicenseInfo.GetOrgColumn(j, ds_ioLicenseInfo.GetColID(i)));
						} else {
							ds_iCustomerModifyHistory.SetColumn(nRow, "CMHBEFOREDATA", "$1");
						}
						//AFTERDATA
						if(!gfn_IsNull((ds_ioLicenseInfo.GetColumn(j, ds_ioLicenseInfo.GetColID(i))))) {
							ds_iCustomerModifyHistory.SetColumn(nRow, "CMHAFTERDATA", ds_ioLicenseInfo.GetColumn(j, ds_ioLicenseInfo.GetColID(i)));
						} else {
							ds_iCustomerModifyHistory.SetColumn(nRow, "CMHAFTERDATA", "$1");
						}
					}					
				}
			}
		} 
	}
}
