#include "LIB::cust.js"; // 공통업무 관련


function Form_OnLoadCompleted(obj) {
	gfn_Form_OnLoadCompleted(obj); // 공통 폼 로딩 이벤트 처리

    // 폼 환경설정
    ls_SystemGubun = gs_SystemGubun;
    ls_SystemMenuId = gs_SystemMenuId;
    var ls_FormId = split(sFormUrl, "::");
    ls_FormId = split(ls_FormId[1], ".");
    obj.Title = sFormTitle + "(" + ls_FormId[0] + ")";

    gfn_MakeRequiredInputMark(this); // 필수 입력란을 설정한다.

    ms_FormAuth = gfn_GetFormAuth(this); // 권한을 조회한다.
    if (ms_FormAuth == "W") {            // 권한에 따른 버튼 설정
        lfn_SetFormAuth(true);           // 저장 권한
    } else if (ms_FormAuth == "R") {
        lfn_SetFormAuth(false);          // 읽기 권한
    } else {
        gfn_Alert(MSGI00080);
        close();
    }

    // 지부 및 소방본부 
    var adminGubun = gds_oUserInfo.GetColumn(0, "ADMINGUBUN");
    var deptCD     = gds_oUserInfo.GetColumn(0, "PDEPTCD");
   
    if (adminGubun == "BM" || adminGubun == "SM" || adminGubun == "PM") {  // 관리자 : 전체 지부 
		gfn_SearchAllJibu(cbxJibu, "Y","");
		ds_oBonbu.Copy("gds_oFireBonbuCode");
	}
	else if (SubStr(deptCD,0,1) == "1") {            // 지부 : 해당지부만 
	    // 지부 
        var nRow = ds_oJibu.AddRow();
        ds_oJibu.SetColumn(nRow, "CD", deptCD);
        ds_oJibu.SetColumn(nRow, "DATA", gds_oUserInfo.GetColumn(0, "PDEPTNM"));	
		ds_oJibu.row = 0;
		cbxJibu.Value = gds_oUserInfo.GetColumn(0, "PDEPTNM");
		cbxJibu.Index = 0;
        
        // 본부 
        
        // 경기 북부는 경기로 .. 나머진 그대
//		if (deptCD == "1141") {
//			deptCD = "1081";
//		} 
		
		gds_oFireBonbuCode.Filter("JIBUCD=='" + trim(deptCD) + "'");
		for (var i = 0; i < gds_oFireBonbuCode.GetCount(); i++) {
			nRow = ds_oBonbu.AddRow();
			ds_oBonbu.SetColumn(nRow, "CD", gds_oFireBonbuCode.GetColumn(i, "CD"));
			ds_oBonbu.SetColumn(nRow, "DATA", gds_oFireBonbuCode.GetColumn(i, "DATA"));
			ds_oBonbu.SetColumn(nRow, "JIBUCD", gds_oFireBonbuCode.GetColumn(i, "JIBUCD"));
		}
		ds_oBonbu.row = 0;
		cbxBonbu.Value = gds_oFireBonbuCode.GetColumn(0, "DATA");
		cbxBonbu.Index = 0;
		gds_oFireBonbuCode.UnFilter();
	}
	else {
        gfn_Alert(MSGI00080);
        close();
	}

	
	// 라디오버튼 초기화 
	radPrtGubun.index=0;   // 출력물 
	
    //지역
	cbxJibu_OnChanged("",cbxJibu.Value);//지역 코드 조회
    cbxRegion1.Index = 0;
    cbxRegion2.index = ds_oRegion.rowcount-1;    		
    
    //직능
    cbxCourse.index=0;
    
	// 24.01  직능에서 위험물운송자 완전삭제, 소방기술자는 임시로 안보이기 삭제(추후 만들것임)
	for(var i=ds_ioCourse.GetRowCount()-1; i>0; i--) {
		if ((ds_ioCourse.GetColumn(i, "CD") == "5") ||
		    (ds_ioCourse.GetColumn(i, "CD") == "8"))
		ds_ioCourse.DeleteRow(i);
	}
  
    //소방서 SEARCH
    lfn_search_FireStation();    
    
    //연계제외코드 값 조회 
	lfn_ExceptCode();
}


