//초기화
function lfn_Cancel(obj) {
    lfn_CancelL(obj);
}

function lfn_CancelL(obj) {
    radPrtGubun.Value="1";
    radPrtGubun_OnChanged(obj, "1");

    radExcept.Index=1;

	radGijun.Index=0;
	
	cbxJibu.Index=0; 
	cbxBonbu.Index=0;
	radGijun_OnChanged(obj, "1");
	cbxJibu_OnChanged(obj, "1011");

	radRegion.Index=0;
	radRegion_OnChanged(obj, "1");
}


/***  조회 조건 변경  ***/
// 소방서 search 
function lfn_search_FireStation() {
	ds_ioFireStation.ClearData(); 

    tit_clearActionInfo();
    tit_addCsvSearchActionInfo("cust:searchCust6520FireStation", false);
    
	// 서버 호출 
	tit_callService("", "", "", "ds_ioFireStation=ds_ioFireStation", "", "lfn_AfterSearch", false, "", true); 
}


// 출력물
function radPrtGubun_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if (strCode == "1") {
		radMatchGubun.Enable = false;
		radExceptGubun.Enable = false;
		radMatchGubun.Value = "0";
		radExceptGubun.Value = "";
		radOrder.Enable = false;
	}
	else {
		radMatchGubun.Enable = true;
		radMatchGubun.Value = "0";
		radOrder.Enable = true;
	}
}

// 출력기준
function radGijun_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	//cbxJibu.Index = "0";
	//cbxBonbu.Index = "0";

	if (strCode == "1") {
		cbxJibu.Enable = true;
		cbxBonbu.Enable = false;
		cbxJibu_OnChanged();
	}
	else {
		cbxJibu.Enable = false;
		cbxBonbu.Enable = true;	
		cbxBonbu_OnChanged();
	}
}


//지부
function cbxJibu_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{ 
    ds_oRegion.ClearData();    
	gfn_SearchCodeRegion(cbxJibu.Value, ds_oRegion, "0");	//지역 코드 조회
    cbxRegion1.Index = 0;
    cbxRegion2.index = ds_oRegion.rowcount-1;
    
    Static5.Text = "지역";
    Static5.Value = "지역";
}

