//지역--라디오 버튼 선택시
function radRegion_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
    if (strCode == "2") { // 선택 
        cbxRegion1.Enable =  true;
        cbxRegion2.Enable =  true;                       
        cbxRegion1.SetFocus();
        cbxRegion1.Index = 0;
        cbxRegion2.index = ds_oRegion.rowcount-1;    
	} else {              // 전체
        cbxRegion1.Enable =  false;
        cbxRegion2.Enable =  false;                
        cbxRegion1.Index = 0;
        cbxRegion2.index = ds_oRegion.rowcount-1;    
	}
}

// 현재 선택한 소방본부에 소방서 찾기 
function lfn_filter_FireStation() {
	
	var bonbu = cbxBonbu.Value;
	
	if (bonbu == "") {
		bonbu = ds_ioFireStation.GetColumn(1, "BID");
	}
	
	ds_oRegion.ClearData();

    var nRow = -1;
    var ls_FilterExpr = "BID=='" + trim(bonbu) + "'";

	ds_ioFireStation.Filter(ls_FilterExpr);
	
    for (var i = 0; i < ds_ioFireStation.GetCount(); i++) {
        nRow = ds_oRegion.AddRow();
        ds_oRegion.SetColumn(nRow, "CD", ds_ioFireStation.GetColumn(i, "SID"));
		ds_oRegion.SetColumn(nRow, "DATA", ds_ioFireStation.GetColumn(i, "SNM"));
		ds_oRegion.SetColumn(nRow, "GTCD", ds_ioFireStation.GetColumn(i, "GMGNO"));
    }

    ds_oRegion.row = 0;
    ds_ioFireStation.UnFilter();

    cbxRegion1.Index = 0;
    cbxRegion2.index = ds_oRegion.rowcount-1;    
}
