/***   기능 : 출력   ***/
function lfn_Print(obj){
    lfn_Search();
    
    course = "";
    if (ds_oDATA.GetCount() < 1) {
        gfn_Alert("출력할 데이터가 없습니다.");
        return false;
    }

    var rpt = "";
    var params = "";
    var params2 = "";
    var params3 = "";
    var sTopMargin = "150";
    var sLeftMargin = "";

    if (radPrtGubun.Value == "1") {    // 출력구분 : 지역별
        
		if (cbxCourse.Value == "1")      { rpt = "frmcust6510RegionJibu_1.rex"; } // 직능 : 소관 
		else if (cbxCourse.Value == "3") { rpt = "frmcust6510RegionJibu_3.rex"; } // 직능 : 위관
