		else if (cbxCourse.Value == "8") { 
			rpt = "frmcust6510RegionJibu_8.rex"; 
			sLeftMargin = "200";
		}
		
		// 출력기준 
        if (radGijun.Value == "1")      { 
			if (cbxCourse.Value == "1")      { params = "(소방안전관리자) 안전원데이터연계 지역별 집계현황"; }
			else if (cbxCourse.Value == "3") { params = "(위험물안전관리자) 안전원데이터연계 지역별 집계현황"; }
			else if (cbxCourse.Value == "5") { params = "(소방기술자) 안전원데이터연계 지역별 집계현황"; }			
			else if (cbxCourse.Value == "8") { params = "(위험물운송자) 안전원데이터연계 지역별 집계현황"; }				
		}
        else if (radGijun.Value == "2") { 
			if (cbxCourse.Value == "1")      { params = "(소방안전관리자) 안전원데이터연계 소방서별 집계현황"; }
			else if (cbxCourse.Value == "3") { params = "(위험물안전관리자) 안전원데이터연계 소방서별 집계현황"; }
			else if (cbxCourse.Value == "5") { params = "(소방기술자) 안전원데이터연계 소방서별 집계현황"; }			
			else if (cbxCourse.Value == "8") { params = "(위험물운송자) 안전원데이터연계 소방서별 집계현황"; }				
		}  
    }
    
	else if (radPrtGubun.Value == "2") {    // 출력구분 : 리스트 
		if (cbxCourse.Value == "8") { rpt = "frmcust6510ListJibu_8.rex"; }
		else { rpt = "frmcust6510ListJibu.rex"; }
		
		// 출력기준 
        if (radGijun.Value == "1")      { 
			if (cbxCourse.Value == "1")      { params = "(소방안전관리자) 안전원데이터연계 지역별 상세현황"; }
			else if (cbxCourse.Value == "3") { params = "(위험물안전관리자) 안전원데이터연계 지역별 상세현황"; }
			else if (cbxCourse.Value == "5") { params = "(소방기술자) 안전원데이터연계 지역별 상세현황"; }			
			else if (cbxCourse.Value == "8") { params = "(위험물운송자) 안전원데이터연계 지역별 상세현황"; }				
		}
        else if (radGijun.Value == "2") { 
			if (cbxCourse.Value == "1")      { params = "(소방안전관리자) 안전원데이터연계 소방서별 상세현황"; }
			else if (cbxCourse.Value == "3") { params = "(위험물안전관리자) 안전원데이터연계 소방서별 상세현황"; }
			else if (cbxCourse.Value == "5") { params = "(소방기술자) 안전원데이터연계 소방서별 상세현황"; }			
			else if (cbxCourse.Value == "8") { params = "(위험물운송자) 안전원데이터연계 소방서별 상세현황"; }				
