	// 출력기준 
	if (radGijun.Value == "1")      { params2 = "지 역"; }
	else if (radGijun.Value == "2") { params2 = "소방서"; }
	
	// 소제목
	if (cbxCourse.Value == "5") {
		params3 = "기술자명";
	} else {
