	}
	
	params = "rd_TITLE=" + quote(params) + "^s_TITLE=" + quote(params2)+ "^s_TITLE1=" + quote(params3);     
		
	/*
	 * 기능 : 렉스퍼트 보고서를 팝업화면으로 호출한다.
	 * parameter : sReportName - 레포트명(예:레포트.rex)
	 *             sReportDataset - 데이터셋명(예:dsReport1||dsResport2||...)
	 *             sReportParams - 렉스퍼트용 파라메터(예: a에 1, b에 2, c에 3의 값을 넣는 경우, a=1^b=2^c=3...)
	 *             bLcsYn - 수첩여부
	 *             LeftMargin = 왼쪽
	 *             RightMargin = 오른쪽
	 *             TopMargin = 상단
	 *             BottomMargin = 하단
	 * return value : 정상출력 true, 출력 안했으면 false
	 */    
	 
    //gfn_CallReportPopup(rpt, "ds_oDATA", params);
    gfn_CallReportPopup(rpt, "ds_oDATA", params, '', '', sTopMargin, sLeftMargin);
}




function lfn_Search(obj) {
    SetWaitCursor(false);
	
    ds_oDATA.ClearData(); // 데이타셋 초기화
    
    tit_clearActionInfo();
    
    
    var qry = "cust:searchCust6510RegionJibu_1";
    var bizGubun = "0";  // MATCH 테이블 - 직능 구분  :: 소방안전관리자 
    
    if      (cbxCourse.Value == "3") { bizGubun = "1"; } // 위험물안전관리자 
    else if (cbxCourse.Value == "5") { bizGubun = "3"; } // 소방기술자 
    else if (cbxCourse.Value == "8") { bizGubun = "2"; } // 위험물운송자 

    if (radPrtGubun.Value == "1") {    // 출력구분 : 지역별
        // 출력기분 
        if (radGijun.Value == "1")      { qry = "cust:searchCust6510RegionJibu"; }
        else if (radGijun.Value == "2") { qry = "cust:searchCust6510RegionBonbu"; }
    }
    
	else if (radPrtGubun.Value == "2") {    // 출력구분 : 리스트 
        if (radGijun.Value == "1") {    // 출력기분 : 지부 
            if (cbxCourse.Value == "8") { qry = "cust:searchCust6510ListJibu_8"; } // 위험물운송자 
            else { qry = "cust:searchCust6510ListJibu"; }
        }
        else if (radGijun.Value == "2") {    // 출력기분 : 소방본부 
            if (cbxCourse.Value == "8") { qry = "cust:searchCust6510ListBonbu_8"; } // 위험물운송자 
            else { qry = "cust:searchCust6510ListBonbu"; }
        }
    }

    tit_addCsvSearchActionInfo(qry, false);

	// 서버 호출 
	tit_callService(
         "",""
		,""	// inDs
		,"ds_oDATA=ds_oDATA"	// outDs
		,     " MATCHGUBUN=" + quote(radMatchGubun.Value)    // 일치구분 
            + " JIGUN=" + quote(radGijun.Value)      // 출력기준 
            + " JIBU=" + quote(cbxJibu.Value)        // 지부
	    	+ " BONBU=" + quote(cbxBonbu.Value)      // 소방본부 
	    	+ " REGION=" +quote(radRegion.value)     // 지역구분
            + " REGIONCD1=" +quote(cbxRegion1.value) // 지역코드1 
            + " REGIONCD2=" +quote(cbxRegion2.value) // 지역코드2 
            + " COURSE=" + quote(cbxCourse.Value)    // 직능
            + " COURSE=" + quote(cbxCourse.Value)    // 직능
            + " COURSE=" + quote(cbxCourse.Value)    // 직능
            + " ORDER=" +quote(radOrder.Value)	     // 정렬순서 
            + " BIZGUBUN=" +quote(bizGubun)	         // MATCH 테이블 - 직능 구분 
            + " EXCEPTGUBUN=" + quote(radExceptGubun.Value)     // 연계제외사유코드 구분
		, "lfn_AfterSearch"
		, false 
		, false    // 처리중 창 보여주기 (null or false : 보여줌) 
		, true     // 동기(true), 비동기(false) 
	); 
		   
}

function lfn_AfterSearch(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);     
        course = "";
        return;
    }
    
        // 조회결과 유무 체크 
    if (ds_oDATA.GetCount() > 0) {
		// 조회이력을 저장한다.  
		ls_HistoryArg = "출력물=" + quote(radPrtGubun.Value)
					+ ",처리구분=" + quote(radMatchGubun.Value)
					+ ",출력기준=" + quote(radGijun.Value)
					+ ",지부=" + quote(cbxJibu.Value)
					+ ",소방본부=" + quote(cbxBonbu.Value)                
					+ ",지역선택=" + quote(radRegion.Value)
					+ ",지역1=" + quote(cbxRegion1.Value)
					+ ",지역2=" + quote(cbxRegion2.Value)
					+ ",직능=" + quote(cbxCourse.Value)
					+ ",정렬순서=" + quote(radOrder.value);
					+ ",처리구분=" + quote(radMatchGubun.Value)    // 일치구분 
					+ ",연계제외처리구분=" + quote(radExceptGubun.Value);
	
		gfn_InsertFPISSearchPrintHistory(
			"P"
			, this.ID
			, global.mnuLeft.cbxOpenForm.Text
			, radPrtGubun.Text
			, ls_HistoryArg
		);
    }
}







/***  엑셀   ***/
function lfn_Excel(obj) {	
    lfn_ExcelL(obj);
}

function lfn_ExcelL(obj)
{	
    lfn_Search();

	if(ds_oDATA.rowcount == 0){
		alert("데이터가 없습니다.");
		return;
	}

    if (radPrtGubun.Value == "1") {    // 출력구분 : 지역별
		gdList1.Format = "region_" + cbxCourse.Value;
		
		// 출력기준 
        if (radGijun.Value == "1")      { 
			gdList1.SetCellProp("head",1,"text","지 역"); 
			
			if (cbxCourse.Value == "1")      { 
				gdList1.SetCellProp("head",0,"text","안전원데이터연계 지역별 집계현황(소방안전관리자)"); 
			}
			else if (cbxCourse.Value == "3") { 
				gdList1.SetCellProp("head",0,"text","안전원데이터연계 지역별 집계현황(위험물안전관리자)"); 
			}
			else if (cbxCourse.Value == "5") { 
				gdList1.SetCellProp("head",0,"text","안전원데이터연계 지역별 집계현황(소방기술자)"); 
			}
			else if (cbxCourse.Value == "8") { 
				gdList1.SetCellProp("head",0,"text","안전원데이터연계 지역별 집계현황(위험물운송자)"); 
			}
		}
        else if (radGijun.Value == "2") { 
			gdList1.SetCellProp("head",1,"text","소방서"); 

			if (cbxCourse.Value == "1")      { 
				gdList1.SetCellProp("head",0,"text","안전원데이터연계 소방서별 집계현황(소방안전관리자)"); 
			}
			else if (cbxCourse.Value == "3") { 
				gdList1.SetCellProp("head",0,"text","안전원데이터연계 소방서별 집계현황(위험물안전관리자)"); 
			}
			else if (cbxCourse.Value == "5") { 
				gdList1.SetCellProp("head",0,"text","안전원데이터연계 소방서별 집계현황(소방기술자)"); 
			}
			else if (cbxCourse.Value == "8") { 
				gdList1.SetCellProp("head",0,"text","안전원데이터연계 소방서별 집계현황(위험물운송자)"); 
			}
		}
    }
    
    else if (radPrtGubun.Value == "2") {    // 출력구분 : 리스트 
        if (cbxCourse.Value == "8") { gdList1.Format = "list_jibu_8"; }
        else { gdList1.Format = "list_jibu"; }
        
		// 출력기준 
        if (radGijun.Value == "1")      { 
			gdList1.SetCellProp("head",3,"text","지 역"); 

			if (cbxCourse.Value == "1")      { 
				gdList1.SetCellProp("head",0,"text","안전원데이터연계 지역별 상세현황(소방안전관리자)"); 
			}
			else if (cbxCourse.Value == "3") { 
				gdList1.SetCellProp("head",0,"text","안전원데이터연계 지역별 상세현황(위험물안전관리자)"); 
			}
			else if (cbxCourse.Value == "5") { 
				gdList1.SetCellProp("head",0,"text","안전원데이터연계 지역별 상세현황(소방기술자)"); 
			}
			else if (cbxCourse.Value == "8") { 
				gdList1.SetCellProp("head",0,"text","안전원데이터연계 지역별 상세현황(위험물운송자)"); 
			}
		}
        else if (radGijun.Value == "2") { 
			gdList1.SetCellProp("head",3,"text","소방서"); 

			if (cbxCourse.Value == "1")      { 
				gdList1.SetCellProp("head",0,"text","안전원데이터연계 소방서별 상세현황(소방안전관리자)"); 
			}
			else if (cbxCourse.Value == "3") { 
				gdList1.SetCellProp("head",0,"text","안전원데이터연계 소방서별 집계현황(위험물안전관리자)"); 
			}
			else if (cbxCourse.Value == "5") { 
				gdList1.SetCellProp("head",0,"text","안전원데이터연계 소방서별 상세현황(소방기술자)"); 
			}
			else if (cbxCourse.Value == "8") { 
				gdList1.SetCellProp("head",0,"text","안전원데이터연계 소방서별 상세현황(위험물운송자)"); 
			}
		}
		
		// 개인정보 다운로드 사유 등록
		var parValue = "sCode=1455";	// COMMON.PERSONALDOWNLOADH
			parValue += " sRowCnt=" + ds_oDATA.GetRowCount();
			parValue += " sTitle=" + quote(gdList1.UserData);
		var ls_IsRemark = Dialog("COM::frmCOM2100PDownloadRemark.xml", parValue, "", "", "", -1, -1);	
	
		if (ls_IsRemark == "" || ls_IsRemark == null || ls_IsRemark == "FAIL") {
			return false;
		}
    }
    
	gdList1.ExportExcelEx("sheet1");
}
/***  엑셀 출력 끝  ***/

/*
	처리구분 변경시 
*/
function radMatchGubun_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if (strCode == "2") {	// 연계제외인경우 연계 제외사유 구분 활성화 
		radExceptGubun.Enable = true;
		radExceptGubun.Value = "";
	} else { // 연계제외가 아닌  경우 연계 제외사유 구분 비활성화 
		radExceptGubun.Enable = false;
		radExceptGubun.Value = "";
	}
}

/*
 *  연계제외코드 값 조회 
*/
function lfn_ExceptCode(obj) {  
    
	ds_ioExceptGubun.ClearData();

    tit_clearActionInfo();
	tit_addSingleActionInfo("cust:searchCust6003PExceptMatchCode", false);
	
	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_ioExceptGubun=ds_ioReasonCode"	// outDs
		,""
		,"lfn_ExceptCodeAfterSearch"
		,false
	);
}

/*
 * 기능 : 연계제외코드 값 조회 후 처리 
 * parameter : nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_ExceptCodeAfterSearch(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }
    ds_ioExceptGubun.InsertRow("0");
    ds_ioExceptGubun.SetColumn(0,"DATA","전체");
     
     radExceptGubun.Value = "";
}

function Form_OnActivate(obj)
{
	gs_SysID = "[stm_01,bsn_1003,dsc_0012]";	// 개인정보접속기록 시스템ID 
}
