#include "LIB::cust.js"; // 공통업무 관련


function Form_OnLoadCompleted(obj) {
	gfn_Form_OnLoadCompleted(obj); // 공통 폼 로딩 이벤트 처리
	
    // 폼 환경설정
    ls_SystemGubun = gs_SystemGubun;
    ls_SystemMenuId = gs_SystemMenuId;
    var ls_FormId = split(sFormUrl, "::");
    ls_FormId = split(ls_FormId[1], ".");
    obj.Title = sFormTitle + "(" + ls_FormId[0] + ")";

    gfn_MakeRequiredInputMark(this); // 필수 입력란을 설정한다.

    ms_FormAuth = gfn_GetFormAuth(this); // 권한을 조회한다.
    if (ms_FormAuth == "W") {            // 권한에 따른 버튼 설정
        lfn_SetFormAuth(true);           // 저장 권한
    } else if (ms_FormAuth == "R") {
        lfn_SetFormAuth(false);          // 읽기 권한
    } else {
        gfn_Alert(MSGI00080);
        close();
    }

    // 소방본부 콤보세팅
    var adminGubun = gds_oUserInfo.GetColumn(0, "ADMINGUBUN");
    var deptCD     = gds_oUserInfo.GetColumn(0, "PDEPTCD");
   
    if (adminGubun == "BM" || adminGubun == "SM" || adminGubun == "PM") {  // 관리자 : 전체 지부 
		ds_oBonbu.Copy("gds_oFireBonbuCode");
	}
	else if (SubStr(deptCD,0,1) == "1") {            // 지부 : 해당지부만 
	
		// 경기 북부는 경기로 .. 나머진 그대
//		if (deptCD == "1141") {
//			deptCD = "1081";
//		} 
		
		gds_oFireBonbuCode.Filter("JIBUCD=='" + trim(deptCD) + "'");
		for (var i = 0; i < gds_oFireBonbuCode.GetCount(); i++) {
			nRow = ds_oBonbu.AddRow();
			ds_oBonbu.SetColumn(nRow, "CD", gds_oFireBonbuCode.GetColumn(i, "CD"));
			ds_oBonbu.SetColumn(nRow, "DATA", gds_oFireBonbuCode.GetColumn(i, "DATA"));
			ds_oBonbu.SetColumn(nRow, "JIBUCD", gds_oFireBonbuCode.GetColumn(i, "JIBUCD"));
		}
		ds_oBonbu.row = 0;
		cbxBonbu.Value = gds_oFireBonbuCode.GetColumn(0, "DATA");
		cbxBonbu.Index = 0;
		gds_oFireBonbuCode.UnFilter();
	}
	else {
        gfn_Alert(MSGI00080);
        close();
	}
		
		
	// 라디오버튼 초기화 
	radPrtGubun.index=0;   // 출력물 
    
    //직능
    cbxCourse.index=0;
    
	// 24.01  직능에서 위험물운송자 완전삭제, 소방기술자는 임시로 안보이기 삭제(추후 만들것임)
	for(var i=ds_ioCourse.GetRowCount()-1; i>0; i--) {
		if ((ds_ioCourse.GetColumn(i, "CD") == "5") ||
		    (ds_ioCourse.GetColumn(i, "CD") == "8"))
		ds_ioCourse.DeleteRow(i);
	}
    
    //소방서 SEARCH
    lfn_search_FireStation();
    lfn_filter_FireStation();
    
    //연계제외코드 값 조회 
	lfn_ExceptCode();
}


function Form_OnSize(obj,nCx,nCy,nState) {
    if (gs_IsResizingForm == "Y") { }
}

function lfn_SetFormAuth(bStatus) {}


// 소방서 search 
function lfn_search_FireStation() {
	ds_ioFireStation.ClearData(); 

    tit_clearActionInfo();
    tit_addCsvSearchActionInfo("cust:searchCust6520FireStation", false);
    
	// 서버 호출 
	tit_callService("", "", "", "ds_ioFireStation=ds_ioFireStation", "", "lfn_AfterSearch", false, "", true); 
}


// 현재 선택한 소방본부에 소방서 찾기 
function lfn_filter_FireStation() {
	
	var bonbu = cbxBonbu.Value;
	
	if (bonbu == "") {
		bonbu = ds_ioFireStation.GetColumn(1, "BID");
	}
	
	ds_oFireStation.ClearData();

    var nRow = -1;
    var ls_FilterExpr = "BID=='" + trim(bonbu) + "'";

	ds_ioFireStation.Filter(ls_FilterExpr);
	
    for (var i = 0; i < ds_ioFireStation.GetCount(); i++) {
        nRow = ds_oFireStation.AddRow();
        ds_oFireStation.SetColumn(nRow, "CD", ds_ioFireStation.GetColumn(i, "SID"));
		ds_oFireStation.SetColumn(nRow, "DATA", ds_ioFireStation.GetColumn(i, "SNM"));
		ds_oFireStation.SetColumn(nRow, "GTCD", ds_ioFireStation.GetColumn(i, "GMGNO"));
    }

    ds_oFireStation.row = 0;
    ds_ioFireStation.UnFilter();

    cbxFireStation1.Index = 0;
    cbxFireStation2.index = ds_oFireStation.rowcount-1;    
}



//초기화
function lfn_Cancel(obj) {
    lfn_CancelL(obj);
}

function lfn_CancelL(obj) {
    radPrtGubun.Value="1";
    radPrtGubun_OnChanged(obj, "1");
    
	cbxBonbu.Index=0;

	radRegion_OnChanged(obj, "1");
}


/***  조회 조건 변경  ***/

// 출력물
function radPrtGubun_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if (strCode == "1") {
		radMatchGubun.Enable = false;
		radExceptGubun.Enable = false;
		radMatchGubun.Value = "0";
		radExceptGubun.Value = "";
		radOrder.Enable = false;
	}
	else {
		radMatchGubun.Enable = true;
		radMatchGubun.Value = "0";
		radOrder.Enable = true;
	}
}


// 소방본부
function cbxBonbu_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	lfn_filter_FireStation();
}

//지역--라디오 버튼 선택시
function radFireStation_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
    if (strCode == "2") { // 선택 
        cbxFireStation1.Enable =  true;
        cbxFireStation2.Enable =  true;                       
        cbxFireStation1.SetFocus();
        cbxFireStation1.Index = 0;
        cbxFireStation2.index = ds_oFireStation.rowcount-1;    
	} else {              // 전체
        cbxFireStation1.Enable =  false;
        cbxFireStation2.Enable =  false;                
        cbxFireStation1.Index = 0;
        cbxFireStation2.index = ds_oFireStation.rowcount-1;    
	}
}
