Window.Script:
#include "LIB::cust.js"; // 공통업무 관련


function Form_OnLoadCompleted(obj) {
	gfn_Form_OnLoadCompleted(obj); // 공통 폼 로딩 이벤트 처리
	
    // 폼 환경설정
    ls_SystemGubun = gs_SystemGubun;
    ls_SystemMenuId = gs_SystemMenuId;
    var ls_FormId = split(sFormUrl, "::");
    ls_FormId = split(ls_FormId[1], ".");
    obj.Title = sFormTitle + "(" + ls_FormId[0] + ")";

    gfn_MakeRequiredInputMark(this); // 필수 입력란을 설정한다.

    ms_FormAuth = gfn_GetFormAuth(this); // 권한을 조회한다.
    if (ms_FormAuth == "W") {            // 권한에 따른 버튼 설정
        lfn_SetFormAuth(true);           // 저장 권한
    } else if (ms_FormAuth == "R") {
        lfn_SetFormAuth(false);          // 읽기 권한
    } else {
        gfn_Alert(MSGI00080);
        close();
    }

    radPrtGubun.Value="1";
    cbxCourse.index=0;
	// 24.01  직능에서 위험물운송자 완전삭제, 소방기술자는 임시로 안보이기 삭제(추후 만들것임)
	for(var i=ds_ioCourse.GetRowCount()-1; i>0; i--) {
		if ((ds_ioCourse.GetColumn(i, "CD") == "5") ||
		    (ds_ioCourse.GetColumn(i, "CD") == "8"))
		ds_ioCourse.DeleteRow(i);
	}
}


function Form_OnSize(obj,nCx,nCy,nState) {
    if (gs_IsResizingForm == "Y") { }
}

function lfn_SetFormAuth(bStatus) {}



//초기화
function lfn_Cancel(obj) {
    radPrtGubun.Value="1";
    cbxCourse.index=0;
}


/***   기능 : 출력   ***/
function lfn_Print(obj){
	
    lfn_Search();    
    
    course = "";
    if (ds_oDATA.GetCount() < 1) {
        gfn_Alert("출력할 데이터가 없습니다.");
        return false;
    }

    var rpt = "";
    var params = "";
    var params2 = "";
    var sTopMargin = "";
    var sLeftMargin = "";

    rpt = "frmcust6530List_" + cbxCourse.Value + ".rex";
    
    if (cbxCourse.Value == "8") {
		sTopMargin = "100";
		sLeftMargin = "200";
	}
			
	if (radPrtGubun.Value == "1") { 
		params2 = "지부"; 
		
		if (cbxCourse.Value == "1") {
			params = "소방안전관리 대상 안전원/청간 비교 현황(안전원 기준)"; 			
		}
		else if (cbxCourse.Value == "3") {
			params = "위험물안전관리자 대상 안전원/청간 비교 현황(안전원 기준)"; 
		}	
		else if (cbxCourse.Value == "5") {
			params = "소방기술자 대상 안전원/청간 비교 현황(안전원 기준)"; 
		}			
		else if (cbxCourse.Value == "8") {
			params = "위험물운송자 대상 안전원/청간 비교 현황(안전원 기준)"; 
		}					
	}
	else {
		params2 = "본부"; 
		
		if (cbxCourse.Value == "1") {
			params = "소방안전관리 대상 안전원/청간 비교 현황(소방방재청 기준)"; 			
		}	
		else if (cbxCourse.Value == "3") {
			params = "위험물안전관리자 대상 안전원/청간 비교 현황(소방방재청 기준)"; 			
		}			
		else if (cbxCourse.Value == "5") {
			params = "소방기술자 대상 안전원/청간 비교 현황(소방방재청 기준)"; 			
		}
		else if (cbxCourse.Value == "8") {
			params = "위험물운송자 대상 안전원/청간 비교 현황(소방방재청 기준)"; 			
		}		
	}	
	
	params = "rd_TITLE=" + quote(params) + "^s_TITLE=" + quote(params2);
    
	/*
	 * 기능 : 렉스퍼트 보고서를 팝업화면으로 호출한다.
	 * parameter : sReportName - 레포트명(예:레포트.rex)
	 *             sReportDataset - 데이터셋명(예:dsReport1||dsResport2||...)
	 *             sReportParams - 렉스퍼트용 파라메터(예: a에 1, b에 2, c에 3의 값을 넣는 경우, a=1^b=2^c=3...)
	 *             bLcsYn - 수첩여부
	 *             LeftMargin = 왼쪽
	 *             RightMargin = 오른쪽
	 *             TopMargin = 상단
	 *             BottomMargin = 하단
	 * return value : 정상출력 true, 출력 안했으면 false
	 */    
	 
    //gfn_CallReportPopup(rpt, "ds_oDATA", params);
    gfn_CallReportPopup(rpt, "ds_oDATA", params, '', '', sTopMargin, sLeftMargin);
}




function lfn_Search(obj) {
    
    // 조회이력을 저장한다.  
    ls_HistoryArg = "출력물=" + quote(radPrtGubun.Value)
                + ",연계대상=" + quote(cbxCourse.Value);

    gfn_InsertSearchPrintHistory(
        "S"
        , this.ID
        , global.mnuLeft.cbxOpenForm.Text
        , ls_HistoryArg
    );
	
    ds_oDATA.ClearData(); // 데이타셋 초기화
    
    tit_clearActionInfo();
    
    
    var qry = "";

    if (radPrtGubun.Value == "1") {
		qry = "cust:searchCust6530Jibu_" + cbxCourse.Value;
    }
    
	else if (radPrtGubun.Value == "2") {
        qry = "cust:searchCust6530Bonbu_" + cbxCourse.Value;
    }
    
    tit_addCsvSearchActionInfo(qry, false);

	// 서버 호출 
	tit_callService(
         "",""
		,""	// inDs
		,"ds_oDATA=ds_oDATA"	// outDs
		, "MATCHGUBUN="  + quote(radMatchStatus.value)
		, "lfn_AfterSearch"
		, false
		, false  // 처리중 창 보여주기 (null or false : 보여줌) 
		, true);    
}

function lfn_AfterSearch(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);     
        course = "";
        return;
    }   
}


/***  엑셀   ***/
function lfn_Excel(obj) {	
    lfn_ExcelL(obj);
}

function lfn_ExcelL(obj)
{	
    lfn_Search();

	if(ds_oDATA.rowcount == 0){
		alert("데이터가 없습니다.");
		return;
	}

	gdList1.Format = "list_" + cbxCourse.Value;
	
	if (radPrtGubun.Value == "1") { 
		gdList1.SetCellProp("head",1,"text","지부"); 
		
		if (cbxCourse.Value == "1") {
			gdList1.SetCellProp("head",0,"text","소방안전관리 대상 안전원/청간 비교 현황(안전원 기준)"); 
		}
		else if (cbxCourse.Value == "3") {
			gdList1.SetCellProp("head",0,"text","위험물안전관리자 대상 안전원/청간 비교 현황(안전원 기준)"); 
		}	
		else if (cbxCourse.Value == "5") {
			gdList1.SetCellProp("head",0,"text","소방기술자 대상 안전원/청간 비교 현황(안전원 기준)"); 
		}
		else if (cbxCourse.Value == "8") {
			gdList1.SetCellProp("head",0,"text","위험물운송자 대상 안전원/청간 비교 현황(안전원 기준)"); 
		}
	}
	else {
		gdList1.SetCellProp("head",1,"text","본부"); 
		
		if (cbxCourse.Value == "1") {
			gdList1.SetCellProp("head",0,"text","소방안전관리 대상 안전원/청간 비교 현황(소방방재청 기준)"); 		
		}	
		else if (cbxCourse.Value == "3") {
			gdList1.SetCellProp("head",0,"text","위험물안전관리자 대상 안전원/청간 비교 현황(소방방재청 기준)"); 		
		}	
		else if (cbxCourse.Value == "5") {
			gdList1.SetCellProp("head",0,"text","소방기술자 대상 안전원/청간 비교 현황(소방방재청 기준)"); 		
		}			
		else if (cbxCourse.Value == "8") {
			gdList1.SetCellProp("head",0,"text","위험물운송자 대상 안전원/청간 비교 현황(소방방재청 기준)"); 		
		}			
	}
	
	gdList1.ExportExcelEx("sheet1");
}
