Window.Script:
/*

 * 프로그램명 : frmcust0010MCustomerManagement.xml

 * 설명 : 고객정보관리

 * 작성일 : 2009.11.21

 * 작성자 : 김은규

 */



#include "LIB::cust.js"; // 공통업무 관련



var ls_curComponent = "";	//조회시점에서의 포커스된 Component

var bGroupSearch = true;	//그룹조회 팝업 호출 유무(true:호출, false:미호출)


// 청자료 접근권한관련
var PDEPTCD = gds_oUserInfo.GetColumn(0,"PDEPTCD");

var ADMINGUBUN = gds_oUserInfo.GetColumn(0,"ADMINGUBUN");

/* 공통 필수 시작 */

function Form_OnLoadCompleted(obj)

{
	gfn_Form_OnLoadCompletedCheck(obj);	//공통 폼 로딩 이벤트 처리(공통코드 조회되었는지 확인)

	gfn_Form_OnLoadCompleted(obj); // 공통 폼 로딩 이벤트 처리

    // 폼 환경설정

    ls_SystemGubun = gs_SystemGubun;

    ls_SystemMenuId = gs_SystemMenuId;

    var ls_FormId = split(sFormUrl, "::");

    ls_FormId = split(ls_FormId[1], ".");

    obj.Title = sFormTitle + "(" + ls_FormId[0] + ")";



    gfn_MakeRequiredInputMark(this); // 필수 입력란을 설정한다.



    ms_FormAuth = gfn_GetFormAuth(this); // 권한을 조회한다.

    if (ms_FormAuth == "W") { // 권한에 따른 버튼 설정

        lfn_SetFormAuth(true); // 저장 권한

    } else if (ms_FormAuth == "R") {

        lfn_SetFormAuth(false); // 읽기 권한

    } else {

        gfn_Alert(MSGI00080);

        close();

    }



    // 공통 코드 조회

    gfn_SearchAllJibu(cbxJibu, "Y"); 											// 지부 코드 조회

    gfn_SearchCodeRegion(gds_oUserInfo.GetColumn(0, "PDEPTCD"), ds_oRegion, "0");	// 지역 코드 조회

    gfn_SearchCustomerCourseFullName(ds_oCourse, "0");							// 대표고객직능 조회

    gfn_SearchCustomerDetailCourseFullName("", ds_oDetailCourse, "0");			// 세부고객직능 조회

    gfn_SearchCommonCode(ds_oEmailDomain, "EMAILDOMAIN", "Y", "직접입력"); 	// 이메일도메인 조회

    gfn_SearchCommonCode(ds_oAddition, "ADCD", "Y", "선택"); 					// 고객부가정보코드 조회

    gfn_SearchCommonCode(ds_oStatusReason, "STATUSREASONCD", "Y", "선택"); 		// 고객부가정보코드 조회

    gfn_SearchCommonCode(ds_oBusinessGubun, "COCD", "Y", "지정요망"); 			// 소방기술자 업체구분

    gfn_SearchCommonCode(ds_oBizCd, "BIZCD", "Y", "지정요망"); 				// 방관-용도코드

    gfn_SearchCodeRegion(cbxJibu.Value, ds_oRegion, "0");						// 지역 코드 조회

    gfn_SearchCustomerCourseFullName(ds_oCourse, "0");							// 대표고객직능 조회

    gfn_SearchCommonCode(ds_oSunapGubun, "SUNAPGUBUN", "Y", "미납");			// 수납구분

    lfn_searchCust0010LicenseCodeList();										// 공통 코드(자격증 조회)
    

    lfn_ClearData();							//모든데이타셋 초기화

	lfn_AddDataMain(); 							//Main 데이타셋 입력가능상태로 만들기

	lfn_InitButton(false);						//초기화시 버튼(비활성화)

	lfn_InitComboBoxIndex(0);					//콤보박스 index=0으로 초기화

	

	edManagerNm.Readonly = false;				//선임자 성명

	medResidentNo.Readonly = false;				//선임자 주민번호

	lfn_FireManagerNewOldGubun(true);			//선임자 신규상태

    

    //상단 조회 초기값 셋팅

	cbxRegion.Value = ds_oRegion.GetColumn(0, "CD");

	cbxCourse.Value = ds_oCourse.GetColumn(0, "CD");

	cbxCourse_OnChanged(object("cbxCourse"), cbxCourse.Value);

	cbxDetailCourse.Value = ds_oDetailCourse.GetColumn(0, "CD");

    lfn_FilterFireStation(cbxJibu.Value);									//소방서 필터링

    cbxStation_OnChanged(Object("cbxStation"), cbxStation.Value);			//대상물 소방관서 강제 이벤트

    

    /*주소구분*/

	radAddrGubun.Value = 0;								//대상물 주소(구주소)

	tabTab3.tabpgTab3Page1.radPostAddrGubun.Value = 0;	//선임자 주소(구주소)

	cbxAddPost.Value = 0;								//희망주소구분(0:공통,1:개별)

	div0.cbxPostGubun.Value = 2;						//회비희망주소지(0:대상물,1:선임자,2:직접)

	div4.cbxEduPostGubun.Value = 2;						//교육희망주소지(0:대상물,1:선임자,2:직접)

	

    /* 교육/회비에서 링크 왔을시 */

    if (IsExistVar("sJibu") && IsExistVar("sCustNo")) {

        cbxJibu.Value = sJibu;

        medCustNo.Value = sCustNo;

        lfn_searchCust0010CustomerInfo(sJibu, sCustNo);

    } else if (IsExistVar("sCustMgno")) {

        medBarcode.Value = sCustMgno;

        lfn_searchCust0010CustomerInfo2(sCustMgno);

    }
    
    // 청연계관련버튼 권한     
    if (PDEPTCD == "1031" || PDEPTCD == "1041" || PDEPTCD == "1121" || PDEPTCD == "0243") {
		btnFireSearch.Visible = true;
    } else {
		btnFireSearch.Visible = false;
    }
	btnSearchCivilAppeal.Enable = false;
	
    medCustNo.SetFocus();								//지역 콤보 포커스

}



/**

 * 폼 키 다운

 */

function frmcust0010MCustomerManagement_OnKeyDown(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam)

{

	if(nChar == 117) {

        if(bGroupSearch == true) {

			lfn_GroupSearch(obj);

        }

        bGroupSearch = true;

	} else {

		gfn_Form_OnKeyDown(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam);

	}

}



/***************************

 * 공통 코드 조회

 ***************************/

// 자격증/소방서/소방센터 조회

function lfn_searchCust0010LicenseCodeList(obj) {

	

	ds_oLicenseGubun.ClearData(); // 자격증 초기화

	ds_oFireCd.ClearData();	//소방서 초기화

	ds_oFireCenterCd.ClearData();	//소방센터 초기화

	

	ds_oLicenseGubun.Copy("gds_oLicenseCode");

	ds_oFireCd.Copy("gds_oFireCode");

	ds_oFireCenterCd.Copy("gds_oFireCenterCode");

}



/*

 * 기능 : 버튼 활성/비활성시킨다.

 * parameter : bStatus - 활성/비활성여부(true:활성, false:비활성)

 * return : 없음

 */

function lfn_SetFormAuth(bStatus) {

	btnInput.Enable = bStatus; // 추가 버튼

	btnSave.Enable = bStatus; // 수정&저장 버튼

}



/*

 * 조회(F2)

 */

function lfn_Search(obj)

{

	if(gfn_IsNull(medCustNo.Value)) {

		gfn_Alert(lbCustNo.Text + MSGW00010);		//관리번호을(를) 입력해 주십시오.

		medCustNo.SetFocus();

		return;

	}

	lfn_ClearData();

	lfn_searchCust0010CustomerInfo(trim(cbxJibu.Value), trim(medCustNo.Value));	//고객 정보 조회(MAIN 1)

}





/*

 * 그룹조회(F6)

 * parameter : sGubun - 조회 구분

 *             1: 대상물명

 *             2: 주소1

 *             3: 번지

 *             4: 선임자명

 *             5: 선임자주민번호

 *             6: 대상물전화번호

 *             7: 위험물허가번호

 *             8: 이동탱크차량번호

 *             9: 이동탱크허가번호

 *           : sKeyword - 키워드

 */

function lfn_SearchGroupPopup(sJibu, sGubun, sKeyword) {

	var arrGroup = lfn_SearchGroup(sJibu, sGubun, sKeyword);

	

	if(length(arrGroup) > 0) {

		var sJibu = arrGroup[0];	//지부

		var sCustNo = arrGroup[1];	//고객번호

		

		lfn_searchCust0010CustomerInfo(sJibu, sCustNo);	//고객 정보 조회(MAIN 1)

	}

}



/*

 * 추가(F3)

 */

function lfn_Input(obj)

{

	lfn_SetDeleteStatus(true);			//삭제된 정보 상태변경한다.

	lfn_InitButton(false);				//초기화시 버튼(비활성화)

	lfn_ClearData();					//초기화 (모든 데이터셋)

	lfn_AddDataMain();					//Main 데이타셋 입력가능상태로 만들기

	lfn_InputDataSetting();				//디폴트값 셋팅

	lfn_InitComboBoxIndex(9);			//콤보박스 index=0으로 초기화

	lfn_FireManagerNewOldGubun(true);	//선임자 신규상태

	meOldCustNO.Enable = false;			//구시스템번호(비활성화)

	

	/* 2010-01-18 정현호 대리 추가 요청 START */

	if(cbxCourse.Value == "60") {		//개인회원

		lfn_PersonCustomerHidden(true);

	}

	/* 2010-01-18 정현호 대리 추가 요청 END */

}



/*

 * 기능 : 초기화(F10)

 */

function lfn_Cancel(obj) 

{

	lfn_SetDeleteStatus(true);			//삭제된 정보 상태변경한다.

	lfn_InitButton(false);				//초기화시 버튼(비활성화)

    lfn_ClearData();					//초기화 (모든 데이터셋)	

	lfn_AddDataMain();					//Main 데이타셋 입력가능상태로 만들기

	lfn_ClearSearchCompornent();		//상단 조회 초기화

	lfn_InitDataSetting();				//초기화시 데이터 셋팅

	lfn_InitComboBoxIndex(0);			//콤보박스 index=0으로 초기화

	lfn_FireManagerNewOldGubun(true);	//선임자 신규상태

	cbxRegion.SetFocus();				//지역 콤보 포커스

	meOldCustNO.Enable = true;			//구시스템번호(활성화)

	btnCustFee.Visible = false;			//교육통지서 버튼 히든

	btnEduNoti.Visible = false;			//회비고지서 버튼 히든

}



/*

 * 기능 : 초기화시 버튼 셋팅

 */

function lfn_InitButton(bStatus) 

{

	btnSearchManagerList.Enable = bStatus;			//선임현황

	btnSearchChangeH.Enable = bStatus;				//변경이력

	btnEduDetailInfo1.Enable = bStatus;				//회비/교육비 상세정보

	Div3.btnEduDetailInfo2.Enable = bStatus;		//실무교육 상세정보

	Div4.btnSearchEduReturnPostH.Enable = bStatus;	//우편반송이력

	Div0.btnSearchReturnPostH.Enable = bStatus;		//우편반송이력

	btnConcurrentH.Enable = bStatus;				//겸직이력

	btnSearchAddInfoH.Enable = bStatus;				//부가정보이력

	btnSaveAddInfo.Enable = bStatus;				//부가정보

	btnUpdateManagerInfo.Enable = bStatus;			//선임자정보변경

	btnSearchAgency.Enable = bStatus;				//업무대행선택 버튼

	cbxDelYN.Enable = bStatus;						//삭제구분

	calDelDate.Enable = bStatus;					//삭제일자

	cbxStatusReason.Enable = bStatus;				//변경사유

}



/*

 * 기능 : 초기화시 데이터 셋팅

 */

function lfn_InitDataSetting() {

	radJibuGubun.Value = "S";							//소속지부

	radJibuGubun_OnChanged(Object("radJibuGubun"),radJibuGubun.Value);				//소속/타지부 변경 이벤트 강제 발생

	cbxStation_OnChanged(Object("cbxStation"), cbxStation.Value);					//대상물 소방관서 강제 이벤트

	cbxCourse.Enable = true;							//대표직능

	medEmailCnt.Value = 0;								//총미납금액

	radAddrGubun.Value = 0;								//대상물 주소(구주소)

	tabTab3.tabpgTab3Page1.radPostAddrGubun.Value = 0;	//선임자 주소(구주소)

	cbxAddPost.Value = 0;								//희망주소구분(0:공통,1:개별)

	div0.cbxPostGubun.Value = 2;						//회비희망주소지(0:대상물,1:선임자,2:직접)

	div4.cbxEduPostGubun.Value = 2;						//교육희망주소지(0:대상물,1:선임자,2:직접)

	btnSaveAddInfo.Enable = false;						//부가정보(비활성화)

	btnSearchAddInfoH.Enable = false;					//부가정보이력(비활성화)

	edManagerNm.Readonly = false;						//선임자 성명

	medResidentNo.Readonly = false;						//선임자 주민번호

	btnUpdateManagerInfo.Enable = false;				//선임자 정보변경 버튼

	chkSameBuilding.Value = "N";						//동일대상물 계속입력(No)	

}



/*

 * 기능 : 초기화 (모든 데이터셋)

 */

function lfn_ClearData() {

	//Dataset Clear

	ds_ioCustomerInfo.ClearData(); 		// 고객 정보(MAIN 1)

	ds_ioBuildingInfo.ClearData(); 		// 대상물 정보(MAIN 2)

	ds_ioManagerInfo.ClearData(); 		// 선임자 정보(MAIN 3)

	ds_ioAdditionInfo.ClearData();		// 고객 부가 정보(SUB 1)

	

	ds_ioOwnerInfo.ClearData();			// 관계자 정보

	ds_ioDangerInfo.ClearData();		// 위험물부가정보

	ds_ioTankInfo.ClearData();			// 이동탱크부가정보

	

	ds_ioCustomerFeeInfo.ClearData();	// 회비/교육비 정보

	ds_ioEducationPassInfo.ClearData();	// 실무교육 정보

	

	ds_ioOverBizInfo.ClearData();		// 겸직 정보

	ds_ioLicenseInfo.ClearData();		// 보유 자격 정보(타자격)

	ds_oLicenseInfo.ClearData();		// 보유 자격 정보(안전원자격)

	

	medCancelDate.Value = "";			// 선임자 해임처리시스템일시

	tabTab3.tabpgTab3Page1.imgPicture.ImageID = "";		//선임자 사진 초기화

    tabTab3.tabpgTab3Page1.imgPicture.Text = "사진";	//선임자 사진 초기화

}



/*

 * 기능 : Main 데이타셋 입력가능상태로 만들기

 */

function lfn_AddDataMain() {

	ds_ioCustomerInfo.InsertRow(0); 		// 고객 정보(MAIN 1)

	ds_ioBuildingInfo.InsertRow(0); 		// 대상물 정보(MAIN 2)

	ds_ioManagerInfo.InsertRow(0);			// 선임자 정보(MAIN 3)

	ds_ioAdditionInfo.InsertRow(0);			// 고객 부가 정보(SUB 1)

}



/*

 * 기능 : 상단 조회 초기화

 */

function lfn_ClearSearchCompornent() {

	medCustNo.Value = "";

	medBarCode.Value = "";

	cbxRegion.Value = "";

	cbxCourse.Value = "";

	cbxDetailCourse.Value = "";

}



/*

 * 기능 : 삭제된 정보 상태변경한다.

 * parameter : bStatus - 활성/비활성여부(true:활성, false:비활성)

 * return : 없음

 */

function lfn_SetDeleteStatus(bStatus) {

    if (bStatus) {

        Shape0.BkColor = "";

        Shape2.BkColor = "";

        Shape4.BkColor = "";

    } else {

		Shape0.BkColor = "user18";

        Shape2.BkColor = "user18";

        Shape4.BkColor = "user18";

    }

	

	/* 대상물 정보 */

    lfn_BuildingInfoStatus(bStatus);

    

    /* 고객정보 */

    meJoinYear.Enable = bStatus;

    meFeeYear.Enable = bStatus;

    cbxStatus.Enable = bStatus;

    medMemStatusDate.Enable = bStatus;

    cbxAddPost.Enable = bStatus;

    lfn_ObjComponentStatus(5, bStatus);	//Div4.Enable = bStatus;

    lfn_ObjComponentStatus(6, bStatus);	//Div0.Enable = bStatus;

    cbxPersonGubun.Enable = bStatus;

    cbxExclusionYN.Enable = bStatus;

    calExclusionDate.Enable = bStatus;

    calDelDate.Enable = bStatus;

    cbxAgencyYn.Enable = bStatus;

    btnSearchAgency.Enable = bStatus;

    cbxStatusReason.Enable = bStatus;

    cbxAddGubun.Enable = bStatus;

    btnSaveAddInfo.Enable = bStatus;

    edAddContent.Enable = bStatus;

    edFireDept.Enable = bStatus;

    

    /* 선임자 정보 */

    edManagerNm.Enable = bStatus;

    btnUpdateManagerInfo.Enable = bStatus;

    btn_SearchFireManager.Enable = bStatus;

    calAppointDate.Enable = bStatus;

    medResidentNo.Enable = bStatus;

    btnAuthResidentNo.Enable = bStatus;

    lfn_ObjComponentStatus(7, bStatus);	//tabTab3.Enable = bStatus;

}



function lfn_ObjComponentStatus(sObj, bStatus) {

	switch(sObj)

	{

		case 0 : 	//제연설비 DIV1

			Div1.cbxEquipmentYn.Enable = bStatus;

			Div1.medArea.Enable = bStatus;

		break;

		case 1 : 	//사업자등록번호 DIV2

			Div2.medCompanyNo.Enable = bStatus;

			Div2.cbxBizGubun.Enable = bStatus;

		break;

		case 2 : 	//관계자정보+위험물부가정보 DIV6

			Div6.btnAddRow1.Enable = bStatus;

			Div6.btnDelRow1.Enable = bStatus;

			Div6.tabTab1.tabpgTab1Page1.gdConnections.Editable = bStatus;

			Div6.tabTab1.tabpgTab1Page2.gdDangerInfo.Editable = bStatus;

		break;

		case 3 : 	//관계자정보+이동탱크부가정보 DIV7

			Div7.btnAddRow1.Enable = bStatus;

			Div7.btnDelRow1.Enable = bStatus;

			Div7.tabTab1.tabpgTab1Page1.gdConnections.Editable = bStatus;

			Div7.tabTab1.tabpgTab1Page3.gdTankInfo.Editable = bStatus;

		break;

		case 4 : 	//관계자정보 DIV8

			Div8.btnAddRow1.Enable = bStatus;

			Div8.btnDelRow1.Enable = bStatus;

			Div8.tabTab1.tabpgTab1Page1.gdConnections.Editable = bStatus;

		break;

		case 5 : 	//공통(교육통지서) 주소 DIV4

			Div4.edEduPost1.Enable = bStatus;

			Div4.edEduPost2.Enable = bStatus;

			Div4.medEduPostZipCode.Enable = bStatus;

			Div4.btnSearchEduPostZipCode.Enable = bStatus;

			Div4.cbxEduPostGubun.Enable = bStatus;

			Div4.edEduPostPlace.Enable = bStatus;

		break;

		case 6 : 	//회비고지서 주소 DIV0

			Div0.edPost1.Enable = bStatus;

			Div0.edPost2.Enable = bStatus;

			Div0.medPostZipCode.Enable = bStatus;

			Div0.btnSearchPostZipCode.Enable = bStatus;

			Div0.cbxPostGubun.Enable = bStatus;

			Div0.edPostPlace.Enable = bStatus;

		break;

		case 7 : 	//선임자 정보 tabTab3

			tabTab3.tabpgTab3Page1.edPTel.Enable = bStatus;

			tabTab3.tabpgTab3Page1.edTel.Enable = bStatus;

			tabTab3.tabpgTab3Page1.edEMailID.Enable = bStatus;

			tabTab3.tabpgTab3Page1.edEmailDomain.Enable = bStatus;

			tabTab3.tabpgTab3Page1.cbxEMailDomain.Enable = bStatus;

			tabTab3.tabpgTab3Page1.edOldPostAddr1.Enable = bStatus;

			tabTab3.tabpgTab3Page1.edOldPostAddr2.Enable = bStatus;

			tabTab3.tabpgTab3Page1.medOldPostZipCode.Enable = bStatus;

			tabTab3.tabpgTab3Page1.btnSearchOldPostZipCode.Enable = bStatus;

			/* 신주소 미구현

			tabTab3.tabpgTab3Page1.edNewPostAddr1.Enable = bStatus;

			tabTab3.tabpgTab3Page1.edNewPostAddr2.Enable = bStatus;

			tabTab3.tabpgTab3Page1.medNewPostZipCode.Enable = bStatus;

			tabTab3.tabpgTab3Page1.btnSearchNewPostZipCode.Enable = bStatus;

			tabTab3.tabpgTab3Page1.radPostAddrGubun.Enable = bStatus;

			*/

			tabTab3.tabpgTab3Page3.btnAddRow.Enable = bStatus;

			tabTab3.tabpgTab3Page3.btnDelRow.Enable = bStatus;

			tabTab3.tabpgTab3Page3.gdLicenseInfo.Enable = bStatus;

		break;

	}

}



