    radAddrGubun.Enable = bStatus;

    edNewAddr1.Enable = bStatus;

    edNewAddr2.Enable = bStatus;

    btnSearchNewZipCode.Enable = bStatus;

    */

    edTelNo.Enable = bStatus;

    edFaxNo.Enable = bStatus;

}



