    edTelNo.Enable = bStatus;

    edFaxNo.Enable = bStatus;

    lfn_ObjComponentStatus(1, bStatus);	//div2.Enable = bStatus;

    lfn_ObjComponentStatus(0, bStatus);	//div1.Enable = bStatus;

    lfn_ObjComponentStatus(2, bStatus);	//div6.Enable = bStatus;

    lfn_ObjComponentStatus(3, bStatus);	//div7.Enable = bStatus;

    lfn_ObjComponentStatus(4, bStatus);	//div8.Enable = bStatus;

}



/*

 * 콤보박스 Null일때 index=0으로 초기화

 */

function lfn_InitComboBoxIndex(sGubun) {

	switch(sGubun)

	{

		case 0 :	//고객 컨퍼넌트

			if(gfn_IsNull(ds_ioCustomerInfo.GetColumn(ds_ioCustomerInfo.currow, "CSTATUSREASONCD"))) cbxStatusReason.Index = 0;	//변경사유

			if(gfn_IsNull(ds_ioCustomerInfo.GetColumn(ds_ioCustomerInfo.currow, "CDELGUBUN"))) cbxDelYN.Index = 0;				//삭제구분

		break;

		

		case 1 :	//대상물 컨퍼넌트

			if(gfn_IsNull(ds_ioBuildingInfo.GetColumn(ds_ioBuildingInfo.currow, "CCOCD"))) div2.cbxBizGubun.Index = 0;	//업체구분

			if(gfn_IsNull(ds_ioBuildingInfo.GetColumn(ds_ioBuildingInfo.currow, "BBIZCD"))) cbxBusiness.Index = 0;		//용도

		break;

		case 2 :	//선임자 컨퍼넌트

			if(gfn_IsNull(ds_ioManagerInfo.GetColumn(ds_ioManagerInfo.currow, "FMHEMAILDOMAIN"))) tabTab3.tabpgTab3Page1.cbxEMailDomain.Index = 0;	//선임자 메일도메인

		break;

		

		case 3 :	//고객 부가정보

			if(gfn_IsNull(ds_ioAdditionInfo.GetColumn(ds_ioAdditionInfo.currow, "ADCD"))) cbxAddGubun.Index = 0;	//부가정보구분

		break;

		

		default :	//모든 컨퍼넌트(초기화/추가 버튼 클릭시)

			if(gfn_IsNull(ds_ioCustomerInfo.GetColumn(ds_ioCustomerInfo.currow, "CSTATUSREASONCD"))) cbxStatusReason.Index = 0;	//변경사유

			if(gfn_IsNull(ds_ioCustomerInfo.GetColumn(ds_ioCustomerInfo.currow, "CDELGUBUN"))) cbxDelYN.Index = 0;				//삭제구분

			if(gfn_IsNull(ds_ioBuildingInfo.GetColumn(ds_ioBuildingInfo.currow, "CCOCD"))) div2.cbxBizGubun.Index = 0;	//업체구분

			if(gfn_IsNull(ds_ioBuildingInfo.GetColumn(ds_ioBuildingInfo.currow, "BBIZCD"))) cbxBusiness.Index = 0;		//용도

			if(gfn_IsNull(ds_ioAdditionInfo.GetColumn(ds_ioAdditionInfo.currow, "ADCD"))) cbxAddGubun.Index = 0;	//부가정보구분

			if(gfn_IsNull(ds_ioManagerInfo.GetColumn(ds_ioManagerInfo.currow, "FMHEMAILDOMAIN"))) tabTab3.tabpgTab3Page1.cbxEMailDomain.Index = 0;	//선임자 메일도메인

		break;

	}	

}



/*

 * 세부직능 필터링

 */

function lfn_FilterDetailCourse(sFilterGubun) {

	ds_oDetailCourse.UnFilter();

		

	var ls_FilterExpr = "";

    if (!gfn_IsNull(sFilterGubun)) {

        ls_FilterExpr += "GCD=='" + trim(sFilterGubun) + "'";

    }

    

    ds_oDetailCourse.Filter(ls_FilterExpr);

    

	if( ds_oDetailCourse.FindRow("DATA", cbxDetailCourse.Text) == -1) {

		cbxDetailCourse.Text = "";

    }

}



/*

 * 인력구분 필터링

 */

function lfn_FilterPersonGubun(sFilterGubun) {

	

	ds_oPersonGubun.UnFilter();

	var fGubun;		//필터구분(직능)

	lbSection.Visible = true;

	cbxPersonGubun.Visible = true;

	switch(sFilterGubun)

	{

		//공공기관

		case 13 :

			fGubun = "10";

		break;

		case 14 :

			fGubun = "10";

		break;

		//위험물

		case 30 :

			fGubun = "30";

		break;

		case 31 :

			fGubun = "30";

		break;

		case 32 :

			fGubun = "30";

		break;

		case 33 :

			fGubun = "30";

		break;

		case 34 :

			fGubun = "30";

		break;

		case 35 :

			fGubun = "30";

		break;

		case 36 :

			fGubun = "30";

		break;

		//소방기술자

		case 50 :

			fGubun = "50";

		break;

		case 51 :

			fGubun = "50";

		break;

		case 52 :

			fGubun = "50";

		break;

		case 53 :

			fGubun = "50";

		break;

		case 54 :

			fGubun = "50";

		break;

		case 55 :

			fGubun = "50";

		break;

		default :	//인력구분에 속하지 않는 직능

			lbSection.Visible = false;

			cbxPersonGubun.Visible = false;

			cbxPersonGubun.Value = "";

			cbxPersonGubun.Text = "";

		break;

	}

	

	var ls_FilterExpr = "";

    if (!gfn_IsNull(fGubun)) {

        ls_FilterExpr += "PCD=='" + trim(fGubun) + "'";

    }

    

    ds_oPersonGubun.Filter(ls_FilterExpr);

    

	if(ds_oPersonGubun.FindRow("DATA", cbxPersonGubun.Text) == -1) {

		cbxPersonGubun.Text = "";

    }

    

	//신규입력시 인력구분 디폴트 셋팅

    if( fGubun == "10" || fGubun == "30" || fGubun == "50" ) {

		cbxPersonGubun.Index = 0;

	} else {

		cbxPersonGubun.Index = -1;

	}

}



/*

 *  조회 후 상단 조회 내역 변경

 */

function lfn_AfterSearchComponentChange() {



	cbxJibu.Value = ds_ioCustomerInfo.GetColumn(ds_ioCustomerInfo.currow, "CGTMGNO");			//지부

	gfn_SearchCodeRegion(cbxJibu.Value, ds_oRegion, "0");										//지역 코드 조회



	if( gds_oUserInfo.GetColumn(0, "PDEPTCD") == ds_ioCustomerInfo.GetColumn(0, "CGTMGNO") ) {	//소속지부

		radJibuGubun.Value = "S";

		radJibuGubun.BkColor = "";

        cbxJibu.Enable = false;

	} else {	//타지부

		radJibuGubun.Value = "T";

		radJibuGubun.BkColor = "red";

        cbxJibu.Enable = true;

	}

	var sPersonGubun = ds_ioCustomerInfo.GetColumn(ds_ioCustomerInfo.currow, "CPERSONGUBUN");			//인력구분

	

	medBarCode.Value = lpad(ds_ioCustomerInfo.GetColumn(ds_ioCustomerInfo.currow, "CMGNO"), "0", 13);	//고객관리번호

	cbxRegion.Value = ds_ioCustomerInfo.GetColumn(ds_ioCustomerInfo.currow, "CREGCD");					//지역

	medCustNo.Value = lpad(ds_ioCustomerInfo.GetColumn(ds_ioCustomerInfo.currow, "CNO"), "0", 6);		//고객번호

	cbxCourse.Value = ds_ioCustomerInfo.GetColumn(ds_ioCustomerInfo.currow, "CGCOURSECD");				//대표직능

	cbxCourse_OnChanged(object("cbxCourse"), cbxCourse.Value);											//대표직능 변경 이벤트 강제 발생

	cbxDetailCourse.Value = ds_ioCustomerInfo.GetColumn(ds_ioCustomerInfo.currow, "CCOURSECD");			//세부직능

	lfn_FilterDetailCourse(ds_ioCustomerInfo.GetColumn(0, "CGCOURSECD"));								//세부고객직능 필터링

	lfn_FilterPersonGubun(ds_ioCustomerInfo.GetColumn(0, "CCOURSECD"));									//인력구분

	cbxPersonGubun.Value = sPersonGubun;

	//cbxAddPost_OnChanged(Object("cbxAddPost"),cbxAddPost.Value);										//우편물청구구분

	lfn_AfterSearchcbxAddPost_OnChanged();																//우편물청구구분

	cbxAgencyYn_OnChanged(Object("cbxAgencyYn"), cbxAgencyYn.Value);									//업무대행여부

	

	lfn_BuildingAddInfoHidden();		//대상물 부가정보 히든처리

	btnSaveAddInfo.Enable = true;		//부가정보(활성화)

	btnSearchAddInfoH.Enable = true;	//부가정보이력(활성화)

	cbxCourse.Enable = false;			//대표직능

	btnSearchBuilding.Enable = false;	//대상물찾기 버튼 비활성화

	lfn_InitComboBoxIndex(0);			//고객 콤보 Null일때 index=0으로 초기화

	

	ds_ioCustomerInfo.ApplyChange();	//고객 데이타셋 Normal 상태로 변경

	lfn_FireManagerNewOldGubun(true);	//선임자 신규상태



	ds_iCustomerInfoTmp.ClearData();	//고객정보임시데이타셋 초기화

	ds_iCustomerInfoTmp.InsertRow(0);

	ds_ioCustomerInfo.CopyToRow(ds_ioCustomerInfo.currow, "ds_iCustomerInfoTmp", 0);	//고객정보 임시보관

	meOldCustNO.Enable = true;			//구시스템번호(활성화)

	

	if(ds_ioCustomerInfo.GetColumn(0, "CFEEPOSTGUBUN") == "0" || ds_ioCustomerInfo.GetColumn(0, "CFEEPOSTGUBUN") == "1") {	//회비

		lfn_ObjComponentStatus(6, false);	//고객 희망 주소 Disable

		div0.cbxPostGubun.Enable = true;	//회비 희망 주소 선택

		div0.edPostPlace.Enable = true;		//회비 투입장소

	} else {

		lfn_ObjComponentStatus(6, true);	//고객 희망 주소 Disable

	}

	

	if(ds_ioCustomerInfo.GetColumn(0, "CEDUPOSTGUBUN") == "0" || ds_ioCustomerInfo.GetColumn(0, "CFEEPOSTGUBUN") == "1") {	//교육통지

		lfn_ObjComponentStatus(5, false);	//고객 희망 주소 Disable

		div4.cbxEduPostGubun.Enable = true;	//공통(교육) 희망 주소 선택

		div4.edEduPostPlace.Enable = true;	//공통(교육) 투입장소

	} else {

		lfn_ObjComponentStatus(5, true);	//고객 희망 주소 Disable

	}

	

	/* 2010-01-13 정현호 대리 추가 요청 START */

	cbxAddGubun.Enable = false;		//부가정보 구분

	edAddContent.Enable = false;	//부가정보 내용

	/* 2010-01-13 정현호 대리 추가 요청 END */

}



/*

 * 그룹조회(F6)

 */

function lfn_GroupSearch(obj)

{

	var args = "sGtmgno="+quote(cbxJibu.Value);

	var arrNo = Dialog(ls_Prefix + "::frmcust0001PSearchGroup.xml", args, 800, 402, "", -1, -1);

	

	if(length(arrNo) > 0) {

		lfn_searchCust0010CustomerInfo(arrNo[0], arrNo[1]);	//고객 정보 조회(MAIN 1)

	}

	bGroupSearch = true;

}



/*

 * 저장(F4)

 */

function lfn_Save(obj)

{	

	if(gfn_IsNull(cbxJibu.Value)) {

		gfn_Alert(lbJibu.Text + MSGW00010);			//지부을(를) 입력해 주십시오.

		cbxJibu.SetFocus();

		return;

	}

	if(gfn_IsNull(cbxRegion.Value)) {

		gfn_Alert(lbLocal.Text + MSGW00010);		//지역을(를) 입력해 주십시오.

		cbxRegion.SetFocus();

		return;

	}

	if(gfn_IsNull(cbxDetailCourse.Value)) {

		gfn_Alert(lbDetailCourse.Text + MSGW00010);	//세부직능을(를) 입력해 주십시오.

		cbxDetailCourse.SetFocus();

		return;

	}

	

	if( (ds_ioBuildingInfo.GetRowType(0) == 'normal') 

		&& (ds_ioCustomerInfo.GetRowType(0) == 'normal')

		&& (ds_ioManagerInfo.GetRowType(0) == 'normal')

		&& (ds_ioOwnerInfo.GetUpdate() == false)

		&& (ds_ioDangerInfo.GetUpdate() == false)

		&& (ds_ioTankInfo.GetUpdate() == false)

		&& (ds_ioAdditionInfo.GetRowType(0) == 'normal' || (ds_ioAdditionInfo.GetRowType(0) == 'insert' && gfn_IsNull(cbxAddGubun.Value) && gfn_IsNull(edAddContent.Text)))
		&& (ds_ioLicenseInfo.GetUpdate() == false)

		&& (ds_ioCustomerInfo.GetOrgColumn(ds_ioCustomerInfo.currow, "CREGCD") == trim(cbxRegion.Value))

		&& (ds_ioCustomerInfo.GetOrgColumn(ds_ioCustomerInfo.currow, "CCOURSECD") == trim(cbxDetailCourse.Value)) ) {

		gfn_Alert(MSGI00040);	//변경사항이 없습니다.

		return;

	}

		

	if( gfn_IsNull(edBuildingNm.Text) ) {

		//회비

		gfn_Alert(lbBuildingNm.Text + MSGW00010);	//대상물명을(를) 입력해 주십시오.

		edBuildingNm.SetFocus();

		return;

	}

	

	/* 인력구분 Validation Check */

	switch(cbxCourse.Value) 

	{

		case 10 :	//방화관리자

			if(gfn_IsNull(cbxBusiness.Value)) {

				gfn_Alert(lbBusiness.Text + MSGW00010);	

				cbxBusiness.SetFocus();

				return;

			}

		break;

		case 30 :	//위험물안전관리자

			if(gfn_IsNull(cbxPersonGubun.Value)) {

				gfn_Alert(lbSection.Text + MSGW00010);	//인력구분을(를) 입력해 주십시오.

				cbxPersonGubun.SetFocus();

				return;

			}

		break;

		case 50 :	//소방기술자

			if(gfn_IsNull(cbxPersonGubun.Value)) {

				gfn_Alert(lbSection.Text + MSGW00010);	//인력구분을(를) 입력해 주십시오.

				cbxPersonGubun.SetFocus();

				return;

			}

		break;

	}

	

	switch(cbxDetailCourse.Value) 

	{

		case 13 :	//공공기관1급방화관리자

			if(gfn_IsNull(cbxPersonGubun.Value)) {

				gfn_Alert(lbSection.Text + MSGW00010);	//인력구분을(를) 입력해 주십시오.

				cbxPersonGubun.SetFocus();

				return;

			}

		break;

		case 14 :	//공공기관1급방화관리자

			if(gfn_IsNull(cbxPersonGubun.Value)) {

				gfn_Alert(lbSection.Text + MSGW00010);	//인력구분을(를) 입력해 주십시오.

				cbxPersonGubun.SetFocus();

				return;

			}

		break;

	}

	

	/* 대상물/고객 주소 Validation Check */

	if( left(cbxCourse.Value,1) <> "8") {	//위험물운송자

	

		// 대상물 주소

		if(	gfn_IsNull(edOldAddr1.Text) || gfn_IsNull(edOldAddr2.Text) || gfn_IsNull(medOldZipCode.Value) ) {

			//회비

			gfn_Alert("대상물 대표주소는 필수입력항목 입니다.");

			edOldAddr1.SetFocus();

			return;

		}

		

		// 고객 주소

		if(	gfn_IsNull(Div0.edPost1.Text) || gfn_IsNull(Div0.edPost2.Text) || gfn_IsNull(Div0.medPostZipCode.Value) ) {

			//회비

			gfn_Alert("회비청구지는 필수입력항목 입니다.");

			Div0.edPost1.SetFocus();

			return;

		}

		

		if(	gfn_IsNull(Div4.edEduPost1.Text) || gfn_IsNull(Div4.edEduPost2.Text) || gfn_IsNull(Div4.medEduPostZipCode.Value) ) {

			//교육

			gfn_Alert("청구지는 필수입력항목 입니다.");

			Div4.edEduPost1.SetFocus();

			return;

		}

	}



	/* 대상물 부가정보 Validation Check */

	if(div6.Visible = true) {

		///////gfn_Alert("○○○○○○1○○○○○○");

		for(var i=0; i<ds_ioOwnerInfo.GetRowCount(); i++) {	//관계자 필수입력 체크(성명)

			if( ds_ioOwnerInfo.GetRowType(i) <> "normal" ) {	//수정되었고

				if( gfn_IsNull(ds_ioOwnerInfo.GetColumn(i, "ONM")) ) {

					gfn_Alert("[관계자정보] 성명은(는) 필수입력항목 입니다.");

					div6.tabTab1.tabpgTab1Page1.gdConnections.SetFocus();

					return;

				}

			}

		}

		for(var i=0; i<ds_ioDangerInfo.GetRowCount(); i++) {	//위험물 필수입력 체크(허가번호)

			if( ds_ioDangerInfo.GetRowType(i) <> "normal" ) {	//수정되었고

				if( gfn_IsNull(ds_ioDangerInfo.GetColumn(i, "DPERMISSIONNO")) ) {

					gfn_Alert("[위험물부가정보] 허가번호은(는) 필수입력항목 입니다.");

					div6.tabTab1.tabpgTab1Page2.gdDangerInfo.SetFocus();

					return;

				}

			}

		}

		ds_ioTankInfo.ClearData();

		ds_ioTankInfo.ApplyChange();

	} else if(div7.Visible = true) {

	///////gfn_Alert("○○○○○○2○○○○○○");

		for(var i=0; i<ds_ioOwnerInfo.GetRowCount(); i++) {	//관계자 필수입력 체크(성명)

			if( ds_ioOwnerInfo.GetRowType(i) <> "normal" ) {	//수정되었고

				if( gfn_IsNull(ds_ioOwnerInfo.GetColumn(i, "ONM")) ) {

					gfn_Alert("[관계자정보] 성명은(는) 필수입력항목 입니다.");

					div7.tabTab1.tabpgTab1Page1.gdConnections.SetFocus();

					return;

				}

			}

		}

		for(var i=0; i<ds_ioTankInfo.GetRowCount(); i++) {	//이동탱크 필수입력 체크(차량번호)

			if( ds_ioTankInfo.GetRowType(i) <> "normal" ) {	//수정되었고

				if( gfn_IsNull(ds_ioTankInfo.GetColumn(i, "TCARNO")) ) {

					gfn_Alert("[이동탱크부가정보] 차량번호은(는) 필수입력항목 입니다.");

					div7.tabTab1.tabpgTab1Page2.gdTankInfo.SetFocus();

					return;

				}

			}

		}

		ds_ioDangerInfo.ClearData();

		ds_ioDangerInfo.ApplyChange();

	} else if(div8.Visible = true) {

	///////gfn_Alert("○○○○○○3○○○○○○");

		for(var i=0; i<ds_ioOwnerInfo.GetRowCount(); i++) {	//관계자 필수입력 체크(성명)

			if( ds_ioOwnerInfo.GetRowType(i) <> "normal" ) {	//수정되었고

				if( gfn_IsNull(ds_ioOwnerInfo.GetColumn(i, "ONM")) ) {

					gfn_Alert("[관계자정보] 성명은(는) 필수입력항목 입니다.");

					div8.tabTab1.tabpgTab1Page1.gdConnections.SetFocus();

					return;

				}

			}

		}

		ds_ioDangerInfo.ClearData();

		ds_ioTankInfo.ClearData();

		ds_ioDangerInfo.ApplyChange();

		ds_ioTankInfo.ApplyChange();

	}

	

	if(gfn_IsNull(meJoinYear.Value)) {

		gfn_Alert(lbJoinYear.Text + MSGW00010);	//등록년월을(를) 입력해 주십시오.

		meJoinYear.SetFocus();

		return;

	}

	

	if(gfn_IsNull(meFeeYear.Value)) {

		gfn_Alert(lbFeeYear.Text + MSGW00010);	//회비부과년월을(를) 입력해 주십시오.

		meFeeYear.SetFocus();

		return;

	}

	

	if( ds_ioCustomerInfo.GetRowType(ds_ioCustomerInfo.currow) == "update" ) {

		if( ds_ioCustomerInfo.GetOrgColumn(ds_ioCustomerInfo.currow, "CSTATUSGUBUN")

				<> ds_ioCustomerInfo.GetColumn(ds_ioCustomerInfo.currow, "CSTATUSGUBUN") ) {	//고객상태가 변경되었을때

			if( gfn_IsNull(ds_ioCustomerInfo.GetColumn(ds_ioCustomerInfo.currow, "CSTATUSREASONCD")) ) {	//변경사유가 없다면

				gfn_Alert("고객상태변경에 따른 변경사유를 선택해 주십시오.");

				cbxStatusReason.SetFocus();

				return;

			}

		}

	}

	

	if(ds_ioAdditionInfo.GetRowType(0) == "insert") {

		if( gfn_IsNull(cbxAddGubun.Value) && !gfn_IsNull(edAddContent.Text) ) {

			gfn_Alert("부가정보내용을 저장하려면 부가정보구분을 선택해 주십시오.");

			cbxAddGubun.SetFocus();

			return;

		}

	}

	if(gfn_IsNull(edManagerNm.Text)) {

		gfn_Alert("선임자"+lbAppointNm.Text + MSGW00010);	//선임자 성명을(를) 입력해 주십시오.

		edManagerNm.SetFocus();

		return;

	}
	
	if(gfn_IsNull(medResidentNo.Value)) {

		gfn_Alert("선임자"+lbResidentNo.Text + MSGW00010);	//선임자 주민번호을(를) 입력해 주십시오.

		medResidentNo.SetFocus();

		return;

	}

	if(ds_ioManagerInfo.GetRowCount() > 0) {	//선임자가 존재한다면

		if(ds_ioManagerInfo.GetUpdate()) {	//수정되었다면(I,U,D)

			if( ds_ioManagerInfo.GetRowType(0) == "insert" ) {	//신규 입력시

				if( gfn_IsNull(edManagerNm.Text) && gfn_IsNull(medResidentNo.Value) ) {	//선임자 Normal인 상태(선임자는 Transation에 태우지 않음)

					ds_ioManagerInfo.SetColumn(0, "FMRESIDENTNO", "0000000000000");

					//ds_ioManagerInfo.ApplyChange();

				} else {	//선임자를 Transation에 태움

					if (gfn_IsNull(calAppointDate.Value)) {	//선임일자가 없는 경우

						gfn_Alert("선임일자은(는) 필수입력항목 입니다.");

						calAppointDate.SetFocus();

						return;

					} else {	//선임일자가 있는 경우

						

					}

				}

			} else if (ds_ioManagerInfo.GetRowType(0) == "update") {

				if( gfn_IsNull(edManagerNm.Text) || gfn_IsNull(medResidentNo.Value) ) {

					gfn_Alert("선임자의 성명,주민번호 둘중 하나는 필수입력항목 입니다.");

					return;

				} else {

					if (gfn_IsNull(calAppointDate.Value)) {	//선임일자가 없는 경우

						gfn_Alert("선임일자는 필수입력항목 입니다.");

						calAppointDate.SetFocus();

						return;

					} else {	//선임일자가 있는 경우

						

					}

				}

			}

		} else {	//선임자 정보는 수정되지않음

			

		}

		

		if(ds_ioLicenseInfo.GetUpdate()) {	//보유자격 정보가 수정 되었으면

			for(var i=0; i<ds_ioLicenseInfo.GetRowCount(); i++) {	//자격증정보 필수입력 체크(자격증명)

				if( ds_ioLicenseInfo.GetRowType(i) <> "normal" ) {	//수정되었고

					if( gfn_IsNull(ds_ioLicenseInfo.GetColumn(i, "OLHCD")) ) {

						gfn_Alert("[보유자격] 자격증명은(는) 필수입력항목 입니다.");

						tabTab3.tabpgTab3Page3.gdLicenseInfo.SetFocus();

						return;

					}

				}

			}

		}

	}
	
	if(!gfn_Confirm(MSGW00060)) {	//저장 하시겠습니까?

		return;

	}

	

	/* 2010-01-25 정현호 대리 추가 요청 START */

	// 대상물 주소 변경시 주소1,성명,주민번호 가 동일한 경우 겸직이 안된 경우 겸직처리하기

	if(ds_ioBuildingInfo.GetRowType(0) == "update") {

		if( ds_ioBuildingInfo.GetOrgColumn(0, "BADDR1") <> ds_ioBuildingInfo.GetColumn(0, "BADDR1") 

		|| ds_ioBuildingInfo.GetOrgColumn(0, "BADDR2") <> ds_ioBuildingInfo.GetColumn(0, "BADDR2") 

		|| ds_ioBuildingInfo.GetOrgColumn(0, "BZIPCD") <> ds_ioBuildingInfo.GetColumn(0, "BZIPCD") ) {	//대상물 주소가 변경되었을때

			lfn_FindNotOverBizFireManagerList();

		}

	}

	/* 2010-01-25 정현호 대리 추가 요청 END */

	

	lfn_SaveCaseCombination();

}



/*

 * 조회 : MAIN 

 *        고객 정보(MAIN 1) - 지부, 고객번호

 */

function lfn_searchCust0010CustomerInfo(sJibu, sCustNo) {

	

	var sJibuCd = sJibu;

	var sCustNoCd = sCustNo;

	

	if(gfn_IsNull(sJibu) && gfn_IsNull(sCustNo)) {	//그룹조회

		sJibuCd = trim(cbxJibu.Value);

		sCustNoCd = trim(medCustNo.Value);

	}

	

	var sArg = "CNO=" + quote(sCustNoCd);	// 고객번호

    sArg += " CGTMGNO="	+ quote(sJibuCd); 	// 지부코드

	

	lfn_ClearData();	//데이타셋 초기화(모두)

	

	lfn_SearchAllInfo(sArg);
	

}



/*

 * 조회 : MAIN (고객바코드에 의한 조회)

 *        고객 정보(MAIN 1) - 고객관리번호

 */

function lfn_searchCust0010CustomerInfo2(sCmgno) {

	

	var sCmgnoCd = sCmgno;	

	

	if(gfn_IsNull(sCmgno)) {	//그룹조회

		sCmgnoCd = trim(medBarCode.Value);

	}

	

	var sArg = "CMGNO="	+ quote(sCmgnoCd);	// 고객관리번호

	

	lfn_ClearData();	//데이타셋 초기화(모두)

	

	lfn_SearchAllInfo(sArg);

}



/*

 * 조회 : MAIN (소방본부연계에 의한 조회)

 *        고객 정보(MAIN 1) - 소방본부연계관리번호

 */

function lfn_searchCust0010CustomerInfo3(sCfmgno) {

	

	var sCfmgnoCd = sCfmgno;	

	

	if(gfn_IsNull(sCfmgno)) {	//그룹조회

		gfn_Alert("잘못된 조회입니다.");

		return;

	}

	

	var sArg = "CFIREMGNO="	+ quote(sCfmgnoCd);	// 고객관리번호

    

	lfn_ClearData();	//데이타셋 초기화(모두)

    

    lfn_SearchAllInfo(sArg);

}



/*

 * 조회 : MAIN (과거관리번호에 의한 조회)

 *        고객 정보(MAIN 1) - 과거관리번호

 */

function lfn_searchCust0010CustomerInfo4(sJibu, sOldMgno, sNextYn) {

	

	var sJibuCd = sJibu;

	var sOldMgnoCd = sOldMgno;

	var sNextYnCd = sNextYn;

	

	if(gfn_IsNull(sOldMgno) || gfn_IsNull(sOldMgno)) {	//그룹조회

		gfn_Alert("잘못된 조회입니다.");

		return;

	}

	

	var sArg = "COLDMGNO="	+ quote(sOldMgnoCd);	// 과거관리번호

    sArg += " CGTMGNO="	+ quote(sJibu);		        // 지부

    sArg += " CNEXTYN="	+ quote(sNextYn);   	    // 다음번호유무

    

	lfn_ClearData();	//데이타셋 초기화(모두)

    

    lfn_SearchAllInfo(sArg);

}



/*

 * 조회 : MAIN (>> 버튼 클릭 - 과거관리번호에 의한 조회)

 *        고객 정보(MAIN 1) - 과거관리번호

 */

function btn_NextOldCNo_OnClick(obj)

{

	if(length(meOldCustNO.Value) < 4) {

		gfn_Alert("구시스템번호에 의한 조회시에 4자리(지역-직능)는 필수항목입니다.");

		return;

	}

	var lsOldCustNo = Replace(meOldCustNO.Value, "-", "");

	var lsHeadNo = "";

	var lsEndNo = "";

	lsHeadNo = left(lsOldCustNo, 4);

	lsEndNo = substr(lsOldCustNo, 4);

	lsEndNo = lpad(lsEndNo, "0", 5);

	meOldCustNO.Value = lsHeadNo + lsEndNo;

	

	lfn_searchCust0010CustomerInfo4(trim(cbxJibu.Value), trim((lsHeadNo+lsEndNo)), "Y");

}



/*

 * 기능 : 고객 정보(MAIN 1) 조회 후 처리해야 할 내용

 * parameter : ErrorCode - 에러코드

 *             ErrorMsg - 에러메시지

 * Return : 없음

 */

function lfn_AfterSearchCust0010CustomerInfo(nErrorCode, strErrorMsg) {

    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.

        gfn_ErrorMsg(nErrorCode, strErrorMsg);

        return;

    }

	

    if(ds_ioCustomerInfo.GetRowCount() > 0) {

    

		lfn_InitButton(true);						//초기화시 버튼(활성화)

		

		if( ds_ioCustomerInfo.GetColumn(ds_ioCustomerInfo.currow, "CDELGUBUN") <> "0" ) {	//삭제구분이 있는경우 Disable

			lfn_AfterSearchComponentChange();			//조회 후 상단 조회 내역 변경

			lfn_SetDeleteStatus(false);

		} else {

			lfn_SetDeleteStatus(true);

			lfn_AfterSearchComponentChange();			//조회 후 상단 조회 내역 변경

		}

		

		lfn_searchCust0010BuildingInfo();			//대상물 정보(MAIN 2)

		lfn_searchCust0010ManagerInfo();			//선임자 정보(MAIN 3)

		//lfn_searchCust0010CustomerAdditionInfoList();	//고객관련 부가정보(고객 부가 정보(SUB 1) & 회비/교육비 정보 & 실무교육 정보)

		

		//lfn_searchCust0010AdditionInfoList();		// 고객 부가 정보(SUB 1)

		//lfn_searchCust0010CustomerFeeInfoList();	//회비/교육비 정보

		//lfn_searchCust0010EducationPassInfoList();	//실무교육 정보		

		

		if(left(cbxCourse.Value,1) == 6) {		//개인회원

			lfn_BuildingInfoStatus(false);	//개인회원인 경우 대상물 정보 수정 불가

		}

		

    } else {

		gfn_Alert(MSGI00010);	//검색된 레코드가 없습니다.

		lfn_Cancel();				//초기화

		if(length(ls_curComponent) > 0 ) {

			object(ls_curComponent).SetFocus();

		}

    }

}



// 대상물 정보(MAIN 2)

function lfn_searchCust0010BuildingInfo(sBmgno) {



	var sBmgnoCd = sBmgno;

	

	if(gfn_IsNull(sBmgno)) {	//고객조회를 타고 온경우

		sBmgnoCd = ds_ioCustomerInfo.GetColumn(ds_ioCustomerInfo.currow, "CBMGNO"); // 대상물관리번호

	}

	

	ds_ioBuildingInfo.ClearData(); // 데이타셋 초기화



    tit_clearActionInfo();

    tit_addCsvSearchActionInfo("cust:searchCust0010BuildingInfo", false);

    // 서버 호출

    tit_callService(

        ""

        , ""

        , "" // inDs

        , "ds_ioBuildingInfo=ds_ioBuildingInfo" // outDs

        , "BMGNO=" + quote(sBmgnoCd)	// 대상물관리번호

        , "lfn_AfterSearchCust0010BuildingInfo"

        ,false

        ,false

        ,true

    );

}



/*

 * 기능 : 대상물 정보(MAIN 2) 조회 후 처리해야 할 내용

 * parameter : ErrorCode - 에러코드

 *             ErrorMsg - 에러메시지

 * Return : 없음

 */

function lfn_AfterSearchCust0010BuildingInfo(nErrorCode, strErrorMsg) {

    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.

        gfn_ErrorMsg(nErrorCode, strErrorMsg);

        return;

    }

    if(ds_ioBuildingInfo.GetRowCount() > 0) {

		lfn_InitComboBoxIndex(1);				//대상물 콤보 index=0으로 초기화

		cbxStation_OnChanged(Object("cbxStation"), cbxStation.Value);		//대상물 소방관서 강제 이벤트

		//lfn_searchCust0010BuildingAdditionInfoList();	//대상물 부가정보 조회(관계자&위험물&이동탱크)

		//lfn_searchCust0010OwnerInfoList();		//관계자 부가정보

		//lfn_searchCust0010DangerInfoList();		//위험물 부가정보

		//lfn_searchCust0010TankInfoList();		//이동탱크 부가정보

		ds_ioBuildingInfo.ApplyChange();

	}

}



// 선임자 정보(MAIN 3)

function lfn_searchCust0010ManagerInfo(obj) {



	ds_ioManagerInfo.ClearData(); // 데이타셋 초기화



	if(gfn_IsNull(ds_ioCustomerInfo.GetColumn(ds_ioCustomerInfo.currow, "CFMHMGNO"))

		|| gfn_IsNull(ds_ioCustomerInfo.GetColumn(ds_ioCustomerInfo.currow, "CFMHSEQ")) )  {

		btnUpdateManagerInfo.Enable = false;

		ds_ioManagerInfo.InsertRow(0);	//선임자 입력 가능상태

		return;

	}

	

    tit_clearActionInfo();

    tit_addCsvSearchActionInfo("cust:searchCust0010ManagerInfo", false);

    // 서버 호출

    tit_callService(

        ""

        , ""

        , "" // inDs

        , "ds_ioManagerInfo=ds_oManagerInfo" // outDs

        , "CFMHMGNO=" + quote(ds_ioCustomerInfo.GetColumn(ds_ioCustomerInfo.currow, "CFMHMGNO")) // 선임자관리번호

        + " CFMHSEQ="  + quote(ds_ioCustomerInfo.GetColumn(ds_ioCustomerInfo.currow, "CFMHSEQ")) // 선임자이력순번

        , "lfn_AfterSearchCust0010ManagerInfo"

        ,false

        ,false

        ,true

    );

}



// 선임자 정보(MAIN 3)

function lfn_searchCust0010ManagerInfoCheck(obj) {

	

	ds_ioManagerInfo.ClearData(); // 데이타셋 초기화



    tit_clearActionInfo();

    tit_addCsvSearchActionInfo("cust:searchCust0010ManagerInfo", false);

    // 서버 호출

    tit_callService(

        ""

        , ""

        , "" // inDs

        , "ds_ioManagerInfo=ds_oManagerInfo" // outDs

        , "SFMNM=" + quote(edManagerNm.Text) // 선임자명

        + " SFMRESIDENTNO=" + quote(medResidentNo.Value) // 선임자주민번호

        , "lfn_AfterSearchCust0010ManagerInfo"

        ,false

        ,false

        ,true

    );

}



/*

 * 기능 : 선임자 정보(MAIN 3) 조회 후 처리해야 할 내용

 * parameter : ErrorCode - 에러코드

 *             ErrorMsg - 에러메시지

 * Return : 없음

 */

function lfn_AfterSearchCust0010ManagerInfo(nErrorCode, strErrorMsg) {

    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.

        gfn_ErrorMsg(nErrorCode, strErrorMsg);

        return;

    }

    if(ds_ioManagerInfo.GetRowCount() > 0) {

		

		//lfn_searchCust0010ManagerAdditionInfoList();	//선임자 부가정보(겸직 자격 & 보유 자격)

		//lfn_searchCust0010OverBizInfoList();	// 겸직 자격

		//lfn_searchCust0010LicenseInfoList();	// 보유 자격

		

		/* 선임자 정보 */

		lfn_FireManagerNewOldGubun(false);	//선임자 기존상태

		edManagerNm.Readonly = true;	//성명

		medResidentNo.Readonly = true;	//주민번호

		if( ds_ioCustomerInfo.GetColumn(ds_ioCustomerInfo.currow, "CDELGUBUN") <> "0" ) {	//삭제구분이 있는경우 Disable

			btn_SearchFireManager.Enable = false;	//선임자 현황 검색

			btnUpdateManagerInfo.Enable = false;	//선임자 정보변경

		} else {

			btnUpdateManagerInfo.Enable = true;	//선임자 정보변경

		}

		

		/* 초기값 셋팅 */

		if(ds_ioManagerInfo.GetColumn(ds_ioManagerInfo.currow, "FMRESIDENTERR") == 0) {

			lfn_ResidentNoChange(true);	//주민번호 정상

		} else {

			lfn_ResidentNoChange(false);	//주민번호 오류

		}

		

		lb_IsNiceAuthCheckOk = false;

		

		if(ds_ioManagerInfo.GetColumn(ds_ioManagerInfo.currow, "FMHENDGUBUN") == 0) {	//해임

			lfn_EndGubunChange(false);

		} else {

			lfn_EndGubunChange(true);

		}

		

		// 이미지(사진)

		ls_RealFileName = ds_ioManagerInfo.GetColumn(ds_ioManagerInfo.currow, "IIREALNM");

        ls_DirPathName = ds_ioManagerInfo.GetColumn(ds_ioManagerInfo.currow, "IIDIRPATH");

        

        if (length(ls_RealFileName) > 0){

            tabTab3.tabpgTab3Page1.imgPicture.ImageID = GF_FILE_SERVER_URL + "kems" + ls_DirPathName + "/" + ls_RealFileName;

            tabTab3.tabpgTab3Page1.imgPicture.Text = "";

        } else {

            tabTab3.tabpgTab3Page1.imgPicture.ImageID = "";

            tabTab3.tabpgTab3Page1.imgPicture.Text = "사진";

        }

        

		medCancelDate.Value = ds_ioManagerInfo.GetColumn(0, "FMHENDSYSDATE");

		

	} else {

		btnUpdateManagerInfo.Enable = false;

		ds_ioManagerInfo.InsertRow(0);	//선임자 입력 가능상태

		lbResidentNoError.Visible = false;	//주민번호 정상/오류

		lbEndStatusYn.Visible = false;		//해임상태

		lfn_FireManagerNewOldGubun(true);	//선임자 신규상태

		edManagerNm.Readonly = false;	//성명

		medResidentNo.Readonly = false;	//주민번호

	}

}



/***************************

 * 조회 (고객 하위 부가정보)

 ***************************/

 

// 고객 부가정보(부가정보(SUB 1) & 회비/교육비 정보 & 실무교육 정보)

function lfn_searchCust0010CustomerAdditionInfoList(obj) {



	ds_ioAdditionInfo.ClearData(); 			// 고객 부가 정보(SUB 1)

	ds_ioCustomerFeeInfo.ClearData(); 		// 회비/교육비 정보

	ds_ioEducationPassInfo.ClearData(); 	// 실무교육 정보



    tit_clearActionInfo();

    //tit_addSearchActionInfo("cust:searchCust0010AdditionInfoList", false);

    tit_addSearchActionInfo("cust:searchCust2010Fee", false);

    tit_addSearchActionInfo("cust:searchCust0010EducationPassInfoList", false);

    

    // 서버 호출

    tit_callService(

        ""

        , ""

        , "" // inDs

        , "ds_ioAdditionInfo=ds_ioAdditionInfo ds_ioCustomerFeeInfo=ds_oFee ds_ioEducationPassInfo=ds_ioEducationPassInfo" // outDs

        , "CMGNO=" + quote(ds_ioCustomerInfo.GetColumn(ds_ioCustomerInfo.currow, "CMGNO")) // 고객정보관리번호

        + " CLASTADDSEQ=" + quote(ds_ioCustomerInfo.GetColumn(ds_ioCustomerInfo.currow, "CLASTADDSEQ")) // 최종부가정보순번

        + " FEEGUBUN=" + "YEAR"	// 회비/교육비 년도

        , "lfn_AfterSearchCust0010CustomerAdditionInfoList"

        ,false

    );

}



/*

 * 기능 : (고객 하위 부가정보) 조회 후 처리해야 할 내용

 * parameter : ErrorCode - 에러코드

 *             ErrorMsg - 에러메시지

 * Return : 없음

 */

function lfn_AfterSearchCust0010CustomerAdditionInfoList(nErrorCode, strErrorMsg) {

    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.

        gfn_ErrorMsg(nErrorCode, strErrorMsg);

        return;

    }

    

    // 부가정보

    if(ds_ioAdditionInfo.GetRowCount() > 0) {

    

    } else {

		ds_ioAdditionInfo.InsertRow(0);	//입력가능하도록 풀어줌

    }

    lfn_InitComboBoxIndex(3);	//부가정보 Null일때 index=0으로 초기화

    

    // 회비/교육비 정보

    medEmailCnt.Value = 0;	//미납금액

    for(var i=0; i<ds_ioCustomerFeeInfo.GetRowCount(); i++) {

		medEmailCnt.Value = toNumber(medEmailCnt.Value) + toNumber(ds_ioCustomerFeeInfo.GetColumn(i, "MINAPFEE"));

    }

}







