function lfn_searchCust0010ManagerAdditionInfoList(obj) {



	ds_ioOverBizInfo.ClearData(); 	// 겸직정보 초기화

	ds_ioLicenseInfo.ClearData(); 	// 보유자격(타자격) 초기화

	ds_oLicenseInfo.ClearData(); 	// 보유자격(안전원자격) 초기화



    tit_clearActionInfo();

    tit_addSearchActionInfo("cust:searchCust0010OverBizInfoList", false);	//겸직정보

    tit_addSearchActionInfo("cust:searchCust0010LicenseInfoList", false);	//보유자격(타자격)

    tit_addSearchActionInfo("cust:searchCust0010LicenseInfo", false);		//보유자격(안전원자격)

    // 서버 호출

    tit_callService(

        ""

        , ""

        , "" // inDs

        , "ds_ioOverBizInfo=ds_ioOverBizInfo ds_ioLicenseInfo=ds_ioLicenseInfo ds_oLicenseInfo=ds_oLicenseInfo" // outDs

        , "CMGNO=" + quote(ds_ioCustomerInfo.GetColumn(ds_ioCustomerInfo.currow, "CMGNO")) // 고객정보관리번호

        + " OLFMMGNO=" + quote(ds_ioManagerInfo.GetColumn(ds_ioManagerInfo.currow, "FMMGNO")) // 선임자관리번호

        + " FMRESIDENTNO=" + quote(ds_ioManagerInfo.GetColumn(ds_ioManagerInfo.currow, "FMRESIDENTNO")) // 선임자주민번호

        , ""

        ,false

    );

}



