/***************************

 * Event & Button

 ***************************/

// 조회(F2 동일)

function medCustNo_OnKeyDown(obj,nChar,bShift,bCtrl,bAlt,nLLParam,nHLParam)

{

	if(nChar == 13) {	// Enter key

		ls_curComponent = GetCurrentComponent().id;

		cbxRegion.SetFocus();

		if(gfn_IsNull(medCustNo.Value) || gfn_IsNull(cbxJibu.Value)) {

			

		} else {

			lfn_Search();

		}

	}

}



// 조회(F2 동일)

function medBarCode_OnKeyDown(obj,nChar,bShift,bCtrl,bAlt,nLLParam,nHLParam)

{

	if(nChar == 13) {	// Enter key		

		if(gfn_IsNull(medBarCode.Value)) {



		} else {

			ls_curComponent = GetCurrentComponent().id;

			cbxRegion.SetFocus();

			lfn_searchCust0010CustomerInfo2(medBarCode.Value);

		}

	}

}



// 조회(F2 동일)

function meOldCustNO_OnKeyDown(obj,nChar,bShift,bCtrl,bAlt,nLLParam,nHLParam)

{

	if(nChar == 13) {	// Enter key

		if(gfn_IsNull(meOldCustNO.Value)) {

			

		} else {

			if(length(meOldCustNO.Value) < 5) {

				gfn_Alert("구시스템번호에 의한 조회시에 4자리(지역-직능)는 필수항목입니다.");

				return;

			}

			ls_curComponent = GetCurrentComponent().id;

			var lsOldCustNo = Replace(meOldCustNO.Value, "-", "");

			var lsHeadNo = "";

			var lsEndNo = "";

			lsHeadNo = left(lsOldCustNo, 4);

			lsEndNo = substr(lsOldCustNo, 4);

			lsEndNo = lpad(lsEndNo, "0", 5);

			meOldCustNO.Value = lsHeadNo + lsEndNo;

			

			lfn_searchCust0010CustomerInfo4(trim(cbxJibu.Value), trim((lsHeadNo+lsEndNo)), "N");

		}

	}

}



// 조회(F2 동일)

function medCustNo_OnKillFocus(obj)

{

    obj.Value = iif(gfn_IsNull(obj.Value), "", lpad(obj.Value, "0", 6));

}



// 조회(F2 동일)

function medBarCode_OnKillFocus(obj)

{

	if(!gfn_IsNull(obj.Value)) {

		obj.Value = iif(gfn_IsNull(obj.Value), "", lpad(obj.Value, "0", 13));

		//lfn_searchCust0010CustomerInfo2(medBarCode.Value);

	}

}



// 주민번호 오류/정상 표시

function lfn_ResidentNoChange(bStatus) {

	if(bStatus) {	//정상

		lbResidentNoError.Text = "정상";

		lbResidentNoError.Color = "blue";

	} else {	//오류

		lbResidentNoError.Text = "오류";

		lbResidentNoError.Color = "brown";

	}

	lbResidentNoError.Visible = true;

}



// 해임상태 해임/선임 표시

function lfn_EndGubunChange(bStatus) {

	if(bStatus) {	//선임

		lbEndStatusYn.Text = "선임";

		lbEndStatusYn.Color = "blue";

	} else {	//해임

		lbEndStatusYn.Text = "해임";

		lbEndStatusYn.Color = "brown";

	}

	lbEndStatusYn.Visible = true;

}



