// 대상물명 조회

function edBuildingNm_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)

{

	if(nChar == 13) {	// Enter key

		if(!gfn_IsNull(edBuildingNm.Text)) {

			if(cbxBusiness.Visible == true) {

				cbxBusiness.SetFocus();

			} else {

				cbxStation.SetFocus();

			}

			if(ds_ioCustomerInfo.GetRowType(0) == "insert") {

				lfn_searchSameBuildingCheck();

			}

		}

	} else if(nChar = 117) {	//Group Search

		bGroupSearch = false;

		lfn_SearchGroupPopup(trim(cbxJibu.Value), "1", trim(edBuildingNm.Text));	// 그룹조회(대상물)

	} else {

		gfn_EnterNextFocus(obj);

	}

}



// 대상물 주소1

function edOldAddr1_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)

{

	if(nChar = 117) {	//Group Search

		bGroupSearch = false;

		lfn_SearchGroupPopup(trim(cbxJibu.Value), "2", trim(edOldAddr1.Text));	// 그룹조회(주소1)

	} else if (nChar == 13) {

        btnSearchOldZipCode.SetFocus();

        var arrZipCode = gfn_ZipCodeListPopup(edOldAddr1.Text); // 우편번호 조회 팝업

        if (length(arrZipCode) > 0) {

            medOldZipCode.Value = arrZipCode[0];

            edOldAddr1.Text = arrZipCode[1];

            edOldAddr2.SetFocus();

        } else {

            edOldAddr1.SetFocus();

        }

	} else {

		gfn_EnterNextFocus(obj);

	}

}



// 대상물 번지

function edOldAddr2_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)

{

	if(nChar = 117) {	//Group Search

		bGroupSearch = false;

		lfn_SearchGroupPopup(trim(cbxJibu.Value), "3", trim(edOldAddr2.Text));	// 그룹조회(번지)

	} else if (nChar == 13) {

		edTelNo.SetFocus();

		if( ds_ioCustomerInfo.GetRowType(0) == "insert" ) {	//고객 신규입력 상태이며 

			if( gfn_IsNull(div4.edEduPost1.Text) || gfn_IsNull(div4.edEduPost2.Text) 

			|| gfn_IsNull(div0.edPost1.Text) || gfn_IsNull(div0.edPost2.Text) ) {	//고객 주소가 미입력상태이면

				cbxAddPost.Value = 0;	//공통

				cbxAddPost_OnChanged(Object("cbxAddPost"), cbxAddPost.Value);	//강제이벤트(주소선택)

				Div4.cbxEduPostGubun.Value = 0;	//공통(교육)희망주소구분 대상물로 선택

				lfn_EventDiv4CbxEduPostGubun(Div4.cbxEduPostGubun.Value);

			}

		}

	}

}



// 대상물 번지

function edOldAddr2_OnKillFocus(obj)

{

	if( ds_ioCustomerInfo.GetRowType(0) == "insert" ) {	//고객 신규입력 상태이며 

		if( gfn_IsNull(div4.edEduPost1.Text) || gfn_IsNull(div4.edEduPost2.Text) 

			|| gfn_IsNull(div0.edPost1.Text) || gfn_IsNull(div0.edPost2.Text) ) {	//고객 주소가 미입력상태이면

			cbxAddPost.Value = 0;	//공통

			cbxAddPost_OnChanged(Object("cbxAddPost"), cbxAddPost.Value);	//강제이벤트(주소선택)

			Div4.cbxEduPostGubun.Value = 0;	//공통(교육)희망주소구분 대상물로 선택

			lfn_EventDiv4CbxEduPostGubun(Div4.cbxEduPostGubun.Value);

		}

	}

}



