{

	if(nChar == 13) {

		medResidentNo.SetFocus();

		btn_SearchFireManager_OnClick();

	} else if(nChar = 117) {	//Group Search

		bGroupSearch = false;

		lfn_SearchGroupPopup(trim(cbxJibu.Value), "5", trim(edManagerNm.Text));	// 그룹조회(선임자 성명)

	} else {

		if(edManagerNm.Readonly == false) {

			for(var i=0; i<ds_ioManagerInfo.GetColCount(); i++) {

				if(!(ds_ioManagerInfo.GetColID(i) == "FMNM" || ds_ioManagerInfo.GetColID(i) == "FMHSTARTDATE")) {

					ds_ioManagerInfo.SetColumn(0, ds_ioManagerInfo.GetColID(i), "");

				}

			}

		}		

	}

}



