// 관리제외 변경

function cbxExclusionYN_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)

{

	if(strCode = "1") {

		calExclusionDate.Text = "";

	} else {

		calExclusionDate.Text = left(GetDate(), 8);

	}

}



// 삭제구분 변경

function cbxDelYN_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)

{

	if(strCode <> "0") {

		calDelDate.Value = left(GetDate(), 8);

	} else {

		if(meJoinYear.Enable == false) {	//삭제상태

			if(!gfn_Confirm("자료를 수정하시겠습니까?")) {

			cbxDelYN.Value = ds_ioCustomerInfo.GetOrgColumn(0, "CDELGUBUN");

			return;

			}

			lfn_SetDeleteStatus(true);			//삭제된 정보 상태변경한다.

		}

		calDelDate.Value = "";				//초기화

	}

}



// 회원 <-> 비회원

function cbxStatus_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)

{

	if(ds_ioCustomerInfo.GetRowType(ds_ioCustomerInfo.currow) <> "insert") {

		if( ds_ioCustomerInfo.GetOrgColumn(ds_ioCustomerInfo.currow, "CSTATUSGUBUN")

				<> ds_ioCustomerInfo.GetColumn(ds_ioCustomerInfo.currow, "CSTATUSGUBUN") ) {	//고객상태가 변경되었을때

			ds_ioCustomerInfo.SetColumn(ds_ioCustomerInfo.currow, "CSTATUSDATE", left(GetDate(), 8));

			cbxStatusReason.SetFocus();

		} else {	//이전상태로 되돌린다

			ds_ioCustomerInfo.SetColumn(ds_ioCustomerInfo.currow, "CSTATUSDATE", ds_ioCustomerInfo.GetOrgColumn(ds_ioCustomerInfo.currow, "CSTATUSDATE"));

			ds_ioCustomerInfo.SetColumn(ds_ioCustomerInfo.currow, "CSTATUSREASONCD", ds_ioCustomerInfo.GetOrgColumn(ds_ioCustomerInfo.currow, "CSTATUSREASONCD"));

		}

	}

}



