// 선임자 정보변경

function btnUpdateManagerInfo_OnClick(obj)

{

	var sArg  = "sMmgno=" + quote(ds_ioManagerInfo.GetColumn(ds_ioManagerInfo.currow, "FMMGNO"));

		sArg += " sCmgno=" + quote(ds_ioCustomerInfo.GetColumn(ds_ioCustomerInfo.currow, "CMGNO"));

	/*

     * arrManager[0] : 선임자관리번호, arrManager[1] : 최종선임이력순번

     */

    var arrManager = Dialog(ls_Prefix + "::frmcust0018PManagerChange.xml", sArg, 520, 188, "", -1, -1);

    if (length(arrManager) > 0) {

        //선임자 조회

        ds_ioManagerInfo.ClearData(); // 데이타셋 초기화



		tit_clearActionInfo();

		tit_addCsvSearchActionInfo("cust:searchCust0010ManagerInfo", false);

		// 서버 호출

		tit_callService(

			""

			, ""

			, "" // inDs

			, "ds_ioManagerInfo=ds_oManagerInfo" // outDs

			, "CFMHMGNO=" + quote(arrManager[0]) // 최종선임자관리번호

			+ " CFMHSEQ="  + quote(arrManager[1]) // 최종선임자순번 

			, "lfn_AfterSearchCust0010ManagerInfo"

			,false

			,false

			,true

		);

		

		/*

		 * 기능 : 선임자 정보 변경 후에 메인창도 변경된 회원상태를 반영시킴

		 */

		//Part1

		ds_oCustomerCompare.ClearData();	//현재까지 수정된 고객정보 내용 담기

		ds_oCustomerCompare.InsertRow(0);

		ds_ioCustomerInfo.CopyToRow(0, "ds_oCustomerCompare", 0);	//고객정보 임시보관

		

		//Part2

		ds_ioCustomerInfo.ClearData();

		tit_clearActionInfo();

		tit_addCsvSearchActionInfo("cust:searchCust0010CustomerInfo", false);

		// 서버 호출

		tit_callService(

			""

			, ""

			, "" // inDs

			, "ds_ioCustomerInfo=ds_ioCustomerInfo" // outDs

			, "CMGNO="		+ quote(ds_oCustomerCompare.GetColumn(0, "CMGNO"))	// 고객관리번호

			, "lfn_CustomerStatusChange"

			,false

			,false

			,true

		);

    }

}



/*

 * 기능 : 고객정보 조회(선임자 변경 후) 후 처리해야 할 내용

 * parameter : ErrorCode - 에러코드

 *             ErrorMsg - 에러메시지

 * Return : 없음

 */

function lfn_CustomerStatusChange(nErrorCode, strErrorMsg) {

    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.

        gfn_ErrorMsg(nErrorCode, strErrorMsg);

        return;

    }

    

    if(ds_ioCustomerInfo.rowcount > 0) {

		//Part3

		ds_iCustomerInfoTmp.ClearData();	//고객정보임시데이타셋 초기화

		ds_iCustomerInfoTmp.InsertRow(0);

		ds_ioCustomerInfo.CopyToRow(0, "ds_iCustomerInfoTmp", 0);	//고객정보 임시보관

		

		//Part4

		for(var i=0; i<ds_ioCustomerInfo.colcount; i++) {

			if(ds_ioCustomerInfo.GetColumn(0, ds_ioCustomerInfo.GetColID(i)) <> ds_oCustomerCompare.GetColumn(0, ds_oCustomerCompare.GetColID(i))) {

				if( ds_ioCustomerInfo.GetColID(i) <> "CFMHMGNO" && ds_ioCustomerInfo.GetColID(i) <> "CFMHSEQ"

				&& ds_ioCustomerInfo.GetColID(i) <> "CSTATUSGUBUN" && ds_ioCustomerInfo.GetColID(i) <> "COBMGNO"

				&& ds_ioCustomerInfo.GetColID(i) <> "COVERGUBUN" && ds_ioCustomerInfo.GetColID(i) <> "CFEEYYMM") {

					ds_ioCustomerInfo.SetColumn(0, ds_ioCustomerInfo.GetColID(i), ds_oCustomerCompare.GetColumn(0, ds_oCustomerCompare.GetColID(i)));

				}

			}

		}

    }

    

    lfn_searchCust0010CustomerAdditionInfoList();

    //lfn_searchCust0010CustomerFeeInfoList();	//회비/교육비정보 조회

    //lfn_searchCust0010EducationPassInfoList();	//실무교육정보 조회

}



// 선임자 성명 변경

function edManagerNm_OnChanged(obj,strText)

{

	lb_IsNiceAuthCheckOk = false;

	btnAuthResidentNo.Visible = true;

	ds_ioManagerInfo.SetColumn(ds_ioManagerInfo.currow, "FMAUTHGUBUN", 1);	//비인증

}



// 선임자 주민번호 변경

function medResidentNo_OnChanged(obj,strText)

{

	lb_IsNiceAuthCheckOk = false;

	btnAuthResidentNo.Visible = true;

	ds_ioManagerInfo.SetColumn(ds_ioManagerInfo.currow, "FMRESIDENTERR", 1);	//인증

}



// 실명인증

var lb_IsNiceAuthCheckOk = false;

function btnAuthResidentNo_OnClick(obj)

{

	if(lb_IsNiceAuthCheckOk == false) {

		lb_IsNiceAuthCheckOk = gfn_CheckNice(edManagerNm.Text, medResidentNo.Value); // 실명인증

		if (lb_IsNiceAuthCheckOk) {

			btnAuthResidentNo.Text = "실명인증완료";

			btnAuthResidentNo.Enable = false;

			ds_ioManagerInfo.SetColumn(ds_ioManagerInfo.currow, "FMAUTHGUBUN", 0);	//인증

			lb_IsNiceAuthCheckOk = true;

		} else {

			btnAuthResidentNo.Text = "실명인증";

			btnAuthResidentNo.Enable = true;

			medResidentNo.SetFocus();

			ds_ioManagerInfo.SetColumn(ds_ioManagerInfo.currow, "FMAUTHGUBUN", 1);	//비인증

		}

	}

}



// 주민번호 유효성 체크

function lfn_ResidentNoCheck(obj)

{

	var lb_ValidRedidentNoYn = gfn_CheckResidentNo(medResidentNo.Value); // 주민번호체계 검사

    var lb_ValidForeignNoYn = gfn_CheckForeignNo(medResidentNo.Value); // 재외국인 등록번호 검사

    if (!lb_ValidRedidentNoYn && !lb_ValidForeignNoYn) { // 둘다 실패면

		ds_ioManagerInfo.SetColumn(ds_ioManagerInfo.currow, "FMRESIDENTERR", 1);	//주민번호 오류

		lfn_ResidentNoChange(false);

        /*if (!gfn_Confirm("주민등록번호가 형식에 맞지않습니다.\n강제로 저장 하시겠습니까?")) {

            medResidentNo.SetFocus();

            return false;

        }*/

    } else {

		lfn_ResidentNoChange(true);

		ds_ioManagerInfo.SetColumn(ds_ioManagerInfo.currow, "FMRESIDENTERR", 0);	//주민번호 정상

		if(!gfn_IsNull(edManagerNm.Text)) {	//선임자명 입력했을경우

			//lfn_searchCust0010ManagerInfoCheck();

		}

    }    

}



// 선임자명

function edManagerNm_OnKillFocus(obj)

{

	if(!gfn_IsNull(edManagerNm.Text) && !gfn_IsNull(medResidentNo.Value)) {	//성명, 선임자명 입력했을경우

		//lfn_searchCust0010ManagerInfoCheck();

	}

}



// 선임자 주민번호

function medResidentNo_OnKillFocus(obj)

{

	lfn_ResidentNoCheck();

}



// 대상물명

function btnSearchBuilding_OnClick(obj)

{

	//팝업

	var sArg  = "sBuildingNm=" + quote(trim(edBuildingNm.Text));

		sArg += " sGtmgno=" + quote(trim(cbxJibu.Value));

	/*

     * arrNo[0] : B:대상물, C:고객

     * arrNo[1] : B일때:대상물관리번호, C일때:지부

     * arrNo[2] : B일때:없음, C일때:고객번호

     */

    var arrNo = Dialog(ls_Prefix + "::frmcust0011PSearchbuilding.xml", sArg, 600, 400, "", -1, -1);

    if (length(arrNo) > 0) {

		lfn_Input();	//♣♣♣♣♣♣♣♣♣♣♣♣♣♣♣♣♣♣♣♣♣♣

		if(arrNo[0] == "B") {	//대상물이 검색된 경우

			lfn_searchCust0010BuildingInfo(arrNo[1]);

			if(ds_ioCustomerInfo.GetRowType(0) == "insert") {

				cbxAddPost.Value = 0;	//주소구분 : 공통

				Div0.cbxPostGubun.Value = 0;	//주소구분 : 대상물

				lfn_EventDiv0CbxPostGubun(Div0.cbxPostGubun.Value);

			}

		} else if(arrNo[0] == "C") {	//고객이 검색된 경우

			lfn_searchCust0010CustomerInfo(arrNo[1], arrNo[2]);
			
		}

    }

}



// 업무대행

function btnSearchAgency_OnClick(obj)

{	

	//팝업

	var sArg = "sIsPopup=" + quote("Y");

	

	var arrNo = Dialog("cust::frmcust0040MAgentManagement.xml", sArg, 800, 586, "", -1, -1);

	

	if (length(arrNo) > 0) {

		ds_ioCustomerInfo.SetColumn(ds_ioCustomerInfo.currow, "CAGMGNO", arrNo[0]);

		ds_ioCustomerInfo.SetColumn(ds_ioCustomerInfo.currow, "AGNM", arrNo[1]);

    }

}



