// 부가정보이력

function btnSearchAddInfoH_OnClick(obj)

{

	/*

     * Input >> sCmgno : 고객정보관리번호

     */

	var sArg = "sCmgno=" + quote(ds_ioCustomerInfo.GetColumn(ds_ioCustomerInfo.currow, "CMGNO")); // 고객정보관리번호



    var rtnVal = Dialog(ls_Prefix + "::frmcust0017PAdditionHistory.xml", sArg, 600, 398, "", -1, -1);

    

    if(!gfn_IsNull(rtnVal)) {

		if(rtnVal <> ds_ioCustomerInfo.GetColumn(0, "CLASTADDSEQ")) {

			lfn_searchCust0010AdditionInfoList(rtnVal);		// 고객 부가 정보(SUB 1)

		}

    } else {	//이력이 없을경우

		ds_ioAdditionInfo.ClearData();

		ds_ioAdditionInfo.InsertRow(0);

		cbxAddGubun.Index = 0;

    }

}



// 부가정보 추가입력

function btnSaveAddInfo_OnClick(obj)

{

	if(ds_ioAdditionInfo.GetRowType(0) == "insert" && cbxAddGubun.Enable == true && edAddContent.Enable == true ) {	//이미 추가로 입력한 상태

		gfn_Alert("이미 부가정보를 추가로 입력중인 상태입니다.");

		cbxAddGubun.SetFocus();

		return;

	} else {

		if(!gfn_Confirm("부가정보를 추가로 입력하시겠습니까?")) {

			return;

		}

		cbxAddGubun.Enable = true;

		edAddContent.Enable = true;

		ds_ioAdditionInfo.ClearData();

		ds_ioAdditionInfo.InsertRow(0);

		cbxAddGubun.Index = 0;

		cbxAddGubun.SetFocus();

	}

}



// 상세정보

function btnEduDetailInfo1_OnClick(obj)

{

	/*

     * Input >> sCmgno : 고객정보관리번호

     */

    if(tabTab2.TabIndex == 0) {	// 회비/교육비

    

        if(ds_ioCustomerFeeInfo.Sum("SUNAPFEE") <= 0) {

            gfn_Alert("결제정보가 없습니다.");

            return;

        }



		var sArg = "sCmgno=" + quote(ds_ioCustomerInfo.GetColumn(ds_ioCustomerInfo.currow, "CMGNO"));

		var retDlg = Dialog(ls_Prefix + "::frmcust0019PCustomerSunapInfo.xml", sArg, 700, 400, "", -1, -1);

		

    } else {	//실무교육

		var sArg  = "sCmgno=" + quote(ds_ioCustomerInfo.GetColumn(ds_ioCustomerInfo.currow, "CMGNO"));			// 고객관리번호

		Dialog(ls_Prefix + "::frmcust1001PCustomerEduInfo.xml", sArg, 600, 398, "", -1, -1);

    }

}



