// 희망주소 선택(고객)

function cbxAddPost_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex) {

	if (cbxCourse.Value <> "80") {

		if (cbxAddPost.Value == "0") {	//공통

			btnCustFee.Visible = false;

			btnEduNoti.Visible = false;

			Div4.lbPost.Text = "청구지";

			Div0.Visible = false;

			Div4.Visible = true;

			//공통선택 + 공통(교육)희망주소구분 선택

			lfn_EventDiv4CbxEduPostGubun(Div4.cbxEduPostGubun.Value, false);

		} else {	//개별

			btnCustFee.Visible = true;

			btnEduNoti.Visible = true;

			Div0.lbPost.Text = "회비청구지";

			Div4.Visible = false;

			Div0.Visible = true;

		}

	}

}



// 희망주소 선택(고객)

function lfn_AfterSearchcbxAddPost_OnChanged() {

	if (cbxCourse.Value <> "80") {

		if (cbxAddPost.Value == "0") {	//공통

			btnCustFee.Visible = false;

			btnEduNoti.Visible = false;

			Div4.lbPost.Text = "청구지";

			Div0.Visible = false;

			Div4.Visible = true;

		} else {	//개별

			btnCustFee.Visible = true;

			btnEduNoti.Visible = true;

			Div0.lbPost.Text = "회비청구지";

			Div4.Visible = false;

			Div0.Visible = true;

		}

	}

}



// 회비고지서

function btnCustFee_OnClick(obj) {

	Div0.Visible = true;	//회비

	Div4.Visible = false;	//교육

}



// 교육통지서

