// 교육통지 우편번호(주소)

function Div4_btnSearchEduPostZipCode_OnKeyDown(obj,nChar,bShift,bCtrl,bAlt,nLLParam,nHLParam)

{

	if(nChar == 13) {	// Enter key

		Div4_btnSearchEduPostZipCode_OnClick();

	} else {

		gfn_ComponentOnKillFocus(obj);

	}

}



// 교육통지 우편번호(주소)

function Div4_btnSearchEduPostZipCode_OnClick(obj)

{

	var arrZipCode = gfn_ZipCodeListPopup(); // 우편번호 조회 팝업

    if (length(arrZipCode) > 0) {

        Div4.medEduPostZipCode.Value = arrZipCode[0];

        Div4.edEduPost1.Text = arrZipCode[1];

        Div4.edEduPost2.SetFocus();

    }

}



// 우편반송이력(교육통지)

function Div4_btnSearchEduReturnPostH_OnClick(obj)

{

	if( gfn_IsNull(ds_ioCustomerInfo.GetColumn(0, "CGTMGNO")) || gfn_IsNull(ds_ioCustomerInfo.GetColumn(0, "CNO")) ) {

		gfn_Alert("해당 자료를 찾을 수 없습니다.");

		return;

	} else {

		var ArrMenuInfo = gfn_SearchMenuInfo("cust", "cust::frmcust0080MReturnPostHistory.xml");	//메뉴정보조회

		if(length(ArrMenuInfo) > 0 ) {

			var sArg = "sJibu=" + quote(ds_ioCustomerInfo.GetColumn(0, "CGTMGNO"))

			+ " sCustNo=" + quote(ds_ioCustomerInfo.GetColumn(0, "CNO"));

			

			gs_SystemGubun = arrMenuInfo[7];						//"cust";

			gs_SystemMenuId = arrMenuInfo[5];						//"0000000254";

			global.mnuLeft.tabMenu.tabpgMenu.Text = arrMenuInfo[6];	//"고객관리";

			var ls_MenuId = arrMenuInfo[0];							//"0000000257";

			var ls_MenuTitle = arrMenuInfo[1];						//"반송우편물 이력조회";

			var ls_MenuUrl = arrMenuInfo[2];						//"cust::frmcust0080MReturnPostHistory.xml";

			

			global.mnuTop.tabTopMenu.TabIndex = gds_oTopMenu.FindRow("MSYSGUBUN", gs_SystemGubun);

			gfn_OpenChildWindow(ls_MenuUrl, ls_MenuTitle, ls_MenuId, sArg); 

		} else {

			gfn_Alert("해당 메뉴를 찾을 수 없습니다.");

		}

	}

}



